import React, { useEffect, useState, useMemo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import CategoryNav from '../components/CategoryNav.jsx';
import ProductGrid from '../components/ProductGrid.jsx';

export default function CategoryPage() {
  const { slug } = useParams();
  const navigate = useNavigate();

  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeCategory, setActiveCategory] = useState(slug ? decodeURIComponent(slug) : 'Tất cả');

  useEffect(() => {
    let cancelled = false;
    setLoading(true);
    setError(null);

    async function load() {
      try {
        const [cRes, pRes] = await Promise.all([
          axios.get('/api/categories/'),
          axios.get('/api/products/')
        ]);
        if (cancelled) return;
        const cats = Array.isArray(cRes.data) ? cRes.data : (cRes.data.results ?? []);
        setCategories(['Tất cả', ...cats.map(c => ({ id: c.id, name: c.name, slug: c.slug }))]);

        const prods = Array.isArray(pRes.data) ? pRes.data : (pRes.data.results ?? []);
        setProducts(prods);
        // set activeCategory using slug -> prefer matched category.name
        if (slug) {
          const decoded = decodeURIComponent(slug);
          const found = cats.find(c => c.slug === slug || c.slug === decoded || c.name === decoded);
          setActiveCategory(found ? found.name : decoded);
        } else {
          setActiveCategory('Tất cả');
        }
      } catch (err) {
        if (cancelled) return;
        console.error('CategoryPage load error', err);
        setError(err.response?.data ?? err.message ?? 'Lỗi khi tải dữ liệu');
      } finally {
        if (!cancelled) setLoading(false);
      }
    }

    load();
    return () => { cancelled = true; };
  }, [slug]);

  // update URL when user clicks a category chip
  function handleCategoryChange(label) {
    setActiveCategory(label);
    if (!label || label === 'Tất cả') {
      navigate('/');
    } else {
      // find slug for label
      const found = categories.find(c => (typeof c === 'string' ? c === label : c.name === label));
      const to = found && typeof found !== 'string' && found.slug ? `/category/${encodeURIComponent(found.slug)}` : `/category/${encodeURIComponent(label)}`;
      navigate(to);
    }
  }

  const filteredProducts = useMemo(() => {
    if (!products || products.length === 0) return [];
    if (!activeCategory || activeCategory === 'Tất cả') return products;
    return products.filter(p => {
      const cat = p.category;
      if (!cat) return false;
      const name = typeof cat === 'string' ? cat : (cat.name ?? '');
      const slugVal = typeof cat === 'string' ? '' : (cat.slug ?? '');
      return name === activeCategory || slugVal === activeCategory || slugVal === encodeURIComponent(activeCategory) || name === decodeURIComponent(activeCategory);
    });
  }, [products, activeCategory]);

  return (
    <main className="category-page">
      <h1 className="page-title">{activeCategory === 'Tất cả' ? 'Tất cả sản phẩm' : `Danh mục: ${activeCategory}`}</h1>

      {error && <div className="error" style={{ color: 'red' }}>{String(error)}</div>}

      <CategoryNav categories={categories} active={activeCategory} onChange={handleCategoryChange} />

      {loading ? (
        <div>Đang tải sản phẩm...</div>
      ) : (
        <>
          <div style={{ margin: '12px 0', color: '#444' }}>
            {filteredProducts.length} sản phẩm{filteredProducts.length !== 1 ? '' : ''}
          </div>
          <ProductGrid products={filteredProducts} />
        </>
      )}
    </main>
  );
}
