import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useCart } from '../utils/CartContext';
import { formatPrice } from '../utils/formatPrice';
import '../assets/productDetail.css';

export default function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [mainImage, setMainImage] = useState('');
  const [color, setColor] = useState('');
  const [size, setSize] = useState('');
  const [qty, setQty] = useState(1);

  useEffect(() => {
    fetch(`http://localhost:8000/api/products/${id}/`)
      .then(res => {
        if (!res.ok) throw new Error('Product not found');
        return res.json();
      })
      .then(data => {
        setProduct(data);
        setMainImage(data.image || '/default-product.png');
        setLoading(false);
      })
      .catch(err => {
        console.error('Error loading product:', err);
        setLoading(false);
      });
  }, [id]);

  if (loading) {
    return (
      <div className="product-detail-page">
        <div className="loading-container">
          <div className="spinner"></div>
          <p>Đang tải sản phẩm...</p>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="product-detail-page">
        <div className="empty-state">
          <h2>Không tìm thấy sản phẩm</h2>
          <button onClick={() => navigate('/')} className="btn-primary">
            Quay về trang chủ
          </button>
        </div>
      </div>
    );
  }

  const colors = product.variants?.colors || [];
  const sizes = product.variants?.sizes || [];
  const specs = product.specs || [];
  const description = product.description || 'Chưa có mô tả cho sản phẩm này.';
  
  // Shop info
  const shop = product.shop || product.seller || {
    id: product.user?.id,
    name: product.user?.shop_name || product.user?.username || 'V-Market Store',
    avatar: product.user?.avatar || '/default-avatar.png',
    rating: product.user?.rating || 4.8,
    totalProducts: product.user?.total_products || 150,
    followers: product.user?.followers || 0
  };

  function dec() {
    setQty(prev => Math.max(1, prev - 1));
  }

  function inc() {
    setQty(prev => Math.min(prev + 1, product.stock || 999));
  }

  function handleAddToCart() {
    if (colors.length > 0 && !color) {
      alert('Vui lòng chọn màu sắc');
      return;
    }
    if (sizes.length > 0 && !size) {
      alert('Vui lòng chọn kích thước');
      return;
    }
    addToCart(product, qty, { color, size });
    alert(`Đã thêm ${qty} sản phẩm vào giỏ hàng`);
  }

  function handleBuyNow() {
    if (colors.length > 0 && !color) {
      alert('Vui lòng chọn màu sắc');
      return;
    }
    if (sizes.length > 0 && !size) {
      alert('Vui lòng chọn kích thước');
      return;
    }
    addToCart(product, qty, { color, size });
    navigate('/cart');
  }

  function handleChatWithShop() {
    navigate(`/chat/${shop.id}`);
  }

  function handleViewShop() {
    navigate(`/shop/${shop.id}`);
  }

  return (
    <div className="product-detail-page">
      <div className="container">
        <div className="product-layout">
          {/* Left: Image */}
          <div className="product-image-section">
            <div className="main-image">
              <img src={mainImage} alt={product.name} />
            </div>
            {product.images && product.images.length > 0 && (
              <div className="image-thumbnails">
                {product.images.map((img, i) => (
                  <div
                    key={i}
                    className={`thumbnail ${mainImage === img ? 'active' : ''}`}
                    onClick={() => setMainImage(img)}
                  >
                    <img src={img} alt={`${product.name} ${i + 1}`} />
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Right: Info */}
          <div className="product-info-section">
            <h1 className="product-name">{product.name}</h1>
            <div className="product-price">{formatPrice(product.price)}</div>

            <div className="info-card">
              {/* Voucher */}
              <div className="info-row">
                <span className="label">Voucher</span>
                <span className="value voucher-badge">Giảm 100k</span>
              </div>

              {/* Vận chuyển */}
              <div className="info-row">
                <span className="label">Vận chuyển</span>
                <div className="value">
                  <div>Giao đến: <strong>Toàn quốc</strong></div>
                  <div className="shipping-note">Phí ship: Freeship đơn từ 0đ</div>
                </div>
              </div>

              {/* Màu sắc */}
              {colors.length > 0 && (
                <div className="info-row">
                  <span className="label">Màu sắc</span>
                  <div className="options">
                    {colors.map(c => (
                      <button
                        key={c}
                        className={`option-btn ${color === c ? 'active' : ''}`}
                        onClick={() => setColor(c)}
                      >
                        {c}
                      </button>
                    ))}
                  </div>
                </div>
              )}

              {/* Kích thước */}
              {sizes.length > 0 && (
                <div className="info-row">
                  <span className="label">Size</span>
                  <div className="options">
                    {sizes.map(s => (
                      <button
                        key={s}
                        className={`option-btn ${size === s ? 'active' : ''}`}
                        onClick={() => setSize(s)}
                      >
                        {s}
                      </button>
                    ))}
                  </div>
                </div>
              )}

              {/* Số lượng */}
              <div className="info-row">
                <span className="label">Số lượng</span>
                <div className="quantity-control">
                  <button className="qty-btn" onClick={dec}>-</button>
                  <input type="text" value={qty} readOnly className="qty-input" />
                  <button className="qty-btn" onClick={inc}>+</button>
                </div>
              </div>
            </div>

            {/* Action buttons */}
            <div className="action-buttons">
              <button className="btn-add-cart" onClick={handleAddToCart}>
                Thêm vào giỏ
              </button>
              <button className="btn-buy-now" onClick={handleBuyNow}>
                Mua ngay
              </button>
            </div>

            {/* Shop info card */}
            <div className="shop-card">
              <div className="shop-header">
                <div className="shop-avatar">
                  <img src={shop.avatar} alt={shop.name} />
                </div>
                <div className="shop-info">
                  <h3 className="shop-name">{shop.name}</h3>
                  <div className="shop-stats">
                    <span>Đánh giá: <strong>{shop.rating}</strong></span>
                    <span>•</span>
                    <span>Người theo dõi: <strong>{shop.followers}k</strong></span>
                  </div>
                </div>
              </div>
              <div className="shop-actions">
                <button className="btn-shop-action btn-chat" onClick={handleChatWithShop}>
                  Chat ngay
                </button>
                <button className="btn-shop-action btn-view" onClick={handleViewShop}>
                  Xem shop
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Chi tiết sản phẩm */}
        <div className="product-details">
          {specs.length > 0 && (
            <div className="detail-card">
              <h2 className="detail-title">Chi tiết sản phẩm</h2>
              <div className="specs-table">
                {specs.map((spec, i) => (
                  <div className="spec-row" key={i}>
                    <div className="spec-label">{spec.label}</div>
                    <div className="spec-value">{spec.value}</div>
                  </div>
                ))}
              </div>
            </div>
          )}

          <div className="detail-card">
            <h2 className="detail-title">Mô tả sản phẩm</h2>
            <div className="description-content">
              <pre>{description}</pre>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
