// components/PopupChat.jsx
import React, { useEffect, useRef, useState } from 'react';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:8000';

export default function PopupChat({ shopId, buyerId, user, onClose }) {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');
  const wsRef = useRef(null);
  const bottomRef = useRef(null);

  useEffect(() => {
    if (!user) return;
    const token = localStorage.getItem('access_token') || '';
    const wsProtocol = API_BASE.startsWith('https') ? 'wss' : 'ws';
    let wsUrl = '';

    if (user.user_type === 'buyer') {
      wsUrl = `${wsProtocol}://${API_BASE.replace(/^https?:\/\//,'')}/ws/chat/${shopId}/?token=${token}&buyer=${user.user_id}`;
    } else {
      wsUrl = `${wsProtocol}://${API_BASE.replace(/^https?:\/\//,'')}/ws/chat/${shopId}/?token=${token}&seller=${user.user_id}&buyer=${buyerId}`;
    }

    const ws = new WebSocket(wsUrl);
    wsRef.current = ws;

    ws.onopen = () => console.log(`WS connected (${user.user_type})`);
    ws.onmessage = evt => {
      try {
        const data = JSON.parse(evt.data);
        if (data.type === 'history') setMessages(data.messages || []);
        else if (data.type === 'message' && data.message)
          setMessages(prev => [...prev, data.message]);
      } catch {}
    };
    ws.onerror = e => console.error('WS error', e);
    ws.onclose = e => console.log('WS closed', e);

    return () => ws.close();
  }, [shopId, buyerId, user]);

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = () => {
    const content = text.trim();
    if (!content || !wsRef.current || wsRef.current.readyState !== 1) return;
    wsRef.current.send(JSON.stringify({ type: 'message', content }));
    setText('');
  };

  return (
    <div style={{
      position: 'fixed',
      bottom: 20,
      right: 20 + 60 * (shopId || buyerId),
      width: 300,
      height: 400,
      background: '#fff',
      border: '1px solid #ccc',
      borderRadius: 8,
      display: 'flex',
      flexDirection: 'column',
      zIndex: 10000,
      boxShadow: '0 4px 12px rgba(0,0,0,0.15)'
    }}>
      {/* Header */}
      <div style={{
        padding: 10,
        borderBottom: '1px solid #eee',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <strong>{user.user_type==='buyer' ? `Shop #${shopId}` : `Buyer #${buyerId}`}</strong>
        <button onClick={onClose} style={{border:'none', background:'transparent', cursor:'pointer'}}>✕</button>
      </div>

      {/* Messages */}
      <div style={{flex:1, overflowY:'auto', padding:10}}>
        {messages.map(m => (
          <div key={m.id} style={{
            marginBottom: 8,
            display:'flex',
            justifyContent: m.sender_id === user.user_id ? 'flex-end' : 'flex-start'
          }}>
            <div style={{
              maxWidth:'70%',
              padding:'8px 12px',
              borderRadius:12,
              background: m.sender_id===user.user_id ? '#DCFCE7' : '#F3F4F6'
            }}>
              <div style={{fontSize:14}}>{m.content}</div>
              <div style={{fontSize:11, color:'#6b7280', marginTop:4}}>
                {new Date(m.created_at).toLocaleString('vi-VN')}
              </div>
            </div>
          </div>
        ))}
        <div ref={bottomRef} />
      </div>

      {/* Input */}
      <div style={{padding:10, borderTop:'1px solid #eee', display:'flex', gap:5}}>
        <input
          value={text}
          onChange={e=>setText(e.target.value)}
          onKeyDown={e=>e.key==='Enter' && sendMessage()}
          placeholder="Nhập tin nhắn..."
          style={{flex:1, padding:'6px 10px', border:'1px solid #ddd', borderRadius:8}}
        />
        <button onClick={sendMessage} style={{padding:'6px 12px', background:'#2563eb', color:'#fff', border:'none', borderRadius:8}}>Gửi</button>
      </div>
    </div>
  );
}
