// components/ChatButton.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../utils/AuthContext';
import Chat from '../pages/Chat';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:8000';

export default function ChatButton({ shopId }) {
  const { user } = useAuth();
  const [open, setOpen] = useState(false);

  if (!user) return null; // náº¿u chÆ°a login khÃ´ng hiá»‡n

  return (
    <div style={{ position: 'fixed', bottom: 20, right: 20, zIndex: 999 }}>
      {open && <ChatPopup shopId={shopId} buyerId={user.user_id} onClose={() => setOpen(false)} />}
      <button
        onClick={() => setOpen(!open)}
        style={{
          background: '#2563eb',
          color: '#fff',
          border: 'none',
          borderRadius: '50%',
          width: 60,
          height: 60,
          cursor: 'pointer',
          fontSize: 24,
          boxShadow: '0 2px 8px rgba(0,0,0,0.2)',
        }}
      >
        ðŸ’¬
      </button>
    </div>
  );
}

// ChatPopup: hiá»ƒn thá»‹ ná»™i dung chat
const ChatPopup = ({ shopId, buyerId, onClose }) => {
  return (
    <div
      style={{
        width: 350,
        height: 450,
        position: 'fixed',
        bottom: 90,
        right: 20,
        background: '#fff',
        border: '1px solid #ddd',
        borderRadius: 8,
        display: 'flex',
        flexDirection: 'column',
        boxShadow: '0 2px 10px rgba(0,0,0,0.2)',
        zIndex: 1000,
      }}
    >
      <div
        style={{
          padding: 10,
          borderBottom: '1px solid #eee',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
        }}
      >
        <strong>Chat vá»›i shop #{shopId}</strong>
        <button onClick={onClose} style={{ border: 'none', background: 'none', cursor: 'pointer', fontSize: 18 }}>
          âœ–
        </button>
      </div>
      <div style={{ flex: 1, overflow: 'hidden' }}>
        <Chat shopId={shopId} buyerId={buyerId} isPopup />
      </div>
    </div>
  );
};
