// components/ChatButton.jsx
import React, { useEffect, useState } from 'react';
import PopupChat from './PopupChat';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:8000';

export default function ChatButton({ user }) {
  const [chatList, setChatList] = useState([]); // danh sách shop/buyer chat
  const [openChats, setOpenChats] = useState([]); // popup đang mở
  const token = localStorage.getItem('access_token');

  // Lấy danh sách chat từ API
  useEffect(() => {
    if (!user) return;
    const fetchChats = async () => {
      try {
        const endpoint =
          user.user_type === 'buyer'
            ? `${API_BASE}/api/chats/buyer/`
            : `${API_BASE}/api/chats/seller/`;
        const res = await fetch(endpoint, {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
        });
        if (!res.ok) throw new Error(`HTTP ${res.status}`);
        const data = await res.json();
        setChatList(data);
      } catch (err) {
        console.error('Failed to fetch chats', err);
      }
    };
    fetchChats();
  }, [user, token]);

  const toggleChat = (id) => {
    setOpenChats(prev => {
      if (prev.includes(id)) return prev.filter(x => x !== id);
      return [...prev, id];
    });
  };

  if (!user) return null;

  return (
    <>
      {/* Icon góc phải dưới */}
      <div style={{
        position: 'fixed',
        bottom: 20,
        right: 20,
        display: 'flex',
        flexDirection: 'column-reverse',
        gap: 10,
        zIndex: 9999
      }}>
        {chatList.map(c => (
          <button
            key={c.id}
            onClick={() => toggleChat(c.id)}
            style={{
              width: 50, height: 50, borderRadius: '50%',
              background: '#2563eb', color: '#fff', border: 'none',
              cursor: 'pointer'
            }}
          >
            {c.name[0].toUpperCase()}
          </button>
        ))}
      </div>

      {/* Popup chat */}
      {openChats.map(id => (
        <PopupChat
          key={id}
          shopId={user.user_type === 'buyer' ? id : undefined}
          buyerId={user.user_type === 'seller' ? id : undefined}
          user={user}
          onClose={() => toggleChat(id)}
        />
      ))}
    </>
  );
}
