// components/PopupChat.jsx
import React, { useEffect, useRef, useState } from 'react';

export default function PopupChat({ conversation, user, onClose }) {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');
  const wsRef = useRef(null);
  const bottomRef = useRef(null);

  useEffect(() => {
    if (!conversation) return;

    const token = localStorage.getItem('access_token') || '';
    const wsProtocol = window.location.protocol === 'https:' ? 'wss' : 'ws';
    const wsUrl = `${wsProtocol}://${window.location.host}/ws/chat/${conversation.id}/?token=${token}`;

    const ws = new WebSocket(wsUrl);
    wsRef.current = ws;

    ws.onopen = () => console.log('WS connected for conversation', conversation.id);

    ws.onmessage = (evt) => {
      try {
        const data = JSON.parse(evt.data);
        if (data.type === 'history') setMessages(data.messages || []);
        else if (data.type === 'message') setMessages(prev => [...prev, data.message]);
      } catch (err) {
        console.error(err);
      }
    };

    ws.onclose = (evt) => console.log('WS closed', evt);
    ws.onerror = (err) => console.error('WS error', err);

    return () => ws.close();
  }, [conversation]);

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = () => {
    if (!text.trim() || !wsRef.current || wsRef.current.readyState !== 1) return;
    wsRef.current.send(JSON.stringify({ type: 'message', content: text }));
    setText('');
  };

  if (!conversation) return null;

  return (
    <div style={{
      position: 'fixed',
      bottom: 20,
      right: 20 + 60 * conversation.id, // để popup không chồng nhau
      width: 300,
      height: 400,
      background: '#fff',
      border: '1px solid #ddd',
      borderRadius: 8,
      display: 'flex',
      flexDirection: 'column',
      zIndex: 10000
    }}>
      <div style={{ padding: 8, borderBottom: '1px solid #eee', display: 'flex', justifyContent: 'space-between' }}>
        <strong>{conversation.name}</strong>
        <button onClick={onClose}>X</button>
      </div>
      <div style={{ flex: 1, overflowY: 'auto', padding: 8 }}>
        {messages.map(m => (
          <div key={m.id} style={{ marginBottom: 6, textAlign: m.sender_id === user.user_id ? 'right' : 'left' }}>
            <div style={{
              display: 'inline-block',
              padding: '6px 10px',
              borderRadius: 10,
              background: m.sender_id === user.user_id ? '#DCFCE7' : '#F3F4F6'
            }}>
              {m.content}
            </div>
          </div>
        ))}
        <div ref={bottomRef} />
      </div>
      <div style={{ padding: 8, borderTop: '1px solid #eee', display: 'flex', gap: 4 }}>
        <input
          style={{ flex: 1, padding: 6, border: '1px solid #ddd', borderRadius: 6 }}
          value={text}
          onChange={e => setText(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && sendMessage()}
        />
        <button style={{ padding: '6px 12px', background: '#2563eb', color: '#fff', border: 'none', borderRadius: 6 }} onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}
