import React, { useEffect, useState, useMemo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Layout from '../components/Layout.jsx';
import CategoryNav from '../components/CategoryNav.jsx';
import ProductGrid from '../components/ProductGrid.jsx';
import FiltersSidebar from '../components/FiltersSidebar.jsx';
import PageHeader from '../components/PageHeader.jsx';
import EmptyState from '../components/EmptyState.jsx';
import Pagination from '../components/Pagination.jsx';
import '../assets/CategoryPage.css';
import '../assets/Components.css';

export default function CategoryPage() {
  const { categoryName } = useParams(); // this is slug from URL
  const navigate = useNavigate();
  const slugFromUrl = categoryName ? decodeURIComponent(categoryName) : '';

  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // UI filters
  const [query, setQuery] = useState('');
  const [priceRange, setPriceRange] = useState({ min: '', max: '' });
  const [sortBy, setSortBy] = useState('relevance');
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    let cancelled = false;
    async function load() {
      setLoading(true);
      try {
        const [cRes, pRes] = await Promise.all([axios.get('/api/categories/'), axios.get('/api/products/')]);
        if (cancelled) return;
        const cats = Array.isArray(cRes.data) ? cRes.data : (cRes.data.results ?? []);
        setCategories(cats);
        const prods = Array.isArray(pRes.data) ? pRes.data : (pRes.data.results ?? []);
        setProducts(prods);
      } catch (err) {
        if (cancelled) return;
        setError(err.response?.data ?? err.message ?? 'L·ªói t·∫£i d·ªØ li·ªáu');
      } finally {
        if (!cancelled) setLoading(false);
      }
    }
    load();
    return () => { cancelled = true; };
  }, []);

  // find category object from slugOrName
  const activeCategoryObj = useMemo(() => {
    if (!slugFromUrl) return null;
    return categories.find(c => (c.slug === categoryName || c.slug === slugFromUrl || c.name === slugFromUrl)) ?? null;
  }, [categories, categoryName, slugFromUrl]);

  // helper to test whether a product belongs to the active category
  function productMatchesCategory(prod, categoryIdentifier) {
    if (!categoryIdentifier || categoryIdentifier === 'T·∫•t c·∫£') return true;
    // identifier may be slug, name, or id (string)
    const idToMatch = String(categoryIdentifier);
    // product.category could be number/id, string, or object
    const pc = prod.category;
    if (pc == null) return false;
    if (typeof pc === 'string') {
      // might be name or slug
      if (pc === categoryIdentifier) return true;
      if (pc === decodeURIComponent(categoryIdentifier)) return true;
      return false;
    }
    if (typeof pc === 'number') {
      if (String(pc) === idToMatch) return true;
      return false;
    }
    // pc is object
    const pid = pc.id ?? pc.pk ?? null;
    const pname = pc.name ?? pc.title ?? null;
    const pslug = pc.slug ?? null;
    if (pid != null && String(pid) === idToMatch) return true;
    if (pname && (pname === categoryIdentifier || pname === decodeURIComponent(categoryIdentifier))) return true;
    if (pslug && (pslug === categoryIdentifier || pslug === decodeURIComponent(categoryIdentifier))) return true;
    return false;
  }

  // filtering products with robust category matching
  const filteredProducts = useMemo(() => {
    const activeIdOrSlug = activeCategoryObj ? (activeCategoryObj.slug ?? activeCategoryObj.name ?? String(activeCategoryObj.id)) : (slugFromUrl || 'T·∫•t c·∫£');
    return products.filter(product => {
      // category filter
      const catOk = productMatchesCategory(product, activeIdOrSlug);
      if (!catOk) return false;

      // text query
      const q = (query || '').trim().toLowerCase();
      if (q) {
        const name = (product.name || '').toLowerCase();
        const desc = (product.description || '').toLowerCase();
        if (!name.includes(q) && !desc.includes(q)) return false;
      }

      // price range
      const price = Number(product.price ?? 0);
      if (priceRange.min && price < Number(priceRange.min)) return false;
      if (priceRange.max && price > Number(priceRange.max)) return false;

      return true;
    }).sort((a, b) => {
      switch (sortBy) {
        case 'price-low': return (Number(a.price)||0) - (Number(b.price)||0);
        case 'price-high': return (Number(b.price)||0) - (Number(a.price)||0);
        case 'name': return (a.name||'').localeCompare(b.name||'');
        case 'newest': return (b.id||0) - (a.id||0);
        default: return 0;
      }
    });
  }, [products, activeCategoryObj, slugFromUrl, query, priceRange, sortBy]);

  // pagination
  const ITEMS_PER_PAGE = 12;
  const totalPages = Math.max(1, Math.ceil(filteredProducts.length / ITEMS_PER_PAGE));
  useEffect(() => { if (currentPage > totalPages) setCurrentPage(totalPages); }, [totalPages, currentPage]);

  const paginatedProducts = useMemo(() => {
    const start = (currentPage - 1) * ITEMS_PER_PAGE;
    return filteredProducts.slice(start, start + ITEMS_PER_PAGE);
  }, [filteredProducts, currentPage]);

  // handle user clicking a category chip (nav)
  function handleCategoryChange(value) {
    // value is slug if available (CategoryNav sends slug when possible), else name/id
    if (!value || value === 'T·∫•t c·∫£') {
      navigate('/');
      return;
    }
    // try to find category with matching slug/name/id, prefer slug in URL
    const found = categories.find(c => (c.slug === value || String(c.id) === String(value) || c.name === value));
    const toSlug = found ? found.slug ?? found.name : value;
    navigate(`/category/${encodeURIComponent(toSlug)}`);
  }

  return (
    <Layout className="category-page">
      <div className="category-container">
        <PageHeader
          title={activeCategoryObj ? activeCategoryObj.name : (slugFromUrl || 'T·∫•t c·∫£')}
          subtitle={`K·∫øt qu·∫£ cho ${activeCategoryObj ? activeCategoryObj.name : (slugFromUrl || 'T·∫•t c·∫£')}`}
          resultsCount={filteredProducts.length}
          breadcrumb={[{ text: 'Trang ch·ªß', link: '/' }, { text: activeCategoryObj ? activeCategoryObj.name : (slugFromUrl || 'T·∫•t c·∫£') }]}
          className="category-header"
        />

        <CategoryNav
          categories={['T·∫•t c·∫£', ...categories]}
          active={activeCategoryObj ? (activeCategoryObj.name ?? activeCategoryObj.slug) : (slugFromUrl || 'T·∫•t c·∫£')}
          onChange={handleCategoryChange}
        />

        <div className="category-content">
          <FiltersSidebar
            categories={['T·∫•t c·∫£', ...categories.map(c => c.name ?? c)]}
            selectedCategory={activeCategoryObj ? activeCategoryObj.name : (slugFromUrl || 'T·∫•t c·∫£')}
            onCategoryChange={(cat) => handleCategoryChange(cat)}
            priceRange={priceRange}
            onPriceRangeChange={(r) => { setPriceRange(r); setCurrentPage(1); }}
            sortBy={sortBy}
            onSortChange={(s) => { setSortBy(s); setCurrentPage(1); }}
            onClearFilters={() => { setQuery(''); setPriceRange({ min: '', max: '' }); setSortBy('relevance'); setCurrentPage(1); }}
            showCategoryLinks
          />

          <div className="products-section">
            {loading ? (
              <div>ƒêang t·∫£i s·∫£n ph·∫©m...</div>
            ) : error ? (
              <div style={{ color: 'red' }}>{String(error)}</div>
            ) : filteredProducts.length === 0 ? (
              <EmptyState
                icon="üì¶"
                title="Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o"
                description="Hi·ªán t·∫°i ch∆∞a c√≥ s·∫£n ph·∫©m trong danh m·ª•c n√†y"
                actionText="Xem t·∫•t c·∫£ s·∫£n ph·∫©m"
                actionLink="/"
                className="no-results"
              />
            ) : (
              <>
                <ProductGrid products={paginatedProducts} />
                <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={(p) => setCurrentPage(p)} />
              </>
            )}
          </div>
        </div>
      </div>
    </Layout>
  );
}
