import React, { createContext, useState, useEffect, useContext, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import authService from './authService';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const loadUser = async () => {
      const token = localStorage.getItem('access_token');
      const savedUser = localStorage.getItem('user');
      
      if (token && savedUser) {
        try {
          const userData = JSON.parse(savedUser);
          setUser(userData);
        } catch (error) {
          console.error('‚ùå Error parsing saved user:', error);
          localStorage.removeItem('user');
        }
      }
      setLoading(false);
    };
    
    loadUser();
  }, []);

  // ‚¨áÔ∏è TH√äM: Helper function ƒë·ªÉ redirect theo role
  const getDefaultRoute = (userType) => {
    switch(userType) {
      case 'admin':
        return '/admin/dashboard';
      case 'seller':
        return '/seller/dashboard';
      case 'buyer':
      default:
        return '/dashboard';
    }
  };

  const login = async (username, password) => {
    try {
      console.log('üîê AuthContext: Attempting login');
      const data = await authService.login(username, password);
      
      console.log('‚úÖ AuthContext: Login successful');
      console.log('User data:', data.user);
      
      setUser(data.user);
      
      // ‚¨áÔ∏è S·ª¨A: Redirect theo user_type
      const defaultRoute = getDefaultRoute(data.user.user_type);
      console.log(`üìç Redirecting ${data.user.user_type} to: ${defaultRoute}`);
      navigate(defaultRoute);
      
      return { success: true };
    } catch (error) {
      console.error('‚ùå AuthContext: Login error:', error);
      
      let errorMessage = 'Login failed';
      
      if (error.response?.data) {
        if (typeof error.response.data === 'string') {
          errorMessage = error.response.data;
        } else if (error.response.data.error) {
          errorMessage = error.response.data.error;
        } else if (error.response.data.detail) {
          errorMessage = error.response.data.detail;
        }
      } else if (error.message) {
        errorMessage = error.message;
      }
      
      return { 
        success: false, 
        error: errorMessage
      };
    }
  };

  const register = async (userData) => {
    try {
      console.log('üìù AuthContext: Attempting registration');
      const data = await authService.register(userData);
      
      console.log('‚úÖ AuthContext: Registration successful');
      setUser(data.user);
      
      // ‚¨áÔ∏è S·ª¨A: Redirect theo user_type
      const defaultRoute = getDefaultRoute(data.user.user_type);
      navigate(defaultRoute);
      
      return { success: true };
    } catch (error) {
      console.error('‚ùå AuthContext: Registration error:', error);
      return { 
        success: false, 
        error: error.response?.data || 'Registration failed' 
      };
    }
  };

  const logout = async () => {
    try {
      console.log('üëã AuthContext: Logging out');
      await authService.logout();
    } catch (error) {
      console.error('‚ùå Logout error:', error);
    } finally {
      setUser(null);
      navigate('/login');
    }
  };

  const updateUser = useCallback((updatedUser) => {
    console.log('üìù Updating user:', updatedUser);
    setUser(updatedUser);
    localStorage.setItem('user', JSON.stringify(updatedUser));
  }, []);

  const value = {
    user,
    loading,
    login,
    register,
    logout,
    updateUser,
    isAuthenticated: !!user,
    isAdmin: user?.user_type === 'admin',
    isSeller: user?.user_type === 'seller',
    isBuyer: user?.user_type === 'buyer',
    getDefaultRoute, // ‚¨ÖÔ∏è Export ƒë·ªÉ d√πng ·ªü component kh√°c
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
