import React, { useState, useEffect, useMemo } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Layout from '../components/Layout.jsx';
import ProductGrid from '../components/ProductGrid.jsx';
import FiltersSidebar from '../components/FiltersSidebar.jsx';
import PageHeader from '../components/PageHeader.jsx';
import EmptyState from '../components/EmptyState.jsx';
import Pagination from '../components/Pagination.jsx';
import '../assets/CategoryPage.css';
import '../assets/Components.css';

export default function CategoryPage() {
  const { categoryName } = useParams();
  const decodedCategory = decodeURIComponent(categoryName || '');

  // data states
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);

  // ui states
  const [query, setQuery] = useState('');
  const [priceRange, setPriceRange] = useState({ min: '', max: '' });
  const [sortBy, setSortBy] = useState('relevance');
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // load categories + products from API
  useEffect(() => {
    let cancelled = false;
    async function load() {
      setLoading(true);
      setError(null);
      try {
        const [cRes, pRes] = await Promise.all([
          axios.get('/api/categories/'),
          axios.get('/api/products/')
        ]);
        if (cancelled) return;
        const cats = Array.isArray(cRes.data) ? cRes.data : (cRes.data.results ?? []);
        setCategories(cats);
        const prods = Array.isArray(pRes.data) ? pRes.data : (pRes.data.results ?? []);
        setProducts(prods);
        setCurrentPage(1);
      } catch (err) {
        if (cancelled) return;
        console.error('CategoryPage load error', err);
        setError(err.response?.data ?? err.message ?? 'L·ªói khi t·∫£i d·ªØ li·ªáu');
      } finally {
        if (!cancelled) setLoading(false);
      }
    }
    load();
    return () => { cancelled = true; };
  }, []);

  // current selected category name
  const currentCategory = useMemo(() => {
    if (!decodedCategory) return 'T·∫•t c·∫£';
    // try find by slug or name
    const found = categories.find(c => c.slug === categoryName || c.slug === decodedCategory || c.name === decodedCategory);
    return found ? found.name : decodedCategory;
  }, [categories, categoryName, decodedCategory]);

  // filtering logic
  const filteredProducts = useMemo(() => {
    let filtered = products.filter(product => {
      // category filter
      const prodCatName = typeof product.category === 'string' ? product.category : (product.category?.name ?? '');
      const matchesCategory = currentCategory === 'T·∫•t c·∫£' || !currentCategory || prodCatName === currentCategory;

      // query filter
      const q = (query || '').trim().toLowerCase();
      const matchesQuery = !q || (
        (product.name || '').toLowerCase().includes(q) ||
        (product.description || '').toLowerCase().includes(q)
      );

      // price range filter
      const price = Number(product.price ?? 0);
      const minOk = !priceRange.min || price >= Number(priceRange.min);
      const maxOk = !priceRange.max || price <= Number(priceRange.max);

      return matchesCategory && matchesQuery && minOk && maxOk;
    });

    // sorting
    switch (sortBy) {
      case 'price-low':
        filtered.sort((a, b) => (Number(a.price) || 0) - (Number(b.price) || 0));
        break;
      case 'price-high':
        filtered.sort((a, b) => (Number(b.price) || 0) - (Number(a.price) || 0));
        break;
      case 'name':
        filtered.sort((a, b) => (a.name || '').localeCompare(b.name || ''));
        break;
      case 'newest':
        filtered.sort((a, b) => (b.id || 0) - (a.id || 0));
        break;
      default:
        break;
    }

    return filtered;
  }, [products, currentCategory, query, priceRange, sortBy]);

  // pagination
  const ITEMS_PER_PAGE = 12;
  const totalPages = Math.max(1, Math.ceil(filteredProducts.length / ITEMS_PER_PAGE));
  useEffect(() => {
    if (currentPage > totalPages) setCurrentPage(totalPages);
  }, [currentPage, totalPages]);

  const paginatedProducts = useMemo(() => {
    const start = (currentPage - 1) * ITEMS_PER_PAGE;
    return filteredProducts.slice(start, start + ITEMS_PER_PAGE);
  }, [filteredProducts, currentPage]);

  const clearFilters = () => {
    setQuery('');
    setPriceRange({ min: '', max: '' });
    setSortBy('relevance');
    setCurrentPage(1);
  };

  return (
    <Layout className="category-page">
      <div className="category-container">
        <PageHeader
          title={currentCategory || 'T·∫•t c·∫£'}
          subtitle={`K·∫øt qu·∫£ cho ${currentCategory || 'T·∫•t c·∫£'}`}
          resultsCount={filteredProducts.length}
          breadcrumb={[{ text: 'Trang ch·ªß', link: '/' }, { text: currentCategory || 'T·∫•t c·∫£' }]}
          className="category-header"
        />

        <div className="category-content">
          <FiltersSidebar
            categories={['T·∫•t c·∫£', ...categories.map(c => c.name ?? c)]}
            selectedCategory={currentCategory || 'T·∫•t c·∫£'}
            onCategoryChange={(cat) => {
              // navigation handled by router; here update filter state
              setCurrentPage(1);
              setQuery('');
              setPriceRange({ min: '', max: '' });
              setSortBy('relevance');
            }}
            priceRange={priceRange}
            onPriceRangeChange={(range) => { setPriceRange(range); setCurrentPage(1); }}
            sortBy={sortBy}
            onSortChange={(s) => { setSortBy(s); setCurrentPage(1); }}
            onClearFilters={clearFilters}
            showCategoryLinks={true}
          />

          <div className="products-section">
            {loading ? (
              <div>ƒêang t·∫£i s·∫£n ph·∫©m...</div>
            ) : error ? (
              <div style={{ color: 'red' }}>{String(error)}</div>
            ) : filteredProducts.length === 0 ? (
              <EmptyState
                icon="üì¶"
                title="Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o"
                description="Hi·ªán t·∫°i ch∆∞a c√≥ s·∫£n ph·∫©m trong danh m·ª•c n√†y"
                actionText="Xem t·∫•t c·∫£ s·∫£n ph·∫©m"
                actionLink="/"
                className="no-results"
              />
            ) : (
              <>
                <ProductGrid products={paginatedProducts} />
                <Pagination
                  currentPage={currentPage}
                  totalPages={totalPages}
                  onPageChange={(p) => setCurrentPage(p)}
                />
              </>
            )}
          </div>
        </div>
      </div>
    </Layout>
  );
}
