import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../utils/CartContext";
import { useAuth } from "../utils/AuthContext";
import { formatPrice } from "../utils/formatPrice";
import {
  getCities,
  getDistrictsByCity,
  getWardsByDistrict,
} from "../utils/locationsData";
import "../assets/Checkout.css";

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:8000";

async function safeFetchJSON(url, options = {}) {
  const res = await fetch(url, options);
  const ct = res.headers.get("content-type") || "";
  if (!res.ok) {
    const txt = await res.text().catch(() => "");
    throw new Error(`HTTP ${res.status} - ${txt.slice(0, 200)}`);
  }
  if (!ct.includes("application/json")) {
    const txt = await res.text().catch(() => "");
    throw new Error("Server trả về non-JSON: " + txt.slice(0, 200));
  }
  return res.json();
}

export default function Checkout() {
  const navigate = useNavigate();
  const { cartItems, clearCart, getCartTotal } = useCart();
  const { user } = useAuth();

  const [formData, setFormData] = useState({
    fullName: "",
    phone: "",
    email: "",
    address: "",
    city: "",
    district: "",
    ward: "",
    paymentMethod: "cod",
    notes: "",
  });

  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [cities, setCities] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [wards, setWards] = useState([]);

  useEffect(() => {
    if (cartItems.length === 0) {
      navigate("/cart");
      return;
    }

    if (user) {
      setFormData((prev) => ({
        ...prev,
        fullName: user.full_name || "",
        phone: user.phone || "",
        email: user.email || "",
      }));

      const token = localStorage.getItem("access_token");
      if (token) {
        safeFetchJSON(`${API_BASE}/api/user/address/`, {
          headers: { Authorization: `Bearer ${token}` },
        })
          .then((data) => {
            if (data && (data.address || data.city)) {
              setFormData((prev) => ({
                ...prev,
                address: data.address || "",
                city: data.city || "",
                district: data.district || "",
                ward: data.ward || "",
              }));
            }
          })
          .catch((err) => console.error("Error loading address:", err.message));
      }
    }

    setCities(getCities());
  }, [cartItems, user, navigate]);

  useEffect(() => {
    if (formData.city) {
      setDistricts(getDistrictsByCity(formData.city));
    } else {
      setDistricts([]);
      setWards([]);
    }
  }, [formData.city]);

  useEffect(() => {
    if (formData.district) {
      setWards(getWardsByDistrict(formData.district));
    } else {
      setWards([]);
    }
  }, [formData.district]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));

    if (errors[name]) {
      setErrors((prev) => ({
        ...prev,
        [name]: "",
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.fullName.trim()) newErrors.fullName = "Vui lòng nhập họ tên";
    if (!formData.phone.trim()) newErrors.phone = "Vui lòng nhập số điện thoại";
    else if (!/^[0-9]{10,11}$/.test(formData.phone.trim()))
      newErrors.phone = "Số điện thoại không hợp lệ";
    if (!formData.email.trim()) newErrors.email = "Vui lòng nhập email";
    else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email))
      newErrors.email = "Email không hợp lệ";
    if (!formData.address.trim()) newErrors.address = "Vui lòng nhập địa chỉ";
    if (!formData.city) newErrors.city = "Vui lòng chọn tỉnh/thành phố";
    if (!formData.district) newErrors.district = "Vui lòng chọn quận/huyện";
    if (!formData.ward) newErrors.ward = "Vui lòng chọn phường/xã";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  async function handleSubmit(e) {
    e.preventDefault();
    if (!user) return;

    setLoading(true);
    setErrors({});
    try {
      // 1. Validate giỏ hàng
      if (!cartItems.length) {
        throw new Error("Giỏ hàng trống");
      }

      const total = Math.round(getCartTotal()); // đảm bảo integer
      if (total <= 0) {
        throw new Error("Tổng tiền không hợp lệ");
      }

      // 2. Tạo order trước (ví dụ gọi API orders)
      const token = localStorage.getItem("access_token");
      const orderRes = await safeFetchJSON(`${API_BASE}/api/orders/`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          items: cartItems.map((it) => ({
            product_id: it.id,
            quantity: it.quantity,
          })),
          shipping_address: {
            full_name: formData.fullName,
            phone: formData.phone,
            email: formData.email,
            address: formData.address,
            city: formData.city,
            district: formData.district,
            ward: formData.ward,
          },
          notes: formData.notes || "",
          payment_method: formData.paymentMethod,
        }),
      });

      const orderId = orderRes.id || orderRes.order_id;
      if (!orderId) {
        throw new Error("Không nhận được order_id");
      }

      // 3. Nếu chọn VNPay → gọi endpoint tạo link thanh toán
      if (formData.paymentMethod === "vnpay") {
        const payRes = await safeFetchJSON(`${API_BASE}/api/payment/vnpay/`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({
            order_id: String(orderId),
            amount: total, // backend sẽ *100
          }),
        });

        const paymentUrl = payRes.payment_url;
        if (!paymentUrl) {
          throw new Error("Không nhận được payment_url");
        }

        // 4. Clear cart rồi redirect
        clearCart();
        window.location.href = paymentUrl;
        return;
      }

      // 5. COD: clear cart và chuyển hướng trang cảm ơn
      clearCart();
      navigate(`/orders/${orderId}?success=1`);
    } catch (err) {
      console.error("❌ Error submitting order:", err);
      setErrors((prev) => ({ ...prev, submit: err.message || "Có lỗi xảy ra" }));
    } finally {
      setLoading(false);
    }
  }

  if (cartItems.length === 0) return null;

  return (
    <div className="checkout-page">
      <div className="checkout-container">
        <div className="checkout-header">
          <h1>Thanh toán</h1>
          <div className="checkout-steps">
            <div className="step active">1. Thông tin giao hàng</div>
            <div className="step">2. Xác nhận đơn hàng</div>
            <div className="step">3. Hoàn tất</div>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="checkout-form">
          <div className="checkout-content">
            <div className="shipping-info">
              <h2>Thông tin giao hàng</h2>
              {/* --- Form thông tin người nhận --- */}
              {/* Họ tên, điện thoại */}
              <div className="form-row">
                <div className="form-group">
                  <label>Họ và tên *</label>
                  <input
                    type="text"
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleInputChange}
                    className={errors.fullName ? "error" : ""}
                    placeholder="Nhập họ và tên"
                  />
                  {errors.fullName && (
                    <span className="error-message">{errors.fullName}</span>
                  )}
                </div>
                <div className="form-group">
                  <label>Số điện thoại *</label>
                  <input
                    type="tel"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    className={errors.phone ? "error" : ""}
                    placeholder="Nhập số điện thoại"
                  />
                  {errors.phone && (
                    <span className="error-message">{errors.phone}</span>
                  )}
                </div>
              </div>

              {/* Email */}
              <div className="form-group">
                <label>Email *</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className={errors.email ? "error" : ""}
                  placeholder="Nhập email"
                />
                {errors.email && (
                  <span className="error-message">{errors.email}</span>
                )}
              </div>

              {/* Địa chỉ */}
              <div className="form-row">
                <div className="form-group">
                  <label>Tỉnh/Thành phố *</label>
                  <select
                    name="city"
                    value={formData.city}
                    onChange={handleInputChange}
                    className={errors.city ? "error" : ""}
                  >
                    <option value="">Chọn tỉnh/thành phố</option>
                    {cities.map((c) => (
                      <option key={c.id} value={c.id}>
                        {c.name}
                      </option>
                    ))}
                  </select>
                  {errors.city && (
                    <span className="error-message">{errors.city}</span>
                  )}
                </div>

                <div className="form-group">
                  <label>Quận/Huyện *</label>
                  <select
                    name="district"
                    value={formData.district}
                    onChange={handleInputChange}
                    className={errors.district ? "error" : ""}
                    disabled={!formData.city}
                  >
                    <option value="">Chọn quận/huyện</option>
                    {districts.map((d) => (
                      <option key={d.id} value={d.id}>
                        {d.name}
                      </option>
                    ))}
                  </select>
                  {errors.district && (
                    <span className="error-message">{errors.district}</span>
                  )}
                </div>
              </div>

              <div className="form-group">
                <label>Phường/Xã *</label>
                <select
                  name="ward"
                  value={formData.ward}
                  onChange={handleInputChange}
                  className={errors.ward ? "error" : ""}
                  disabled={!formData.district}
                >
                  <option value="">Chọn phường/xã</option>
                  {wards.map((w) => (
                    <option key={w.id} value={w.id}>
                      {w.name}
                    </option>
                  ))}
                </select>
                {errors.ward && (
                  <span className="error-message">{errors.ward}</span>
                )}
              </div>

              <div className="form-group">
                <label>Địa chỉ chi tiết *</label>
                <textarea
                  name="address"
                  value={formData.address}
                  onChange={handleInputChange}
                  className={errors.address ? "error" : ""}
                  placeholder="Số nhà, tên đường..."
                />
                {errors.address && (
                  <span className="error-message">{errors.address}</span>
                )}
              </div>

              <div className="form-group">
                <label>Ghi chú</label>
                <textarea
                  name="notes"
                  value={formData.notes}
                  onChange={handleInputChange}
                  placeholder="Ghi chú thêm..."
                />
              </div>
            </div>

            {/* --- Tóm tắt đơn hàng --- */}
            <div className="order-summary">
              <h2>Đơn hàng của bạn</h2>
              <div className="order-items">
                {cartItems.map((item, idx) => (
                  <div key={idx} className="order-item">
                    <img
                      src={
                        item.image ||
                        process.env.PUBLIC_URL + "/default-product.png"
                      }
                      alt={item.name}
                    />
                    <div className="item-info">
                      <h4>{item.name}</h4>
                      {item.color && <span>Màu: {item.color}</span>}
                      {item.size && <span>Size: {item.size}</span>}
                      <div>Số lượng: {item.quantity}</div>
                    </div>
                    <div className="item-price">
                      {formatPrice(item.price * item.quantity)}
                    </div>
                  </div>
                ))}
              </div>

              {/* --- Phương thức thanh toán --- */}
              <div className="payment-method">
                <h3>Phương thức thanh toán</h3>
                <div className="payment-options">
                  <label className="payment-option">
                    <input
                      type="radio"
                      name="paymentMethod"
                      value="cod"
                      checked={formData.paymentMethod === "cod"}
                      onChange={handleInputChange}
                    />
                    <span>Thanh toán khi nhận hàng (COD)</span>
                  </label>
                  <label className="payment-option">
                    <input
                      type="radio"
                      name="paymentMethod"
                      value="vnpay"
                      checked={formData.paymentMethod === "vnpay"}
                      onChange={handleInputChange}
                    />
                    <span>Thanh toán qua VNPay</span>
                  </label>
                  <label className="payment-option">
                    <input
                      type="radio"
                      name="paymentMethod"
                      value="momo"
                      checked={formData.paymentMethod === "momo"}
                      onChange={handleInputChange}
                    />
                    <span>Ví MoMo</span>
                  </label>
                </div>
              </div>

              {/* --- Tổng cộng --- */}
              <div className="order-total">
                <div className="total-row">
                  <span>Tạm tính:</span>
                  <span>{formatPrice(getCartTotal())}</span>
                </div>
                <div className="total-row">
                  <span>Phí vận chuyển:</span>
                  <span className="free-shipping">Miễn phí</span>
                </div>
                <div className="total-row final">
                  <span>Tổng cộng:</span>
                  <span>{formatPrice(getCartTotal())}</span>
                </div>
              </div>

              <button
                type="submit"
                className="place-order-btn"
                disabled={loading}
              >
                {loading ? "Đang xử lý..." : "Đặt hàng"}
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}
