import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useCart } from '../utils/CartContext';
import { formatPrice } from '../utils/formatPrice';
import '../assets/productDetail.css';

export default function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [mainImage, setMainImage] = useState('');
  const [color, setColor] = useState('');
  const [size, setSize] = useState('');
  const [qty, setQty] = useState(1);

  useEffect(() => {
    fetch(`http://localhost:8000/api/products/${id}/`)
      .then(res => {
        if (!res.ok) throw new Error('Product not found');
        return res.json();
      })
      .then(data => {
        setProduct(data);
        setMainImage(data.image || '/default-product.png');
        setLoading(false);
      })
      .catch(err => {
        console.error('Error loading product:', err);
        setLoading(false);
      });
  }, [id]);

  if (loading) {
    return (
      <div className="product-detail">
        <div className="loading-container">
          <div className="spinner"></div>
          <p className="loading-text">ƒêang t·∫£i s·∫£n ph·∫©m...</p>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="product-detail">
        <div className="empty-state">
          <h2>Kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m</h2>
          <button onClick={() => navigate('/')} className="btn btn-primary">
            Quay v·ªÅ trang ch·ªß
          </button>
        </div>
      </div>
    );
  }

  const colors = product.variants?.colors || [];
  const sizes = product.variants?.sizes || [];
  const specs = product.specs || [];
  const description = product.description || 'Ch∆∞a c√≥ m√¥ t·∫£ cho s·∫£n ph·∫©m n√†y.';
  
  // Shop info
  const shop = product.shop || product.seller || {
    id: product.user?.id,
    name: product.user?.shop_name || product.user?.username || 'Shop ch∆∞a c√≥ t√™n',
    avatar: product.user?.avatar || '/default-avatar.png',
    rating: 4.5,
    totalProducts: 0
  };

  function dec() {
    setQty(prev => Math.max(1, prev - 1));
  }

  function inc() {
    setQty(prev => Math.min(prev + 1, product.stock || 999));
  }

  function handleAddToCart() {
    if (colors.length > 0 && !color) {
      alert('Vui l√≤ng ch·ªçn m√†u s·∫Øc');
      return;
    }
    if (sizes.length > 0 && !size) {
      alert('Vui l√≤ng ch·ªçn k√≠ch th∆∞·ªõc');
      return;
    }
    addToCart(product, qty, { color, size });
