import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../utils/AuthContext';

export default function Header({
  query = '',
  onQueryChange,
  onOpenLogin,
  onOpenRegister,
  onLogout: onLogoutProp,
  currentUser: currentUserProp
}) {
  const navigate = useNavigate();
  const auth = useAuth();
  // prefer props if provided, otherwise use context
  const currentUser = currentUserProp ?? auth?.user;
  const logout = onLogoutProp ?? auth?.logout ?? (() => {});
  const [menuOpen, setMenuOpen] = useState(false);
  const [localQuery, setLocalQuery] = useState(query || '');

  function onSearch(e) {
    e?.preventDefault();
    const q = (onQueryChange ? localQuery : localQuery).trim();
    if (q) navigate(`/search?q=${encodeURIComponent(q)}`);
    else navigate('/');
  }

  return (
    <header className="topbar">
      <Link to="/" className="brand" aria-label="V·ªÅ trang ch·ªß">
        <span className="brand-name">V-Market</span>
      </Link>

      <form className="search-area" onSubmit={onSearch}>
        <input
          type="text"
          placeholder="T√¨m s·∫£n ph·∫©m, th∆∞∆°ng hi·ªáu..."
          value={onQueryChange ? localQuery : localQuery}
          onChange={(e) => {
            setLocalQuery(e.target.value);
            if (onQueryChange) onQueryChange(e.target.value);
          }}
          className="search-input"
        />
        <button type="submit" className="search-btn">T√¨m</button>
      </form>

      <div className="actions">
        {currentUser ? (
          <div className="user-avatar">
            <img
              src={currentUser.avatar || '/default-avatar.png'}
              alt={currentUser.full_name || currentUser.username}
              onClick={() => setMenuOpen(!menuOpen)}
              style={{ cursor: 'pointer', width: 36, height: 36, borderRadius: '50%' }}
            />
            {menuOpen && (
              <div className="user-menu show" role="menu">
                {/* Gi·ªØ l·∫°i ch·ªâ buyer v√† seller */}
                {currentUser.user_type === 'buyer' && (
                  <Link to="/dashboard" onClick={() => setMenuOpen(false)}>Trang mua h√†ng</Link>
                )}
                {currentUser.user_type === 'seller' && (
                  <Link to="/seller/dashboard" onClick={() => setMenuOpen(false)}>Trang b√°n h√†ng</Link>
                )}

                <Link to="/profile" onClick={() => setMenuOpen(false)}>Trang c√° nh√¢n</Link>
                <button
                  onClick={() => {
                    logout();
                    setMenuOpen(false);
                  }}
                >
                  ƒêƒÉng xu·∫•t
                </button>
              </div>
            )}
          </div>
        ) : (
          <>
            <button
              type="button"
              className="action-btn"
              onClick={() => {
                if (onOpenLogin) return onOpenLogin();
                if (auth?.openLogin) return auth.openLogin();
                // fallback: navigate to login page
                navigate('/login');
              }}
            >
              ƒêƒÉng nh·∫≠p
            </button>
            <button
              type="button"
              className="action-btn"
              onClick={() => {
                if (onOpenRegister) return onOpenRegister();
                if (auth?.openRegister) return auth.openRegister();
                navigate('/register');
              }}
            >
              ƒêƒÉng k√Ω
            </button>
          </>
        )}

        <Link to="/cart" className="cart-btn" aria-label="Gi·ªè h√†ng">
          üõí<span className="cart-dot" />
        </Link>
      </div>
    </header>
  );
}
