import React, { useState, useEffect } from 'react';
import { useAuth } from '../utils/AuthContext';
import { useNavigate } from 'react-router-dom';
import authService from '../utils/authService';
import '../assets/UserProfile.css';

const Profile = () => {
  const { user, updateUser } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('profile');
  const [isEditing, setIsEditing] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });

  const [formData, setFormData] = useState({
    full_name: '',
    phone: '',
    email: '',
    bio: '',
    address: '',
    city: '',
    district: '',
    ward: '',
    postal_code: '',
    country: 'Vietnam',
  });

  const [cities, setCities] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [wards, setWards] = useState([]);

  useEffect(() => {
    if (user) {
      setFormData({
        full_name: user.full_name || '',
        phone: user.phone || '',
        email: user.email || '',
        bio: user.profile?.bio || '',
        address: user.profile?.address || '',
        city: user.profile?.city || '',
        district: user.profile?.district || '',
        ward: user.profile?.ward || '',
        postal_code: user.profile?.postal_code || '',
        country: user.profile?.country || 'Vietnam',
      });
    }

    // Load cities
    fetch('/api/locations/cities/')
      .then(res => res.json())
      .then(data => setCities(data))
      .catch(err => console.error('Error loading cities:', err));
  }, [user]);

  useEffect(() => {
    if (formData.city) {
      fetch(`/api/locations/districts/?city=${formData.city}`)
        .then(res => res.json())
        .then(data => setDistricts(data))
        .catch(err => console.error('Error loading districts:', err));
    }
  }, [formData.city]);

  useEffect(() => {
    if (formData.district) {
      fetch(`/api/locations/wards/?district=${formData.district}`)
        .then(res => res.json())
        .then(data => setWards(data))
        .catch(err => console.error('Error loading wards:', err));
    }
  }, [formData.district]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setMessage({ type: '', text: '' });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      const updateData = {
        full_name: formData.full_name,
        phone: formData.phone,
        profile: {
          bio: formData.bio,
          address: formData.address,
          city: formData.city,
          district: formData.district,
          ward: formData.ward,
          postal_code: formData.postal_code,
          country: formData.country,
        },
      };

      const response = await fetch('/api/user/profile/', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('access_token')}`
        },
        body: JSON.stringify(updateData)
      });

      if (!response.ok) throw new Error('Update failed');

      const result = await response.json();
      updateUser(result.user);
      
      setMessage({ 
        type: 'success', 
        text: 'C·∫≠p nh·∫≠t th√¥ng tin th√†nh c√¥ng!' 
      });
      setIsEditing(false);
    } catch (error) {
      setMessage({ 
        type: 'error', 
        text: error.message || 'C·∫≠p nh·∫≠t th·∫•t b·∫°i. Vui l√≤ng th·ª≠ l·∫°i!' 
      });
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    if (user) {
      setFormData({
        full_name: user.full_name || '',
        phone: user.phone || '',
        email: user.email || '',
        bio: user.profile?.bio || '',
        address: user.profile?.address || '',
        city: user.profile?.city || '',
        district: user.profile?.district || '',
        ward: user.profile?.ward || '',
        postal_code: user.profile?.postal_code || '',
        country: user.profile?.country || 'Vietnam',
      });
    }
    setIsEditing(false);
    setMessage({ type: '', text: '' });
  };

  const renderProfileTab = () => (
    <div className="profile-form">
      {message.text && (
        <div className={`alert ${message.type === 'success' ? 'alert-success' : 'alert-error'}`}>
          {message.text}
        </div>
      )}

      <div className="form-section">
        <h3>Th√¥ng tin c√° nh√¢n</h3>
        <div className="form-row">
          <div className="form-group">
            <label>H·ªç v√† t√™n</label>
            {isEditing ? (
              <input
                type="text"
                name="full_name"
                value={formData.full_name}
                onChange={handleChange}
                placeholder="Nh·∫≠p h·ªç v√† t√™n"
              />
            ) : (
              <p>{formData.full_name || 'Ch∆∞a c·∫≠p nh·∫≠t'}</p>
            )}
          </div>

          <div className="form-group">
            <label>S·ªë ƒëi·ªán tho·∫°i</label>
            {isEditing ? (
              <input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                placeholder="Nh·∫≠p s·ªë ƒëi·ªán tho·∫°i"
              />
            ) : (
              <p>{formData.phone || 'Ch∆∞a c·∫≠p nh·∫≠t'}</p>
            )}
          </div>
        </div>

        <div className="form-group">
          <label>Email</label>
          <p>{formData.email}</p>
        </div>

        <div className="form-group">
          <label>Gi·ªõi thi·ªáu</label>
          {isEditing ? (
            <textarea
              name="bio"
              value={formData.bio}
              onChange={handleChange}
              placeholder="Vi·∫øt v√†i d√≤ng v·ªÅ b·∫£n th√¢n..."
              rows="4"
            />
