export default function PromoAside() {
  return (
    <aside className="side-banner">
      <div className="promo-card">
        <h3>Miễn phí vận chuyển</h3>
        <p>Đơn từ 0đ - Áp dụng toàn quốc</p>
      </div>
      <div className="promo-card secondary">
        <h3>Flash Sale</h3>
        <p>Giảm sốc mỗi ngày, săn deal ngay!</p>
      </div>
    </aside>
  );
}
