// components/ChatButton.jsx
import React, { useEffect, useState } from 'react';
import PopupChat from './PopupChat';

export default function ChatButton({ user }) {
  const [conversations, setConversations] = useState([]);
  const [openChats, setOpenChats] = useState([]);

  // Fetch conversation list từ API
  useEffect(() => {
    if (!user) return;

    const apiUrl = user.user_type === 'buyer'
      ? `/api/conversations/buyer/${user.user_id}/`
      : `/api/conversations/seller/${user.user_id}/`;

    fetch(apiUrl)
      .then(res => res.json())
      .then(data => setConversations(data))
      .catch(err => console.error(err));
  }, [user]);

  const toggleChat = (convId) => {
    setOpenChats(prev => prev.includes(convId) ? prev.filter(id => id !== convId) : [...prev, convId]);
  };

  return (
    <>
      {/* Icon Messenger góc phải dưới */}
      <div style={{
        position: 'fixed',
        bottom: 20,
        right: 20,
        display: 'flex',
        flexDirection: 'column-reverse',
        gap: 10,
        zIndex: 9999
      }}>
        {conversations.map(conv => (
          <button
            key={conv.id}
            onClick={() => toggleChat(conv.id)}
            style={{
              width: 50,
              height: 50,
              borderRadius: '50%',
              background: '#2563eb',
              color: '#fff',
              border: 'none',
              cursor: 'pointer'
            }}
            title={conv.name}
          >
            {conv.name[0].toUpperCase()}
          </button>
        ))}
      </div>

      {/* Popup chat */}
      {openChats.map(convId => {
        const conv = conversations.find(c => c.id === convId);
        return (
          <PopupChat
            key={convId}
            conversation={conv}
            user={user}
            onClose={() => toggleChat(convId)}
          />
        );
      })}
    </>
  );
}
