import React, { useState } from 'react';
import ChatPopup from './ChatPopup';

export default function ChatButton({ shopId, buyerId }) {
  const [open, setOpen] = useState(false);

  return (
    <>
      {/* Popup chat */}
      {open && <ChatPopup shopId={shopId} buyerId={buyerId} onClose={() => setOpen(false)} />}

      {/* Floating button */}
      <button
        onClick={() => setOpen(prev => !prev)}
        style={{
          position: 'fixed',
          bottom: 20,
          right: 20,
          width: 60,
          height: 60,
          borderRadius: '50%',
          background: '#2563eb',
          color: '#fff',
          border: 'none',
          cursor: 'pointer',
          boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
          fontSize: 24,
        }}
      >
        ðŸ’¬
      </button>
    </>
  );
}
