// components/ChatButton.jsx
import React, { useState, useEffect } from 'react';
import PopupChat from './PopupChat';

export default function ChatButton({ user }) {
  const [openChats, setOpenChats] = useState([]); // popup đang mở
  const [chatList, setChatList] = useState([]);   // danh sách các cuộc chat

  const toggleChat = (chatId) => {
    setOpenChats(prev => prev.includes(chatId) ? prev.filter(id => id !== chatId) : [...prev, chatId]);
  };

  useEffect(() => {
    if (!user) return;

    // Ví dụ API trả về danh sách các cuộc chat: { id, name } 
    // id: shopId nếu buyer, buyerId nếu seller
    fetch(`/api/chats?userId=${user.user_id}`)
      .then(res => res.json())
      .then(data => setChatList(data))
      .catch(err => console.error('Failed to fetch chats', err));
  }, [user]);

  if (!chatList.length) return null; // chưa có chat thì không hiện button

  return (
    <>
      {/* Icon Messenger góc phải dưới */}
      <div style={{
        position: 'fixed',
        bottom: 20,
        right: 20,
        display: 'flex',
        flexDirection: 'column-reverse',
        gap: 10,
        zIndex: 9999
      }}>
        {chatList.map(chat => (
          <button
            key={chat.id}
            onClick={() => toggleChat(chat.id)}
            style={{
              width: 50,
              height: 50,
              borderRadius: '50%',
              background: '#2563eb',
              color: '#fff',
              border: 'none',
              cursor: 'pointer'
            }}
          >
            {chat.name[0].toUpperCase()}
          </button>
        ))}
      </div>

      {/* Popup chat */}
      {openChats.map(chatId => (
        <PopupChat
          key={chatId}
          shopId={chatId}   // nếu là buyer, shopId = chatId, nếu seller cần truyền buyerId
          userId={user.user_id}
          onClose={() => toggleChat(chatId)}
        />
      ))}
    </>
  );
}
