import React, { useEffect, useState, createContext, useContext, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../utils/authService";
import '../assets/authPages.css';

// ---- Auth Context (moved from utils/AuthContext.jsx) ----
const AuthContext = createContext();
export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // modal state
  const [authOpen, setAuthOpen] = useState(false);
  const [authMode, setAuthMode] = useState("login"); // 'login' | 'register'

  const navigate = useNavigate();

  useEffect(() => {
    const loadUser = async () => {
      const token = localStorage.getItem('access_token');
      if (token) {
        try {
          const apiUser = await authService.getCurrentUser();
          setUser(apiUser);
          localStorage.setItem('user', JSON.stringify(apiUser));
        } catch (err) {
          console.warn('Auth: token invalid, clearing', err);
          setUser(null);
          localStorage.removeItem('access_token');
          localStorage.removeItem('refresh_token');
          localStorage.removeItem('user');
        }
      }
      setLoading(false);
    };
    loadUser();
  }, []);

  const getDefaultRoute = (userType) => {
    switch (userType) {
      case 'admin': return '/admin/dashboard';
      case 'seller': return '/seller/dashboard';
      case 'buyer': return '/dashboard';
      default: return '/dashboard';
    }
  };

  const login = async (usernameOrEmail, password) => {
    const data = await authService.login(usernameOrEmail, password);
    const u = data.user;
    setUser(u);
    if (u) localStorage.setItem('user', JSON.stringify(u));
    const defaultRoute = getDefaultRoute(u?.user_type);
    setAuthOpen(false);
    navigate(defaultRoute);
    return { success: true };
  };

  const register = async (userData) => {
    const data = await authService.register(userData);
    const u = data.user;
    setUser(u);
    if (u) localStorage.setItem('user', JSON.stringify(u));
    const defaultRoute = getDefaultRoute(u?.user_type);
    setAuthOpen(false);
    navigate(defaultRoute);
    return { success: true };
  };

  const logout = async () => {
    await authService.logout();
    setUser(null);
    navigate('/login');
  };

  const updateUser = useCallback((updatedUser) => {
    setUser(updatedUser);
    localStorage.setItem('user', JSON.stringify(updatedUser));
  }, []);

  // Expose modal controls so app can open the modal anywhere
  const openAuthModal = (mode = 'login') => {
    setAuthMode(mode);
    setAuthOpen(true);
  };
  const closeAuthModal = () => setAuthOpen(false);

  const value = {
    user,
    loading,
    login,
    register,
    logout,
    updateUser,
    isAuthenticated: !!user,
    isAdmin: user?.user_type === 'admin',
    isSeller: user?.user_type === 'seller',
    isBuyer: user?.user_type === 'buyer',
    getDefaultRoute,
    openAuthModal,
    closeAuthModal,
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
      {/* Render modal globally */}
      <AuthModalUI
        open={authOpen}
        initialMode={authMode}
        onClose={closeAuthModal}
      />
    </AuthContext.Provider>
  );
};

// ---- Auth Modal UI (merged from components/AuthModal.jsx) ----
function AuthModalUI({
  open = false,
  initialMode = "login",
  onClose = () => {},
}) {
  const { login, register } = useAuth();

  const [mode, setMode] = useState(initialMode);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // form fields
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [userType, setUserType] = useState("buyer");

  useEffect(() => {
    setMode(initialMode);
    setError(null);
    setLoading(false);
    if (open) {
      setUsername("");
      setEmail("");
      setPassword("");
      setUserType("buyer");
    }
  }, [initialMode, open]);

  if (!open) return null;

  async function handleSubmit(e) {
    e?.preventDefault();
    setError(null);
    setLoading(true);
    try {
      if (mode === "login") {
        await login(username || email, password);
      } else {
        const payload = { username, email, password, user_type: userType };
        await register(payload);
      }
      setLoading(false);
      onClose();
    } catch (err) {
      setLoading(false);
      if (typeof err === "string") setError(err);
      else if (err && err.detail) setError(err.detail);
      else if (err && err.message) setError(err.message);
      else setError("Có lỗi xảy ra. Vui lòng thử lại.");
    }
  }

  return (
    <div className="auth-modal-overlay" role="dialog" aria-modal="true">
      <div className="auth-modal">
        <header className="auth-header">
          <h3>{mode === "login" ? "Đăng nhập" : "Đăng ký"}</h3>
          <button type="button" className="close-btn" onClick={onClose} aria-label="Close">✕</button>
        </header>

        <div className="auth-tabs">
          <button
            type="button"
            className={`tab ${mode === "login" ? "active" : ""}`}
            onClick={() => setMode("login")}
          >
            Đăng nhập
          </button>
          <button
            type="button"
            className={`tab ${mode === "register" ? "active" : ""}`}
            onClick={() => setMode("register")}
          >
            Đăng ký
          </button>
        </div>

        <form className="auth-form" onSubmit={handleSubmit}>
          {mode === "register" && (
            <>
              <label>
                Tên đăng nhập
                <input value={username} onChange={(e) => setUsername(e.target.value)} />
              </label>
              <label>
                Email
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
              </label>
              <label>
                Loại tài khoản
                <select value={userType} onChange={(e) => setUserType(e.target.value)}>
                  <option value="buyer">Buyer</option>
                  <option value="seller">Seller</option>
                </select>
              </label>
            </>
          )}

          {mode === "login" && (
            <>
              <label>
                Email hoặc username
                <input value={username} onChange={(e) => setUsername(e.target.value)} />
              </label>
            </>
          )}

          <label>
            Mật khẩu
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          </label>

          {error && <div className="auth-error" role="alert">{String(error)}</div>}

          <div className="auth-actions">
            <button type="submit" disabled={loading}>
              {loading ? "Đang xử lý..." : mode === "login" ? "Đăng nhập" : "Đăng ký"}
            </button>

            <button
              type="button"
              className="switch-btn"
              onClick={() => setMode(mode === "login" ? "register" : "login")}
            >
              {mode === "login" ? "Chưa có tài khoản? Đăng ký" : "Đã có tài khoản? Đăng nhập"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default AuthModalUI;
