import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useCart } from '../utils/CartContext';
import { formatPrice } from '../utils/formatPrice';
import '../assets/productDetail.css';

export default function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [mainImage, setMainImage] = useState('');
  const [color, setColor] = useState('');
  const [size, setSize] = useState('');
  const [qty, setQty] = useState(1);

  useEffect(() => {
    fetch(`http://localhost:8000/api/products/${id}/`)
      .then(res => {
        if (!res.ok) throw new Error('Product not found');
        return res.json();
      })
      .then(data => {
        console.log('Product data:', data);
        setProduct(data);
        // Set main image
        if (data.images && data.images.length > 0) {
          setMainImage(data.images[0]);
        } else if (data.image) {
          setMainImage(data.image);
        } else {
          setMainImage('/default-product.png');
        }
        setLoading(false);
      })
      .catch(err => {
        console.error('Error loading product:', err);
        setLoading(false);
      });
  }, [id]);

  if (loading) {
    return (
      <div className="product-detail">
        <div style={{ textAlign: 'center', padding: '80px 20px' }}>
          <div className="spinner"></div>
          <p style={{ marginTop: '20px' }}>ƒêang t·∫£i s·∫£n ph·∫©m...</p>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="product-detail">
        <div style={{ textAlign: 'center', padding: '80px 20px' }}>
          <h2>Kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m</h2>
          <button 
            onClick={() => navigate('/')} 
            className="btn-back-home"
          >
            Quay v·ªÅ trang ch·ªß
          </button>
        </div>
      </div>
    );
  }

  const colors = product.colors || [];
  const sizes = product.sizes || [];
  const specs = product.specs || [];
  const description = product.description || 'Ch∆∞a c√≥ m√¥ t·∫£ cho s·∫£n ph·∫©m n√†y.';
  const images = product.images || [];
  const seller = product.seller_info;
  const stock = product.stock || 0;
  const reviews = product.reviews || [];

  function dec() {
    setQty(prev => Math.max(1, prev - 1));
  }

  function inc() {
    if (qty >= stock) {
      alert(`Ch·ªâ c√≤n ${stock} s·∫£n ph·∫©m trong kho`);
      return;
    }
    setQty(prev => prev + 1);
  }

  function handleAddToCart() {
    if (stock === 0) {
      alert('S·∫£n ph·∫©m ƒë√£ h·∫øt h√†ng');
      return;
    }

    if (colors.length > 0 && !color) {
      alert('Vui l√≤ng ch·ªçn m√†u s·∫Øc');
      return;
    }
    if (sizes.length > 0 && !size) {
      alert('Vui l√≤ng ch·ªçn k√≠ch th∆∞·ªõc');
      return;
    }

    addToCart(product, qty, { color, size });
    alert(`ƒê√£ th√™m ${qty} s·∫£n ph·∫©m v√†o gi·ªè h√†ng`);
  }

  function handleBuyNow() {
    if (stock === 0) {
      alert('S·∫£n ph·∫©m ƒë√£ h·∫øt h√†ng');
      return;
    }

    if (colors.length > 0 && !color) {
      alert('Vui l√≤ng ch·ªçn m√†u s·∫Øc');
      return;
    }
    if (sizes.length > 0 && !size) {
      alert('Vui l√≤ng ch·ªçn k√≠ch th∆∞·ªõc');
      return;
    }

    addToCart(product, qty, { color, size });
    navigate('/cart');
  }

  return (
    <div className="product-detail">
      <div className="product-detail-inner">
        {/* Left: Media */}
        <div className="product-detail-media">
          <div className="pd-main-media">
            <img src={mainImage} alt={product.name} />
          </div>
          
          {images.length > 0 && (
            <div className="pd-thumbs">
              {images.map((img, i) => (
                <div
                  key={i}
                  className={`pd-thumb ${mainImage === img ? 'active' : ''}`}
                  onClick={() => setMainImage(img)}
                >
                  <img src={img} alt={`${product.name} ${i + 1}`} />
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Right: Info */}
        <div className="product-detail-info">
          <h1>{product.name}</h1>
          
          {/* Rating */}
          {product.average_rating > 0 && (
            <div className="product-rating">
              <span className="stars">{'‚≠ê'.repeat(Math.round(product.average_rating))}</span>
              <span className="rating-text">
                {product.average_rating.toFixed(1)} ({product.total_reviews} ƒë√°nh gi√°)
              </span>
            </div>
          )}

          <div className="product-detail-price">
            {formatPrice(product.price)}
          </div>

          {/* Seller Info */}
          {seller && (
            <div className="pd-card seller-info">
              <div className="seller-header">
                <div className="seller-avatar">
                  {seller.full_name?.charAt(0) || seller.username?.charAt(0) || 'S'}
                </div>
                <div className="seller-details">
                  <div className="seller-name">{seller.full_name || seller.username}</div>
                  <div className="seller-meta">
                    <span>üìß {seller.email}</span>
                    {seller.phone && <span>üìû {seller.phone}</span>}
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Stock Status */}
          <div className={`stock-status ${stock === 0 ? 'out-of-stock' : stock < 10 ? 'low-stock' : ''}`}>
            {stock === 0 ? (
              <span className="text-danger">‚ùå H·∫øt h√†ng</span>
            ) : stock < 10 ? (
              <span className="text-warning">‚ö†Ô∏è Ch·ªâ c√≤n {stock} s·∫£n ph·∫©m</span>
            ) : (
              <span className="text-success">‚úÖ C√≤n h√†ng ({stock} s·∫£n ph·∫©m)</span>
            )}
          </div>

          {/* Color selection */}
          {colors.length > 0 && (
            <div className="pd-card">
              <div className="pd-row">
                <span className="pd-label">M√†u s·∫Øc:</span>
                <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
                  {colors.map(c => (
                    <button
                      key={c}
                      className={`pd-chip ${color === c ? 'active' : ''}`}
                      onClick={() => setColor(c)}
                    >
                      {c}
                    </button>
                  ))}
                </div>
              </div>
            </div>
          )}

          {/* Size selection */}
          {sizes.length > 0 && (
            <div className="pd-card">
              <div className="pd-row">
                <span className="pd-label">K√≠ch th∆∞·ªõc:</span>
                <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
                  {sizes.map(s => (
                    <button
                      key={s}
                      className={`pd-chip ${size === s ? 'active' : ''}`}
                      onClick={() => setSize(s)}
                    >
                      {s}
                    </button>
                  ))}
                </div>
              </div>
            </div>
          )}

          {/* Quantity */}
          <div className="pd-card">
            <div className="pd-row">
              <span className="pd-label">S·ªë l∆∞·ª£ng:</span>
              <div className="pd-qty">
                <button onClick={dec} disabled={stock === 0}>‚àí</button>
                <input type="number" value={qty} readOnly />
                <button onClick={inc} disabled={stock === 0}>+</button>
              </div>
              <span className="qty-note">T·ªëi ƒëa: {stock}</span>
            </div>
          </div>

          {/* Actions */}
          <div className="pd-actions">
            <button 
              className="add-cart" 
              onClick={handleAddToCart}
              disabled={stock === 0}
            >
              {stock === 0 ? 'H·∫øt h√†ng' : 'Th√™m v√†o gi·ªè'}
            </button>
            <button 
              className="buy-now" 
              onClick={handleBuyNow}
              disabled={stock === 0}
            >
              {stock === 0 ? 'H·∫øt h√†ng' : 'Mua ngay'}
            </button>
          </div>

          {/* Meta info */}
          <div className="pd-meta">
            <div><strong>Danh m·ª•c:</strong> {product.category_name || 'Ch∆∞a ph√¢n lo·∫°i'}</div>
            {product.brand && <div><strong>Th∆∞∆°ng hi·ªáu:</strong> {product.brand}</div>}
            {product.weight && <div><strong>Tr·ªçng l∆∞·ª£ng:</strong> {product.weight} kg</div>}
            {product.dimensions && <div><strong>K√≠ch th∆∞·ªõc:</strong> {product.dimensions}</div>}
          </div>
        </div>
      </div>

      {/* Sections below */}
      <div className="detail-sections">
        {/* Specifications */}
        {specs.length > 0 && (
          <div className="detail-section">
            <h3>Th√¥ng s·ªë k·ªπ thu·∫≠t</h3>
            <div className="specs">
              {specs.map((spec, i) => (
                <div className="pd-row" key={i}>
                  <span className="pd-label">{spec.label}:</span>
                  <div>{spec.value}</div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Description */}
        <div className="detail-section">
          <h3>M√¥ t·∫£ s·∫£n ph·∫©m</h3>
          <div className="pd-description">
            <pre>{description}</pre>
          </div>
        </div>

        {/* Reviews */}
        {reviews.length > 0 && (
          <div className="detail-section">
            <h3>ƒê√°nh gi√° s·∫£n ph·∫©m ({reviews.length})</h3>
            <div className="reviews-list">
              {reviews.map((review) => (
                <div key={review.id} className="review-item">
                  <div className="review-header">
                    <div className="review-user">
                      <strong>{review.user.full_name || review.user.username}</strong>
                    </div>
                    <div className="review-rating">
                      {'‚≠ê'.repeat(review.rating)}
                    </div>
                  </div>
                  <div className="review-comment">{review.comment}</div>
                  <div className="review-date">
                    {new Date(review.created_at).toLocaleDateString('vi-VN')}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
