import React, { useEffect, useState, useMemo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Layout from '../components/Layout.jsx';
import CategoryNav from '../components/CategoryNav.jsx';
import ProductGrid from '../components/ProductGrid.jsx';
import FiltersSidebar from '../components/FiltersSidebar.jsx';
import PageHeader from '../components/PageHeader.jsx';
import EmptyState from '../components/EmptyState.jsx';
import Pagination from '../components/Pagination.jsx';
import '../assets/CategoryPage.css';
import '../assets/Components.css';

export default function CategoryPage() {
  // support multiple possible param names: slug (used in App.jsx), categoryName, category
  const params = useParams();
  const rawCategoryParam = params.slug ?? params.categoryName ?? params.category ?? '';
  const decodedCategory = rawCategoryParam ? decodeURIComponent(rawCategoryParam) : '';

  const navigate = useNavigate();
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // UI filters
  const [query, setQuery] = useState('');
  const [priceRange, setPriceRange] = useState({ min: '', max: '' });
  const [sortBy, setSortBy] = useState('relevance');
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    let cancelled = false;
    async function load() {
      setLoading(true);
      try {
        const [cRes, pRes] = await Promise.all([axios.get('/api/categories/'), axios.get('/api/products/')]);
        if (cancelled) return;
        const cats = Array.isArray(cRes.data) ? cRes.data : (cRes.data.results ?? []);
        const prods = Array.isArray(pRes.data) ? pRes.data : (pRes.data.results ?? []);
        setCategories(cats);
        setProducts(prods);

        // debug: print shapes for inspection
        console.info('CategoryPage: loaded', { decodedCategory, categories: cats.slice(0,5), productsSample: prods.slice(0,5) });
      } catch (err) {
        if (cancelled) return;
        setError(err.response?.data ?? err.message ?? 'L·ªói t·∫£i d·ªØ li·ªáu');
      } finally {
        if (!cancelled) setLoading(false);
      }
    }
    load();
    return () => { cancelled = true; };
  }, [decodedCategory]);

  // helper: normalize string
  function norm(s) {
    return s == null ? '' : String(s).trim().toLowerCase();
  }

  // find active category (object) by slug or name
  const activeCategoryObj = useMemo(() => {
    if (!decodedCategory) return null;
    return categories.find(c => {
      const s = norm(c.slug ?? c.name ?? c.id);
      return s === norm(categoryName) || s === norm(decodedCategory) || s === norm(c.name);
    }) ?? null;
  }, [categories, categoryName, decodedCategory]);

  // robust product -> category matching
  function productMatchesCategory(prod, identifier) {
    if (!identifier || norm(identifier) === norm('T·∫•t c·∫£')) return true;
    const idNorm = norm(identifier);

    const pc = prod.category;
    if (pc == null) return false;

    // if product.category is primitive
    if (typeof pc === 'string' || typeof pc === 'number') {
      if (norm(pc) === idNorm) return true; // exact match on name/id/slug string
      // fuzzy contains (e.g. product.category may be 'Phones' and identifier 'phone')
      if (norm(pc).includes(idNorm) || idNorm.includes(norm(pc))) return true;
      return false;
    }

    // product.category is object
    const pid = pc.id ?? pc.pk ?? null;
    const pname = pc.name ?? pc.title ?? '';
    const pslug = pc.slug ?? '';

    if (pid != null && norm(pid) === idNorm) return true;
    if (norm(pname) === idNorm) return true;
    if (norm(pslug) === idNorm) return true;
    // fuzzy
    if (norm(pname).includes(idNorm) || norm(pslug).includes(idNorm) || idNorm.includes(norm(pname))) return true;

    return false;
  }

  const filteredProducts = useMemo(() => {
    const activeIdentifier = activeCategoryObj ? (activeCategoryObj.slug ?? activeCategoryObj.name ?? String(activeCategoryObj.id)) : (decodedCategory || 'T·∫•t c·∫£');

    // debug
    console.debug('CategoryPage: filtering with activeIdentifier=', activeIdentifier);

    return products.filter(product => {
      // category
      if (!productMatchesCategory(product, activeIdentifier)) return false;

      // query
      const q = norm(query);
      if (q) {
        const name = norm(product.name);
        const desc = norm(product.description);
        if (!name.includes(q) && !desc.includes(q)) return false;
      }

      // price
      const price = Number(product.price ?? 0);
      if (priceRange.min && price < Number(priceRange.min)) return false;
      if (priceRange.max && price > Number(priceRange.max)) return false;

      return true;
    }).sort((a, b) => {
      switch (sortBy) {
        case 'price-low': return (Number(a.price)||0) - (Number(b.price)||0);
        case 'price-high': return (Number(b.price)||0) - (Number(a.price)||0);
        case 'name': return (a.name||'').localeCompare(b.name||'');
        case 'newest': return (b.id||0) - (a.id||0);
        default: return 0;
      }
    });
  }, [products, activeCategoryObj, decodedCategory, query, priceRange, sortBy]);

  const ITEMS_PER_PAGE = 12;
  const totalPages = Math.max(1, Math.ceil(filteredProducts.length / ITEMS_PER_PAGE));
  useEffect(() => { if (currentPage > totalPages) setCurrentPage(totalPages); }, [totalPages, currentPage]);
  const paginatedProducts = useMemo(() => {
    const start = (currentPage - 1) * ITEMS_PER_PAGE;
    return filteredProducts.slice(start, start + ITEMS_PER_PAGE);
  }, [filteredProducts, currentPage]);

  function handleCategoryChange(value) {
    if (!value || value === 'T·∫•t c·∫£') { navigate('/'); return; }
    const found = categories.find(c => (c.slug === value || String(c.id) === String(value) || c.name === value));
    const toSlug = found ? (found.slug ?? found.name) : value;
    navigate(`/category/${encodeURIComponent(toSlug)}`);
  }

  return (
    <Layout className="category-page">
      <div className="category-container">
        <PageHeader
          title={activeCategoryObj ? activeCategoryObj.name : (decodedCategory || 'T·∫•t c·∫£')}
          subtitle={`K·∫øt qu·∫£ cho ${activeCategoryObj ? activeCategoryObj.name : (decodedCategory || 'T·∫•t c·∫£')}`}
          resultsCount={filteredProducts.length}
          breadcrumb={[{ text: 'Trang ch·ªß', link: '/' }, { text: activeCategoryObj ? activeCategoryObj.name : (decodedCategory || 'T·∫•t c·∫£') }]}
          className="category-header"
        />

        <CategoryNav
          categories={['T·∫•t c·∫£', ...categories]}
          active={activeCategoryObj ? (activeCategoryObj.name ?? activeCategoryObj.slug) : (decodedCategory || 'T·∫•t c·∫£')}
          onChange={handleCategoryChange}
        />

        <div className="category-content">
          <FiltersSidebar
            categories={['T·∫•t c·∫£', ...categories.map(c => c.name ?? c)]}
            selectedCategory={activeCategoryObj ? activeCategoryObj.name : (decodedCategory || 'T·∫•t c·∫£')}
            onCategoryChange={(cat) => handleCategoryChange(cat)}
            priceRange={priceRange}
            onPriceRangeChange={(r) => { setPriceRange(r); setCurrentPage(1); }}
            sortBy={sortBy}
            onSortChange={(s) => { setSortBy(s); setCurrentPage(1); }}
            onClearFilters={() => { setQuery(''); setPriceRange({ min: '', max: '' }); setSortBy('relevance'); setCurrentPage(1); }}
            showCategoryLinks
          />

          <div className="products-section">
            {loading ? (
              <div>ƒêang t·∫£i s·∫£n ph·∫©m...</div>
            ) : error ? (
              <div style={{ color: 'red' }}>{String(error)}</div>
            ) : filteredProducts.length === 0 ? (
              <EmptyState
                icon="üì¶"
                title="Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o"
                description="Hi·ªán t·∫°i ch∆∞a c√≥ s·∫£n ph·∫©m trong danh m·ª•c n√†y"
                actionText="Xem t·∫•t c·∫£ s·∫£n ph·∫©m"
                actionLink="/"
                className="no-results"
              />
            ) : (
              <>
                <ProductGrid products={paginatedProducts} />
                <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={(p) => setCurrentPage(p)} />
              </>
            )}
          </div>
        </div>
      </div>
    </Layout>
  );
}
