// components/ChatButton.jsx
import React, { useState, useEffect } from 'react';
import { useAuth } from '../utils/AuthContext';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default function ChatButton() {
  const { user } = useAuth();
  const [open, setOpen] = useState(false);
  const [conversations, setConversations] = useState([]);

  useEffect(() => {
    if (!user) return;

    const fetchConversations = async () => {
      try {
        const token = localStorage.getItem('access_token');
        const res = await axios.get('http://localhost:8000/api/chat/my-conversations/', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setConversations(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchConversations();
  }, [user]);

  if (!user) return null;

  return (
    <div style={{ position: 'fixed', bottom: 20, right: 20 }}>
      <button
        onClick={() => setOpen(!open)}
        style={{
          borderRadius: '50%',
          width: 60,
          height: 60,
          backgroundColor: '#2563eb',
          color: '#fff',
          fontSize: 24,
          border: 'none',
          cursor: 'pointer',
        }}
      >
        üí¨
      </button>

      {open && (
        <div
          style={{
            position: 'absolute',
            bottom: 70,
            right: 0,
            width: 300,
            maxHeight: 400,
            overflowY: 'auto',
            background: '#fff',
            border: '1px solid #ddd',
            borderRadius: 8,
            boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
          }}
        >
          <div style={{ padding: 8, fontWeight: 'bold', borderBottom: '1px solid #eee' }}>
            Tin nh·∫Øn
          </div>
          {conversations.map(conv => {
            const target =
              user.user_type === 'buyer' ? conv.shop : conv.buyer;

            return (
              <Link
                key={conv.id}
                to={`/shop-chat/${conv.shop.user_id}/${conv.buyer.user_id}`}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  padding: 8,
                  borderBottom: '1px solid #eee',
                  textDecoration: 'none',
                  color: '#000',
                }}
              >
                <img
                  src={target.avatar || '/default-avatar.png'}
                  alt={target.username}
                  style={{ width: 40, height: 40, borderRadius: '50%', marginRight: 8 }}
                />
                <div>{target.username}</div>
              </Link>
            );
          })}
          {conversations.length === 0 && (
            <div style={{ padding: 8, color: '#6b7280' }}>Kh√¥ng c√≥ tin nh·∫Øn n√†o</div>
          )}
        </div>
      )}
    </div>
  );
}
