import React, { useState, useEffect, useMemo } from 'react';
import { useAuth } from '../utils/AuthContext';
import { useNavigate } from 'react-router-dom';
import { useWishlist } from '../utils/WishlistContext';
import { useCart } from '../utils/CartContext';
import { 
  getCities, 
  getDistrictsByCity, 
  getWardsByDistrict,
  getCityName,
  getDistrictName,
  getWardName
} from '../utils/locationsData';
import { formatPrice } from '../utils/formatPrice';
import resolveAvatarUrl from '../utils/avatar';

import '../assets/UserProfile.css';
import '../assets/Wishlist.css';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:8000';

const Profile = () => {
  const { user, updateUser } = useAuth();
  const wishlistCtx = useWishlist();
  const { addToCart } = useCart();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('profile');
  const [isEditing, setIsEditing] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });

  const [formData, setFormData] = useState({
    full_name: '',
    phone: '',
    email: '',
    bio: '',
    address: '',
    city: '',
    district: '',
    ward: '',
    country: 'Vietnam',
  });

  const [cities, setCities] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [wards, setWards] = useState([]);
  const [orders, setOrders] = useState([]);
  const [ordersLoading, setOrdersLoading] = useState(false);
  const [ordersError, setOrdersError] = useState('');
  const {
    wishlist = [],
    savedItems = [],
    loading: wishlistLoading,
    error: wishlistError,
    isBuyer,
    removeFromWishlist,
    toggleWishlist,
    updateSavedQuantity,
    removeSavedItem,
  } = wishlistCtx || {};

  const avatarSrc = useMemo(() => resolveAvatarUrl(user, '/default-avatar.png'), [user]);

  useEffect(() => {
    if (user) {
      setFormData({
        full_name: user.full_name || '',
        phone: user.phone || '',
        email: user.email || '',
        bio: user.profile?.bio || '',
        address: user.profile?.address || '',
        city: user.profile?.city || '',
        district: user.profile?.district || '',
        ward: user.profile?.ward || '',
        country: user.profile?.country || 'Vietnam',
      });
    }

    // Load cities from mock data
    setCities(getCities());
  }, [user]);

  useEffect(() => {
    if (formData.city) {
      const districtsList = getDistrictsByCity(formData.city);
      setDistricts(districtsList);
      
      // Reset district and ward if city changes
      if (!districtsList.find(d => d.id === formData.district)) {
        setFormData(prev => ({ ...prev, district: '', ward: '' }));
      }
    } else {
      setDistricts([]);
      setWards([]);
    }
  }, [formData.city]);

  useEffect(() => {
    if (formData.district) {
      const wardsList = getWardsByDistrict(formData.district);
      setWards(wardsList);
      
      // Reset ward if district changes
      if (!wardsList.find(w => w.id === formData.ward)) {
        setFormData(prev => ({ ...prev, ward: '' }));
      }
    } else {
      setWards([]);
    }
  }, [formData.district]);

  useEffect(() => {
    if (activeTab === 'orders') {
      const token = localStorage.getItem('access_token');
      if (!token) {
        setOrders([]);
        setOrdersError('Báº¡n chÆ°a Ä‘Äƒng nháº­p.');
        return;
      }
      setOrdersLoading(true);
      setOrdersError('');
      fetch(`${API_BASE}/api/orders/mine/`, {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(async res => {
          const ct = res.headers.get('content-type') || '';
          if (!res.ok) throw new Error(`HTTP ${res.status}`);
          if (!ct.includes('application/json')) throw new Error('Pháº£n há»“i khÃ´ng pháº£i JSON');
          return res.json();
        })
        .then(data => {
          setOrders(Array.isArray(data.results) ? data.results : []);
        })
        .catch(err => setOrdersError(err.message))
        .finally(() => setOrdersLoading(false));
    }
  }, [activeTab]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setMessage({ type: '', text: '' });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      const updateData = {
        full_name: formData.full_name,
        phone: formData.phone,
        profile: {
          bio: formData.bio,
          address: formData.address,
          city: formData.city,
          district: formData.district,
          ward: formData.ward,
          country: formData.country,
        }
      };

      console.log('ðŸ“¤ Sending update data:', updateData);

      const response = await fetch(`${API_BASE}/api/users/profile/`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('access_token')}`
        },
        body: JSON.stringify(updateData)
      });

      console.log('ðŸ“¥ Response status:', response.status);

      const contentType = response.headers.get('content-type') || '';
      let result = {};
      if (contentType.includes('application/json')) {
        result = await response.json();
      } else {
        const text = await response.text();
        if (text?.trim()) {
          try {
            result = JSON.parse(text);
          } catch (err) {
            throw new Error(text);
          }
        }
      }
      console.log('ðŸ“¥ Response data:', result);

      if (!response.ok) {
        throw new Error(result.message || result.detail || 'Cáº­p nháº­t tháº¥t báº¡i');
      }

      if (result.user) {
        updateUser(result.user);
      } else if (user) {
        const updatedProfile =
          typeof result === 'object' && result !== null
            ? (result.profile && typeof result.profile === 'object'
                ? result.profile
                : result)
            : {};

        updateUser({
          ...user,
          full_name: updateData.full_name,
          phone: updateData.phone,
          profile: {
            ...user.profile,
            ...updateData.profile,
            ...updatedProfile,
          },
        });
      }
      
      setMessage({ 
        type: 'success', 
        text: result.message || 'Cáº­p nháº­t thÃ´ng tin thÃ nh cÃ´ng!' 
      });
      setIsEditing(false);
    } catch (error) {
      console.error('âŒ Update error:', error);
      setMessage({ 
        type: 'error', 
        text: error.message || 'Cáº­p nháº­t tháº¥t báº¡i. Vui lÃ²ng thá»­ láº¡i!' 
      });
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    if (user) {
      setFormData({
        full_name: user.full_name || '',
        phone: user.phone || '',
        email: user.email || '',
        bio: user.profile?.bio || '',
        address: user.profile?.address || '',
        city: user.profile?.city || '',
        district: user.profile?.district || '',
        ward: user.profile?.ward || '',
        country: user.profile?.country || 'Vietnam',
      });
    }
    setIsEditing(false);
    setMessage({ type: '', text: '' });
  };

  const statusMap = {
    pending: 'Chá» xá»­ lÃ½',
    paid: 'ÄÃ£ thanh toÃ¡n',
    shipping: 'Äang giao',
    completed: 'HoÃ n táº¥t',
    canceled: 'ÄÃ£ há»§y'
  };

  const renderProfileTab = () => (
    <div className="profile-form">
      {message.text && (
        <div className={`alert ${message.type === 'success' ? 'alert-success' : 'alert-error'}`}>
          {message.text}
        </div>
      )}

      <div className="form-section">
        <h3>áº¢nh Ä‘áº¡i diá»‡n</h3>
        <div style={{ display: 'flex', alignItems: 'center', gap: 16 }}>
          <img
            src={avatarSrc}
            alt={formData.full_name || user?.username || 'avatar'}
            style={{ width: 96, height: 96, borderRadius: '50%', objectFit: 'cover', border: '2px solid #eee' }}
          />
          <p style={{ color: '#6b7280', fontSize: 14 }}>
            áº¢nh Ä‘áº¡i diá»‡n Ä‘Æ°á»£c Ä‘á»“ng bá»™ á»Ÿ má»i nÆ¡i. Cáº­p nháº­t avatar táº¡i má»¥c thÃ´ng tin tÃ i khoáº£n trong tÆ°Æ¡ng lai.
          </p>
        </div>
      </div>

      <div className="form-section">
        <h3>ThÃ´ng tin cÃ¡ nhÃ¢n</h3>
        <div className="form-row">
          <div className="form-group">
            <label>Há» vÃ  tÃªn</label>
            {isEditing ? (
              <input
                type="text"
                name="full_name"
                value={formData.full_name}
                onChange={handleChange}
                placeholder="Nháº­p há» vÃ  tÃªn"
              />
            ) : (
              <p>{formData.full_name || 'ChÆ°a cáº­p nháº­t'}</p>
            )}
          </div>

          <div className="form-group">
            <label>Sá»‘ Ä‘iá»‡n thoáº¡i</label>
            {isEditing ? (
              <input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                placeholder="Nháº­p sá»‘ Ä‘iá»‡n thoáº¡i"
              />
            ) : (
              <p>{formData.phone || 'ChÆ°a cáº­p nháº­t'}</p>
            )}
          </div>
        </div>

        <div className="form-group">
          <label>Email</label>
          <p>{formData.email}</p>
        </div>

        <div className="form-group">
          <label>Giá»›i thiá»‡u</label>
          {isEditing ? (
            <textarea
              name="bio"
              value={formData.bio}
              onChange={handleChange}
              placeholder="Viáº¿t vÃ i dÃ²ng vá» báº£n thÃ¢n..."
              rows="4"
            />
          ) : (
            <p>{formData.bio || 'ChÆ°a cÃ³ giá»›i thiá»‡u'}</p>
          )}
        </div>
      </div>

      <div className="form-section">
        <h3>Äá»‹a chá»‰</h3>
        
        {isEditing ? (
          <>
            <div className="form-row">
              <div className="form-group">
                <label>Tá»‰nh/ThÃ nh phá»‘</label>
                <select
                  name="city"
                  value={formData.city}
                  onChange={handleChange}
                >
                  <option value="">Chá»n tá»‰nh/thÃ nh phá»‘</option>
                  {cities.map(city => (
                    <option key={city.id} value={city.id}>
                      {city.name}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Quáº­n/Huyá»‡n</label>
                <select
                  name="district"
                  value={formData.district}
                  onChange={handleChange}
                  disabled={!formData.city}
                >
                  <option value="">Chá»n quáº­n/huyá»‡n</option>
                  {districts.map(district => (
                    <option key={district.id} value={district.id}>
                      {district.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>PhÆ°á»ng/XÃ£</label>
                <select
                  name="ward"
                  value={formData.ward}
                  onChange={handleChange}
                  disabled={!formData.district}
                >
                  <option value="">Chá»n phÆ°á»ng/xÃ£</option>
                  {wards.map(ward => (
                    <option key={ward.id} value={ward.id}>
                      {ward.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="form-group">
              <label>Äá»‹a chá»‰ chi tiáº¿t</label>
              <input
                type="text"
                name="address"
                value={formData.address}
                onChange={handleChange}
                placeholder="Sá»‘ nhÃ , tÃªn Ä‘Æ°á»ng..."
              />
            </div>
          </>
        ) : (
          <>
            <div className="form-group">
              <label>Äá»‹a chá»‰ Ä‘áº§y Ä‘á»§</label>
              <p>
                {formData.address && formData.address}
                {formData.ward && `, ${getWardName(formData.ward)}`}
                {formData.district && `, ${getDistrictName(formData.district)}`}
                {formData.city && `, ${getCityName(formData.city)}`}
                {!formData.address && 'ChÆ°a cáº­p nháº­t'}
              </p>
            </div>
          </>
        )}
      </div>
    </div>
  );

  const renderOrdersTab = () => {
    if (ordersLoading) return <div className="loading-box">Äang táº£i Ä‘Æ¡n hÃ ng...</div>;
    if (ordersError) return <div className="alert alert-error">{ordersError}</div>;
    if (!orders.length) {
      return (
        <div className="empty-orders">
          <div className="empty-icon">ðŸ“¦</div>
            <h3>ChÆ°a cÃ³ Ä‘Æ¡n hÃ ng nÃ o</h3>
            <p>Báº¡n chÆ°a cÃ³ Ä‘Æ¡n hÃ ng. HÃ£y mua sáº¯m ngay!</p>
            <a href="/" className="browse-products-btn">KhÃ¡m phÃ¡ sáº£n pháº©m</a>
        </div>
      );
    }
    return (
      <div className="orders-list">
        {orders.map(o => {
          const createdDate = new Date(o.created_at);
          const itemsPreview = o.items?.slice(0, 3) || [];
          const remainingItems = Math.max((o.items?.length || 0) - itemsPreview.length, 0);
          const paymentLabel = o.payment_method === 'cod' ? 'Thanh toÃ¡n COD' : (o.payment_method || 'KhÃ¡c');

          return (
            <div key={o.order_id} className="order-card">
              <div className="order-card-header">
                <div className="order-code">
                  <span>MÃ£ Ä‘Æ¡n</span>
                  <p>#{o.order_id}</p>
                </div>
                <span className={`order-status-pill status-${o.status}`}>
                  {statusMap[o.status] || o.status}
                </span>
              </div>

              <div className="order-card-meta">
                <div className="meta-block">
                  <p>NgÃ y Ä‘áº·t</p>
                  <strong>
                    {createdDate.toLocaleDateString('vi-VN')}<br />
                    <span>{createdDate.toLocaleTimeString('vi-VN')}</span>
                  </strong>
                </div>
                <div className="meta-block">
                  <p>Thanh toÃ¡n</p>
                  <strong>{paymentLabel}</strong>
                </div>
                <div className="meta-block">
                  <p>Tá»•ng tiá»n</p>
                  <strong>{formatPrice(o.total_amount)}</strong>
                </div>
              </div>

              <div className="order-items-preview">
                {itemsPreview.map((it, idx) => (
                  <div key={idx} className="order-item-chip">
                    <div>
                      <p>{it.product}</p>
                      <span>SL {it.quantity}</span>
                    </div>
                    <span>{formatPrice((it.price || 0) * (it.quantity || 0))}</span>
                  </div>
                ))}
                {remainingItems > 0 && (
                  <div className="order-items-more">
                    +{remainingItems} sáº£n pháº©m khÃ¡c
                  </div>
                )}
              </div>

              <div className="order-card-footer">
                <div className="order-destination">
                  <span>Giao Ä‘áº¿n</span>
                  <p>{o.shipping_address || o.delivery_address || 'Äang cáº­p nháº­t'}</p>
                </div>
                <button
                  onClick={() => navigate(`/orders/${o.order_id}`)}
                  className="order-detail-btn"
                >
                  Xem chi tiáº¿t
                </button>
              </div>
            </div>
          );
        })}
      </div>
    );
  };

  const moveWishlistItemToCart = async (item) => {
    addToCart(item.product, 1, { color: item.color, size: item.size });
    await removeFromWishlist(item.id);
  };

  const moveSavedItemToCart = async (item) => {
    addToCart(item.product, item.quantity || 1, { color: item.color, size: item.size });
    await removeSavedItem(item.id);
  };

  const renderWishlistTab = () => {
    if (!isBuyer) {
      return (
        <div className="alert alert-error">Chá»©c nÄƒng chá»‰ kháº£ dá»¥ng cho tÃ i khoáº£n ngÆ°á»i mua.</div>
      );
    }

    return (
      <div className="wishlist-tab">
        {wishlistLoading && <div className="wishlist-card">Äang táº£i danh sÃ¡ch...</div>}
        {wishlistError && (
          <div className="alert alert-error">{wishlistError.message || 'KhÃ´ng táº£i Ä‘Æ°á»£c dá»¯ liá»‡u.'}</div>
        )}

        <section className="wishlist-section">
          <div className="wishlist-section-title">
            <h3>Danh sÃ¡ch yÃªu thÃ­ch</h3>
            <span>{wishlist.length} sáº£n pháº©m</span>
          </div>
          {wishlist.length === 0 ? (
            <div className="wishlist-card empty">
              <p>Báº¡n chÆ°a thÃªm sáº£n pháº©m nÃ o. HÃ£y nháº¥n â¤ trÃªn sáº£n pháº©m Ä‘á»ƒ lÆ°u láº¡i.</p>
            </div>
          ) : (
            <div className="wishlist-grid">
              {wishlist.map((item) => (
                <article key={item.id} className="wishlist-card">
                  <div className="wishlist-thumb">
                    <img src={item.product?.image || '/default-product.png'} alt={item.product?.name} />
                  </div>
                  <div className="wishlist-info">
                    <h4>{item.product?.name}</h4>
                    {item.color && <p>MÃ u: {item.color}</p>}
                    {item.size && <p>Size: {item.size}</p>}
                    <p className="wishlist-price">{formatPrice(item.product?.price || 0)}</p>
                  </div>
                  <div className="wishlist-actions">
                    <button type="button" onClick={() => moveWishlistItemToCart(item)}>ThÃªm vÃ o giá»</button>
                    <button
                      type="button"
                      className="ghost"
                      onClick={() => toggleWishlist(item.product, { color: item.color, size: item.size })}
                    >
                      XÃ³a
                    </button>
                  </div>
                </article>
              ))}
            </div>
          )}
        </section>

        <section className="wishlist-section">
          <div className="wishlist-section-title">
            <h3>LÆ°u Ä‘á»ƒ mua sau</h3>
            <span>{savedItems.length} sáº£n pháº©m</span>
          </div>
          {savedItems.length === 0 ? (
            <div className="wishlist-card empty">
              <p>ChÆ°a cÃ³ sáº£n pháº©m nÃ o Ä‘Æ°á»£c lÆ°u. Báº¡n cÃ³ thá»ƒ lÆ°u tá»« giá» hÃ ng.</p>
            </div>
          ) : (
            <div className="wishlist-grid saved">
              {savedItems.map((item) => (
                <article key={item.id} className="wishlist-card">
                  <div className="wishlist-thumb">
                    <img src={item.product?.image || '/default-product.png'} alt={item.product?.name} />
                  </div>
                  <div className="wishlist-info">
                    <h4>{item.product?.name}</h4>
                    {item.color && <p>MÃ u: {item.color}</p>}
                    {item.size && <p>Size: {item.size}</p>}
                    <p className="wishlist-price">{formatPrice(item.product?.price || 0)}</p>
                    <label className="saved-qty">
                      Sá»‘ lÆ°á»£ng:
                      <input
                        type="number"
                        min="1"
                        value={item.quantity || 1}
                        onChange={(e) => updateSavedQuantity(item.id, Number(e.target.value) || 1)}
                      />
                    </label>
                  </div>
                  <div className="wishlist-actions">
                    <button type="button" onClick={() => moveSavedItemToCart(item)}>ÄÆ°a láº¡i vÃ o giá»</button>
                    <button type="button" className="ghost" onClick={() => removeSavedItem(item.id)}>
                      XÃ³a
                    </button>
                  </div>
                </article>
              ))}
            </div>
          )}
        </section>
      </div>
    );
  };

  return (
    <div className="user-profile-page">
