import React, { useState, useEffect } from 'react';
import { useAuth } from '../utils/AuthContext';
import { useNavigate } from 'react-router-dom';
import { 
  getCities, 
  getDistrictsByCity, 
  getWardsByDistrict,
  getCityName,
  getDistrictName,
  getWardName
} from '../utils/locationsData';
import '../assets/UserProfile.css';

const Profile = () => {
  const { user, updateUser } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('profile');
  const [isEditing, setIsEditing] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });

  const [formData, setFormData] = useState({
    full_name: '',
    phone: '',
    email: '',
    bio: '',
    address: '',
    city: '',
    district: '',
    ward: '',
    postal_code: '',
    country: 'Vietnam',
  });

  const [cities, setCities] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [wards, setWards] = useState([]);

  useEffect(() => {
    if (user) {
      setFormData({
        full_name: user.full_name || '',
        phone: user.phone || '',
        email: user.email || '',
        bio: user.profile?.bio || '',
        address: user.profile?.address || '',
        city: user.profile?.city || '',
        district: user.profile?.district || '',
        ward: user.profile?.ward || '',
        postal_code: user.profile?.postal_code || '',
        country: user.profile?.country || 'Vietnam',
      });
    }

    // Load cities from mock data
    setCities(getCities());
  }, [user]);

  useEffect(() => {
    if (formData.city) {
      const districtsList = getDistrictsByCity(formData.city);
      setDistricts(districtsList);
      
      // Reset district and ward if city changes
      if (!districtsList.find(d => d.id === formData.district)) {
        setFormData(prev => ({ ...prev, district: '', ward: '' }));
      }
    } else {
      setDistricts([]);
      setWards([]);
    }
  }, [formData.city]);

  useEffect(() => {
    if (formData.district) {
      const wardsList = getWardsByDistrict(formData.district);
      setWards(wardsList);
      
      // Reset ward if district changes
      if (!wardsList.find(w => w.id === formData.ward)) {
        setFormData(prev => ({ ...prev, ward: '' }));
      }
    } else {
      setWards([]);
    }
  }, [formData.district]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setMessage({ type: '', text: '' });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      const updateData = {
        full_name: formData.full_name,
        phone: formData.phone,
        profile: {
          bio: formData.bio,
          address: formData.address,
          city: formData.city,
          country: formData.country,
          postal_code: formData.postal_code,
        }
      };

      console.log('üì§ Sending update data:', updateData);

      const response = await fetch('http://localhost:8000/api/users/profile/update/', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('access_token')}`
        },
        body: JSON.stringify(updateData)
      });

      console.log('üì• Response status:', response.status);

      const result = await response.json();
      console.log('üì• Response data:', result);

      if (!response.ok || !result.success) {
        throw new Error(result.message || 'C·∫≠p nh·∫≠t th·∫•t b·∫°i');
      }

      // C·∫≠p nh·∫≠t user context v·ªõi th√¥ng tin m·ªõi
      updateUser(result.user);
      
      setMessage({ 
        type: 'success', 
        text: 'C·∫≠p nh·∫≠t th√¥ng tin th√†nh c√¥ng!' 
      });
      setIsEditing(false);
    } catch (error) {
      console.error('‚ùå Update error:', error);
      setMessage({ 
        type: 'error', 
        text: error.message || 'C·∫≠p nh·∫≠t th·∫•t b·∫°i. Vui l√≤ng th·ª≠ l·∫°i!' 
      });
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    if (user) {
      setFormData({
        full_name: user.full_name || '',
        phone: user.phone || '',
        email: user.email || '',
        bio: user.profile?.bio || '',
        address: user.profile?.address || '',
        city: user.profile?.city || '',
        district: user.profile?.district || '',
        ward: user.profile?.ward || '',
        postal_code: user.profile?.postal_code || '',
        country: user.profile?.country || 'Vietnam',
      });
    }
    setIsEditing(false);
    setMessage({ type: '', text: '' });
  };

  const renderProfileTab = () => (
    <div className="profile-form">
      {message.text && (
        <div className={`alert ${message.type === 'success' ? 'alert-success' : 'alert-error'}`}>
          {message.text}
        </div>
      )}

      <div className="form-section">
        <h3>Th√¥ng tin c√° nh√¢n</h3>
        <div className="form-row">
          <div className="form-group">
            <label>H·ªç v√† t√™n</label>
            {isEditing ? (
              <input
                type="text"
                name="full_name"
                value={formData.full_name}
                onChange={handleChange}
                placeholder="Nh·∫≠p h·ªç v√† t√™n"
              />
            ) : (
              <p>{formData.full_name || 'Ch∆∞a c·∫≠p nh·∫≠t'}</p>
            )}
          </div>

          <div className="form-group">
            <label>S·ªë ƒëi·ªán tho·∫°i</label>
            {isEditing ? (
              <input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                placeholder="Nh·∫≠p s·ªë ƒëi·ªán tho·∫°i"
              />
            ) : (
              <p>{formData.phone || 'Ch∆∞a c·∫≠p nh·∫≠t'}</p>
            )}
          </div>
        </div>

        <div className="form-group">
          <label>Email</label>
          <p>{formData.email}</p>
        </div>

        <div className="form-group">
          <label>Gi·ªõi thi·ªáu</label>
          {isEditing ? (
            <textarea
              name="bio"
              value={formData.bio}
              onChange={handleChange}
              placeholder="Vi·∫øt v√†i d√≤ng v·ªÅ b·∫£n th√¢n..."
              rows="4"
            />
          ) : (
            <p>{formData.bio || 'Ch∆∞a c√≥ gi·ªõi thi·ªáu'}</p>
          )}
        </div>
      </div>

      <div className="form-section">
        <h3>ƒê·ªãa ch·ªâ</h3>
        
        {isEditing ? (
          <>
            <div className="form-row">
              <div className="form-group">
                <label>T·ªânh/Th√†nh ph·ªë</label>
                <select
                  name="city"
                  value={formData.city}
                  onChange={handleChange}
                >
                  <option value="">Ch·ªçn t·ªânh/th√†nh ph·ªë</option>
                  {cities.map(city => (
                    <option key={city.id} value={city.id}>
                      {city.name}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Qu·∫≠n/Huy·ªán</label>
                <select
                  name="district"
                  value={formData.district}
                  onChange={handleChange}
                  disabled={!formData.city}
                >
                  <option value="">Ch·ªçn qu·∫≠n/huy·ªán</option>
                  {districts.map(district => (
                    <option key={district.id} value={district.id}>
                      {district.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Ph∆∞·ªùng/X√£</label>
                <select
                  name="ward"
                  value={formData.ward}
                  onChange={handleChange}
                  disabled={!formData.district}
                >
                  <option value="">Ch·ªçn ph∆∞·ªùng/x√£</option>
                  {wards.map(ward => (
                    <option key={ward.id} value={ward.id}>
                      {ward.name}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>M√£ b∆∞u ƒëi·ªán</label>
                <input
                  type="text"
                  name="postal_code"
                  value={formData.postal_code}
                  onChange={handleChange}
                  placeholder="Nh·∫≠p m√£ b∆∞u ƒëi·ªán"
                />
              </div>
            </div>

            <div className="form-group">
              <label>ƒê·ªãa ch·ªâ chi ti·∫øt</label>
              <input
                type="text"
                name="address"
                value={formData.address}
                onChange={handleChange}
                placeholder="S·ªë nh√†, t√™n ƒë∆∞·ªùng..."
              />
            </div>
          </>
        ) : (
          <>
            <div className="form-group">
              <label>ƒê·ªãa ch·ªâ ƒë·∫ßy ƒë·ªß</label>
              <p>
                {formData.address && formData.address}
                {formData.ward && `, ${getWardName(formData.ward)}`}
                {formData.district && `, ${getDistrictName(formData.district)}`}
                {formData.city && `, ${getCityName(formData.city)}`}
                {!formData.address && 'Ch∆∞a c·∫≠p nh·∫≠t'}
              </p>
            </div>
          </>
        )}
      </div>
    </div>
  );

  const renderOrdersTab = () => (
    <div className="empty-orders">
      <div className="empty-icon">üì¶</div>
      <h3>Ch∆∞a c√≥ ƒë∆°n h√†ng n√†o</h3>
      <p>B·∫°n ch∆∞a c√≥ ƒë∆°n h√†ng n√†o. H√£y b·∫Øt ƒë·∫ßu mua s·∫Øm ngay!</p>
      <a href="/" className="browse-products-btn">
        Kh√°m ph√° s·∫£n ph·∫©m
      </a>
    </div>
  );

  const renderWishlistTab = () => (
    <div className="empty-wishlist">
      <div className="empty-icon">‚ù§Ô∏è</div>
      <h3>Danh s√°ch y√™u th√≠ch tr·ªëng</h3>
      <p>B·∫°n ch∆∞a th√™m s·∫£n ph·∫©m n√†o v√†o danh s√°ch y√™u th√≠ch.</p>
      <a href="/" className="browse-products-btn">
        Kh√°m ph√° s·∫£n ph·∫©m
      </a>
    </div>
  );

  return (
    <div className="user-profile-page">
      <div className="profile-container">
        <aside className="profile-sidebar">
          <div className="user-info">
            <div className="avatar">
              {user?.avatar ? (
                <img src={user.avatar} alt={user.full_name} />
              ) : (
                <div className="avatar-placeholder">
                  {user?.full_name?.charAt(0) || user?.username?.charAt(0) || '?'}
                </div>
              )}
            </div>
            <h3>{user?.full_name || user?.username}</h3>
            <p>{user?.email}</p>
            <p className="member-since">
              Th√†nh vi√™n t·ª´ {new Date(user?.date_joined).toLocaleDateString('vi-VN')}
            </p>
          </div>

          <nav className="profile-nav">
            <button
              className={`nav-item ${activeTab === 'profile' ? 'active' : ''}`}
              onClick={() => setActiveTab('profile')}
            >
              Th√¥ng tin c√° nh√¢n
            </button>
            <button
              className={`nav-item ${activeTab === 'orders' ? 'active' : ''}`}
              onClick={() => setActiveTab('orders')}
            >
              ƒê∆°n h√†ng c·ªßa t√¥i
            </button>
            <button
              className={`nav-item ${activeTab === 'wishlist' ? 'active' : ''}`}
              onClick={() => setActiveTab('wishlist')}
            >
              Danh s√°ch y√™u th√≠ch
            </button>
            <button
              className="nav-item"
              onClick={() => navigate('/change-password')}
            >
              ƒê·ªïi m·∫≠t kh·∫©u
            </button>
          </nav>
        </aside>

        <main className="profile-content">
          <div className="tab-header">
            <h2>
              {activeTab === 'profile' && 'Th√¥ng tin c√° nh√¢n'}
              {activeTab === 'orders' && 'ƒê∆°n h√†ng c·ªßa t√¥i'}
              {activeTab === 'wishlist' && 'Danh s√°ch y√™u th√≠ch'}
            </h2>
            
            {activeTab === 'profile' && (
              <div className="edit-actions">
                {isEditing ? (
                  <>
                    <button 
                      className="save-btn"
                      onClick={handleSubmit}
                      disabled={loading}
                    >
                      {loading ? 'ƒêang l∆∞u...' : 'L∆∞u thay ƒë·ªïi'}
                    </button>
                    <button 
                      className="cancel-btn"
                      onClick={handleCancel}
                      disabled={loading}
                    >
                      H·ªßy
                    </button>
                  </>
                ) : (
                  <button 
                    className="edit-btn"
                    onClick={() => setIsEditing(true)}
                  >
                    Ch·ªânh s·ª≠a
                  </button>
                )}
              </div>
            )}
          </div>

          {activeTab === 'profile' && renderProfileTab()}
          {activeTab === 'orders' && renderOrdersTab()}
          {activeTab === 'wishlist' && renderWishlistTab()}
        </main>
      </div>
    </div>
  );
};

export default Profile;
