import React, { useEffect, useRef, useState } from 'react';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:8000';

export default function ChatPopup({ shopId, buyerId, onClose }) {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');
  const wsRef = useRef(null);
  const bottomRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem('access_token');
    const wsProtocol = API_BASE.startsWith('https') ? 'wss' : 'ws';
    const raw = API_BASE.replace(/^https?:\/\//, '');
    const qs = new URLSearchParams({ token, buyer: buyerId });
    const ws = new WebSocket(`${wsProtocol}://${raw}/ws/chat/${shopId}/?${qs.toString()}`);
    wsRef.current = ws;

    ws.onopen = () => console.log('WS connected');
    ws.onmessage = (evt) => {
      try {
        const data = JSON.parse(evt.data);
        if (data.type === 'history') setMessages(data.messages || []);
        else if (data.type === 'message' && data.message) setMessages(prev => [...prev, data.message]);
      } catch {}
    };
    ws.onerror = (e) => console.error('WS error', e);
    ws.onclose = () => console.log('WS closed');

    return () => ws.close();
  }, [shopId, buyerId]);

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = () => {
    const content = text.trim();
    if (!content || !wsRef.current || wsRef.current.readyState !== 1) return;
    wsRef.current.send(JSON.stringify({ type: 'message', content }));
    setText('');
  };

  return (
    <div
      style={{
        position: 'fixed',
        bottom: 90,
        right: 20,
        width: 300,
        maxHeight: 400,
        background: '#fff',
        border: '1px solid #ddd',
        borderRadius: 8,
        display: 'flex',
        flexDirection: 'column',
        boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
        zIndex: 1000,
      }}
    >
      {/* Header */}
      <div style={{ padding: 10, borderBottom: '1px solid #eee', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <strong>Chat với Shop #{shopId}</strong>
        <button onClick={onClose} style={{ border: 'none', background: 'transparent', cursor: 'pointer' }}>✖</button>
      </div>

      {/* Messages */}
      <div style={{ flex: 1, padding: 10, overflowY: 'auto' }}>
        {messages.map(m => (
          <div key={m.id} style={{ marginBottom: 8, textAlign: m.sender_id === buyerId ? 'right' : 'left' }}>
            <div style={{
              display: 'inline-block',
              padding: '6px 12px',
              borderRadius: 12,
              background: m.sender_id === buyerId ? '#DCFCE7' : '#F3F4F6',
              maxWidth: '80%',
              wordWrap: 'break-word'
            }}>
              {m.content}
            </div>
          </div>
        ))}
        <div ref={bottomRef} />
      </div>

      {/* Input */}
      <div style={{ display: 'flex', borderTop: '1px solid #eee' }}>
        <input
          style={{ flex: 1, border: 'none', padding: '8px 10px' }}
          value={text}
          onChange={e => setText(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && sendMessage()}
          placeholder="Nhập tin nhắn..."
        />
        <button onClick={sendMessage} style={{ padding: '0 12px', border: 'none', background: '#2563eb', color: '#fff' }}>Gửi</button>
      </div>
    </div>
  );
}
