import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../utils/AuthContext';
import { Link, useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../assets/SellerProducts.css';

const SellerProducts = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  // Filters
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all'); // all, active, inactive
  const [sortBy, setSortBy] = useState('newest'); // newest, oldest, price-high, price-low, name
  const [viewMode, setViewMode] = useState('grid'); // grid, list
  
  // Pagination
  const [currentPage, setCurrentPage] = useState(1);
  const productsPerPage = 12;

  // Stats
  const [stats, setStats] = useState({
    total: 0,
    active: 0,
    inactive: 0,
    outOfStock: 0
  });

  useEffect(() => {
    if (user && user.user_type !== 'seller') {
      navigate('/dashboard');
    } else if (user) {
      fetchProducts();
    }
  }, [user, navigate]);

  const filterAndSortProducts = useCallback(() => {
    let filtered = [...products];

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description?.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Status filter
    if (statusFilter === 'active') {
      filtered = filtered.filter(p => p.is_active);
    } else if (statusFilter === 'inactive') {
      filtered = filtered.filter(p => !p.is_active);
    }

    // Sort
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'newest':
          return new Date(b.created_at) - new Date(a.created_at);
        case 'oldest':
          return new Date(a.created_at) - new Date(b.created_at);
        case 'price-high':
          return b.price - a.price;
        case 'price-low':
          return a.price - b.price;
        case 'name':
          return a.name.localeCompare(b.name);
        default:
          return 0;
      }
    });

    setFilteredProducts(filtered);
    setCurrentPage(1); // Reset to first page when filters change
  }, [products, searchTerm, statusFilter, sortBy]);

  useEffect(() => {
    filterAndSortProducts();
  }, [filterAndSortProducts]);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem('access_token');
      
      const response = await fetch('http://localhost:8000/api/seller/products/', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (!response.ok) throw new Error('Failed to fetch products');
      
      const data = await response.json();
      setProducts(data);
      
      // Calculate stats
      const newStats = {
        total: data.length,
        active: data.filter(p => p.is_active).length,
        inactive: data.filter(p => !p.is_active).length,
        outOfStock: data.filter(p => p.stock === 0).length
      };
      setStats(newStats);
      
    } catch (err) {
      console.error('Error fetching products:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleToggleStatus = async (productId, currentStatus) => {
    if (!window.confirm(`Báº¡n cÃ³ cháº¯c muá»‘n ${currentStatus ? 'áº©n' : 'hiá»‡n'} sáº£n pháº©m nÃ y?`)) {
      return;
    }

    try {
      const token = localStorage.getItem('access_token');
      const response = await fetch(`http://localhost:8000/api/products/${productId}/toggle/`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ is_active: !currentStatus }),
      });

      if (!response.ok) throw new Error('Failed to update product');

      const updatedProduct = await response.json();
      
      // Update local state
      setProducts(products.map(p => 
        p.id === productId ? updatedProduct : p
      ));
      
      alert(`ÄÃ£ ${!currentStatus ? 'hiá»‡n' : 'áº©n'} sáº£n pháº©m thÃ nh cÃ´ng!`);
    } catch (err) {
      alert('Lá»—i khi cáº­p nháº­t sáº£n pháº©m: ' + err.message);
    }
  };

  const handleDeleteProduct = async (productId, productName) => {
    if (!window.confirm(`Báº¡n cÃ³ cháº¯c muá»‘n XÃ“A sáº£n pháº©m "${productName}"?\nHÃ nh Ä‘á»™ng nÃ y khÃ´ng thá»ƒ hoÃ n tÃ¡c!`)) {
      return;
    }

    try {
      const token = localStorage.getItem('access_token');
      const response = await fetch(`http://localhost:8000/api/products/${productId}/`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (!response.ok) throw new Error('Failed to delete product');

      // Remove from local state
      setProducts(products.filter(p => p.id !== productId));
      alert('ÄÃ£ xÃ³a sáº£n pháº©m thÃ nh cÃ´ng!');
    } catch (err) {
      alert('Lá»—i khi xÃ³a sáº£n pháº©m: ' + err.message);
    }
  };

  // Pagination
  const indexOfLastProduct = currentPage * productsPerPage;
  const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
  const currentProducts = filteredProducts.slice(indexOfFirstProduct, indexOfLastProduct);
  const totalPages = Math.ceil(filteredProducts.length / productsPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  if (loading) {
    return (
      <>
        <Header />
        <div className="seller-products-loading">
          <div className="spinner"></div>
          <p>Äang táº£i sáº£n pháº©m...</p>
        </div>
        <Footer />
      </>
    );
  }

  return (
    <>
      
      <div className="seller-products-page">
        <div className="seller-products-container">
          
          {/* Page Header */}
          <div className="page-header">
            <div className="header-left">
              <Link to="/seller/dashboard" className="back-btn">
                â† Dashboard
              </Link>
              <div>
                <h1>ğŸ“¦ Quáº£n lÃ½ sáº£n pháº©m</h1>
                <p>Quáº£n lÃ½ táº¥t cáº£ sáº£n pháº©m cá»§a báº¡n</p>
              </div>
            </div>
            <Link to="/seller/products/add" className="btn btn-primary" style={{ width: "230px" }}>
              â• ThÃªm sáº£n pháº©m má»›i
            </Link>
          </div>

          {/* Stats Cards */}
          <div className="stats-row">
            <div className="stat-card">
              <div className="stat-icon">ğŸ“Š</div>
              <div className="stat-info">
                <h3>{stats.total}</h3>
                <p>Tá»•ng sáº£n pháº©m</p>
              </div>
            </div>
            <div className="stat-card active">
              <div className="stat-icon">âœ…</div>
              <div className="stat-info">
                <h3>{stats.active}</h3>
                <p>Äang hoáº¡t Ä‘á»™ng</p>
              </div>
            </div>
            <div className="stat-card inactive">
              <div className="stat-icon">âŒ</div>
              <div className="stat-info">
                <h3>{stats.inactive}</h3>
                <p>ÄÃ£ áº©n</p>
              </div>
            </div>
            <div className="stat-card warning">
              <div className="stat-icon">âš ï¸</div>
              <div className="stat-info">
                <h3>{stats.outOfStock}</h3>
                <p>Háº¿t hÃ ng</p>
              </div>
            </div>
          </div>

          {/* Filters & Actions */}
          <div className="filters-section">
            <div className="filters-row">
              {/* Search */}
              <div className="search-box">
                <span className="search-icon">ğŸ”</span>
                <input
                  type="text"
                  placeholder="TÃ¬m kiáº¿m sáº£n pháº©m..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                {searchTerm && (
                  <button 
                    className="clear-search"
                    onClick={() => setSearchTerm('')}
                  >
                    Ã—
                  </button>
                )}
              </div>

              {/* Status Filter */}
              <select 
                className="filter-select"
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
              >
                <option value="all">Táº¥t cáº£ tráº¡ng thÃ¡i</option>
                <option value="active">Äang hoáº¡t Ä‘á»™ng</option>
                <option value="inactive">ÄÃ£ áº©n</option>
              </select>

              {/* Sort */}
              <select 
                className="filter-select"
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
              >
                <option value="newest">Má»›i nháº¥t</option>
                <option value="oldest">CÅ© nháº¥t</option>
                <option value="price-high">GiÃ¡ cao â†’ tháº¥p</option>
                <option value="price-low">GiÃ¡ tháº¥p â†’ cao</option>
                <option value="name">TÃªn A â†’ Z</option>
              </select>

              {/* View Mode */}
              <div className="view-mode-toggle">
                <button 
                  className={`view-btn ${viewMode === 'grid' ? 'active' : ''}`}
                  onClick={() => setViewMode('grid')}
                  title="Xem dáº¡ng lÆ°á»›i"
                >
                  â–¦
                </button>
                <button 
                  className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
                  onClick={() => setViewMode('list')}
                  title="Xem dáº¡ng danh sÃ¡ch"
                >
                  â˜°
                </button>
              </div>
            </div>

            {/* Results Info */}
            <div className="results-info">
              <p>
                Hiá»ƒn thá»‹ <strong>{indexOfFirstProduct + 1}-{Math.min(indexOfLastProduct, filteredProducts.length)}</strong> 
                {' '}trong <strong>{filteredProducts.length}</strong> sáº£n pháº©m
              </p>
            </div>
          </div>

          {/* Error Message */}
          {error && (
            <div className="error-message">
              âŒ Lá»—i: {error}
              <button onClick={fetchProducts}>Thá»­ láº¡i</button>
            </div>
          )}

          {/* Products Grid/List */}
          {filteredProducts.length === 0 ? (
            <div className="empty-state">
              <div className="empty-icon">ğŸ“¦</div>
              <h2>KhÃ´ng tÃ¬m tháº¥y sáº£n pháº©m</h2>
              <p>
                {searchTerm || statusFilter !== 'all' 
                  ? 'Thá»­ thay Ä‘á»•i bá»™ lá»c hoáº·c tÃ¬m kiáº¿m' 
                  : 'Báº¡n chÆ°a cÃ³ sáº£n pháº©m nÃ o'}
              </p>
              {!searchTerm && statusFilter === 'all' && (
                <Link to="/seller/products/add" className="btn btn-primary">
                  â• ThÃªm sáº£n pháº©m Ä‘áº§u tiÃªn
                </Link>
              )}
            </div>
          ) : (
            <>
              <div className={`products-container ${viewMode}`}>
                {currentProducts.map((product) => (
                  <div key={product.id} className="product-card">
                    
                    {/* Product Image */}
                    <div className="product-image-wrapper">
                      <img 
                        src={product.image || '/placeholder.png'} 
                        alt={product.name}
                        className="product-image"
                      />
                      <div className="product-badges">
                        {product.stock === 0 && (
                          <span className="badge badge-danger">Háº¿t hÃ ng</span>
                        )}
                        {!product.is_active && (
                          <span className="badge badge-secondary">ÄÃ£ áº©n</span>
                        )}
                        {product.is_active && product.stock > 0 && (
                          <span className="badge badge-success">Äang bÃ¡n</span>
                        )}
                      </div>
                    </div>

                    {/* Product Info */}
                    <div className="product-info">
                      <h3 className="product-name">{product.name}</h3>
                      
                      <p className="product-description">
                        {product.description?.substring(0, 100)}
                        {product.description?.length > 100 ? '...' : ''}
                      </p>

                      <div className="product-meta">
                        <div className="meta-item">
                          <span className="label">GiÃ¡:</span>
                          <span className="value price">${product.price}</span>
                        </div>
                        <div className="meta-item">
                          <span className="label">Kho:</span>
                          <span className={`value stock ${product.stock === 0 ? 'out-of-stock' : ''}`}>
                            {product.stock}
                          </span>
                        </div>
                        <div className="meta-item">
                          <span className="label">Danh má»¥c:</span>
                          <span className="value">{product.category_name || 'N/A'}</span>
                        </div>
                        <div className="meta-item">
                          <span className="label">Táº¡o lÃºc:</span>
                          <span className="value">{new Date(product.created_at).toLocaleDateString('vi-VN')}</span>
                        </div>
                      </div>
                    </div>

                    {/* Product Actions */}
                    <div className="product-actions">
                      <Link 
                        to={`/products/${product.id}`}
                        className="action-btn view"
                        title="Xem chi tiáº¿t"
                      >
                        ğŸ‘ï¸ Xem
                      </Link>
                      <Link 
                        to={`/seller/products/edit/${product.id}`}
                        className="action-btn edit"
                        title="Chá»‰nh sá»­a"
                      >
                        âœï¸ Sá»­a
                      </Link>
                      <button 
                        className={`action-btn toggle ${product.is_active ? 'hide' : 'show'}`}
                        onClick={() => handleToggleStatus(product.id, product.is_active)}
                        title={product.is_active ? 'áº¨n sáº£n pháº©m' : 'Hiá»‡n sáº£n pháº©m'}
                      >
                        {product.is_active ? 'ğŸš« áº¨n' : 'âœ… Hiá»‡n'}
                      </button>
                      <button 
                        className="action-btn delete"
                        onClick={() => handleDeleteProduct(product.id, product.name)}
                        title="XÃ³a sáº£n pháº©m"
                      >
                        ğŸ—‘ï¸ XÃ³a
                      </button>
                    </div>

                  </div>
                ))}
              </div>

              {/* Pagination */}
              {totalPages > 1 && (
                <div className="pagination">
                  <button 
                    className="page-btn"
                    onClick={() => paginate(currentPage - 1)}
                    disabled={currentPage === 1}
                  >
                    â† TrÆ°á»›c
                  </button>
                  
                  <div className="page-numbers">
                    {[...Array(totalPages)].map((_, index) => {
                      const pageNumber = index + 1;
                      // Show first, last, current, and adjacent pages
                      if (
                        pageNumber === 1 ||
                        pageNumber === totalPages ||
                        (pageNumber >= currentPage - 1 && pageNumber <= currentPage + 1)
                      ) {
                        return (
                          <button
                            key={pageNumber}
                            className={`page-number ${currentPage === pageNumber ? 'active' : ''}`}
                            onClick={() => paginate(pageNumber)}
                          >
                            {pageNumber}
                          </button>
                        );
                      } else if (
                        pageNumber === currentPage - 2 ||
                        pageNumber === currentPage + 2
                      ) {
                        return <span key={pageNumber} className="page-dots">...</span>;
                      }
                      return null;
                    })}
                  </div>

                  <button 
                    className="page-btn"
                    onClick={() => paginate(currentPage + 1)}
                    disabled={currentPage === totalPages}
                  >
                    Sau â†’
                  </button>
                </div>
              )}
            </>
          )}

        </div>
      </div>

      <Footer />
    </>
  );
};

export default SellerProducts;
