// App.jsx
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './utils/AuthContext';
import { CartProvider } from './utils/CartContext';
import PrivateRoute from './components/PrivateRoute';
import Header from './components/Header';
import ChatButton from './components/ChatButton';

// Pages
import Home from './pages/Home';
import CategoryPage from './pages/CategoryPage.jsx';
import ProductDetail from './pages/ProductDetail';
import Cart from './pages/Cart';
import Checkout from './pages/Checkout';
import OrderSuccess from './pages/OrderSuccess';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import AdminDashboard from './pages/AdminDashboard';
import SellerDashboard from './pages/SellerDashboard';
import Profile from './pages/Profile';
import ChangePassword from './pages/ChangePassword';
import UserList from './pages/UserList';
import AddProduct from './pages/AddProduct';
import AddCategory from './pages/AddCategory';
import Chat from './pages/Chat';
import ShopChat from './pages/ShopChat';

// ----- AppContent tách riêng để dùng useAuth() -----
function AppContent() {
  const { user } = useAuth();

  // Ví dụ shop list, có thể fetch từ API
  const shops = [
    { id: 2, name: 'Shop B' },
    { id: 4, name: 'Shop C' },
  ];

  return (
    <div className="App">
      <Header />

      <Routes>
        <Route index element={<Home />} />
        <Route path="/" element={<Home />} />
        <Route path="/category/:slug" element={<CategoryPage />} />
        <Route path="/product/:id" element={<ProductDetail />} />
        <Route path="/cart" element={<Cart />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* Buyer */}
        <Route
          path="/dashboard"
          element={
            <PrivateRoute allowedRoles={['buyer']}>
              <Dashboard />
            </PrivateRoute>
          }
        />

        {/* Seller */}
        <Route
          path="/seller/dashboard"
          element={
            <PrivateRoute allowedRoles={['seller']}>
              <SellerDashboard />
            </PrivateRoute>
          }
        />
        <Route
          path="/seller/products/new"
          element={
            <PrivateRoute allowedRoles={['seller']}>
              <AddProduct />
            </PrivateRoute>
          }
        />

        {/* Admin */}
        <Route
          path="/admin/dashboard"
          element={
            <PrivateRoute allowedRoles={['admin']}>
              <AdminDashboard />
            </PrivateRoute>
          }
        />
        <Route
          path="/admin/categories/new"
          element={
            <PrivateRoute allowedRoles={['admin']}>
              <AddCategory />
            </PrivateRoute>
          }
        />
        <Route
          path="/users"
          element={
            <PrivateRoute allowedRoles={['admin']}>
              <UserList />
            </PrivateRoute>
          }
        />

        {/* Common protected */}
        <Route
          path="/profile"
          element={
            <PrivateRoute>
              <Profile />
            </PrivateRoute>
          }
        />
        <Route
          path="/change-password"
          element={
            <PrivateRoute>
              <ChangePassword />
            </PrivateRoute>
          }
        />

        {/* Checkout */}
        <Route path="/checkout" element={<Checkout />} />
        <Route path="/order-success" element={<OrderSuccess />} />

        {/* Chat */}
        <Route path="/chat/:shopId" element={<Chat />} />
        <Route path="/shop-chat/:shopId/:buyerId" element={<ShopChat />} />

        {/* Default */}
        <Route path="*" element={<RoleBasedRedirect />} />
      </Routes>

      {/* Chat popup button */}
      {user && <ChatButton />}
    </div>
  );
}

// ----- App wrapper -----
function App() {
  return (
    <Router>
      <AuthProvider>
        <CartProvider>
          <AppContent />
        </CartProvider>
      </AuthProvider>
    </Router>
  );
}

// ----- Role-based redirect -----
const RoleBasedRedirect = () => {
  const { user, getDefaultRoute } = useAuth();

  if (!user) return <Navigate to="/login" />;
  return <Navigate to={getDefaultRoute(user.user_type)} />;
};

export default App;
