n', conv.id);    navigate(`/chat/${conv.shop_id}?${params.toString()}`);    setOpen(false);  };  return (    <div style={{ position:'relative' }}>      <button        ref={btnRef}        onClick={() => setOpen(o => !o)}        aria-label="Tin nh·∫Øn"        title="Tin nh·∫Øn"        style={{          display:'inline-flex', alignItems:'center', justifyContent:'center',          width:38, height:38, borderRadius:'50%', border:'1px solid #e5e7eb',          background:'#fff', cursor:'pointer'        }}      >        üí¨      </button>      {open && (        <div          ref={menuRef}          style={{            position:'absolute', right:0, top:46, width:320,            background:'#fff', border:'1px solid #e5e7eb', borderRadius:8,            boxShadow:'0 8px 24px rgba(0,0,0,0.12)', zIndex:1000, overflow:'hidden'          }}        >          <div style={{ padding:'10px 12px', borderBottom:'1px solid #eee', fontWeight:600 }}>            H·ªôi tho·∫°i c·ªßa b·∫°n          </div>          {loading && <div style={{ padding:12, fontSize:14 }}>ƒêang t·∫£i...</div>}          {error && <div style={{ padding:12, fontSize:13, color:'#b91c1c' }}>{error}</div>}          {!loading && !error && conversations.length === 0 && (            <div style={{ padding:12, fontSize:14, color:'#6b7280' }}>Ch∆∞a c√≥ h·ªôi tho·∫°i.</div>          )}          <div style={{ maxHeight:360, overflowY:'auto' }}>            {conversations.map(conv => {              const name = (user.is_seller || user.is_staff)                ? (conv.buyer_name || `Ng∆∞·ªùi mua #${conv.buyer_id}`)                : (conv.shop_name || `Shop #${conv.shop_id}`);              return (                <button                  key={conv.id}                  onClick={() => openConversation(conv)}                  style={{                    display:'flex', width:'100%', gap:10, padding:'10px 12px',                    border:'none', background:'#fff', cursor:'pointer',                    borderBottom:'1px solid #f3f4f6'                  }}                >                  <div style={{                    width:36, height:36, borderRadius:'50%',                    background:'#eef2ff', display:'flex', alignItems:'center', justifyContent:'center',                    color:'#4f46e5', fontWeight:700                  }}>                    {name?.[0]?.toUpperCase() || '?'}                  </div>                  <div style={{ flex:1, textAlign:'left' }}>                    <div style={{ fontWeight:600, fontSize:14, lineHeight:'18px' }}>{name}</div>                    <div style={{ fontSize:12, color:'#6b7280' }}>                      {conv.product_id ? `S·∫£n ph·∫©m #${conv.product_id}` : 'H·ªôp tho·∫°i chung'}                    </div>                  </div>                </button>              );            })}          </div>          <div style={{ padding:8, textAlign:'right' }}>            <button              onClick={() => setOpen(false)}              style={{ background:'transparent', border:'none', color:'#2563eb', cursor:'pointer', padding:'6px 10px' }}            >              ƒê√≥ng            </button>          </div>        </div>      )}
    </div>
