import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../utils/AuthContext';
import AuthModal from './AuthModal.jsx';

export default function Header({
  query = '',
  onQueryChange,
  onOpenLogin,
  onOpenRegister,
  onLogout: onLogoutProp,
  currentUser: currentUserProp
}) {
  const navigate = useNavigate();
  const auth = useAuth();
  const currentUser = currentUserProp ?? auth?.user;
  const logoutFn = onLogoutProp ?? auth?.logout ?? (() => {});
  const [menuOpen, setMenuOpen] = useState(false);
  const [localQuery, setLocalQuery] = useState(query || '');
  const [isAuthOpen, setIsAuthOpen] = useState(false);
  const [authMode, setAuthMode] = useState('login'); // 'login' | 'register'

  function onSearch(e) {
    e?.preventDefault();
    const q = localQuery.trim();
    if (onQueryChange) onQueryChange(q);
    if (q) navigate(`/search?q=${encodeURIComponent(q)}`);
  }

  function openLogin() {
    setAuthMode('login');
    setIsAuthOpen(true);
    if (onOpenLogin) onOpenLogin();
  }
  function openRegister() {
    setAuthMode('register');
    setIsAuthOpen(true);
    if (onOpenRegister) onOpenRegister();
  }

  function handleLogout() {
    try {
      logoutFn();
    } catch (_) {}
    // try to let auth context refresh, otherwise fallback to reload
    if (auth?.refresh) auth.refresh();
    else window.location.href = '/';
  }

  function handleAuthSuccess(user) {
    // try to set user in context if available
    if (auth?.setUser) auth.setUser(user);
    if (auth?.refresh) auth.refresh();
    setIsAuthOpen(false);
    setMenuOpen(false);
    // navigate to default route for the user (buyer/seller)
    if (user?.user_type && auth?.getDefaultRoute) {
      navigate(auth.getDefaultRoute(user.user_type));
    } else {
      navigate('/');
    }
  }

  return (
    <>
      <header className="topbar">
        <Link to="/" className="brand" aria-label="V·ªÅ trang ch·ªß">
          <span className="brand-name">V-Market</span>
        </Link>

        <form className="search-area" onSubmit={onSearch}>
          <input
            type="text"
            placeholder="T√¨m s·∫£n ph·∫©m, th∆∞∆°ng hi·ªáu..."
            value={localQuery}
            onChange={(e) => {
              setLocalQuery(e.target.value);
              if (onQueryChange) onQueryChange(e.target.value);
            }}
            className="search-input"
          />
          <button type="submit" className="search-btn">T√¨m</button>
        </form>

        <div className="actions">
          {currentUser ? (
            <>
              <div className="user-area">
                <button
                  className="user-toggle"
                  onClick={() => setMenuOpen((s) => !s)}
                  aria-haspopup="true"
                  aria-expanded={menuOpen}
                >
                  <img
                    src={currentUser.avatar || '/default-avatar.png'}
                    alt={currentUser.full_name || currentUser.username}
                    style={{ width: 36, height: 36, borderRadius: '50%' }}
                  />
                </button>

                {menuOpen && (
                  <div className="user-menu" role="menu">
                    {currentUser.user_type === 'buyer' && (
                      <Link to="/dashboard" onClick={() => setMenuOpen(false)}>Trang mua h√†ng</Link>
                    )}
                    {currentUser.user_type === 'seller' && (
                      <Link to="/seller/dashboard" onClick={() => setMenuOpen(false)}>Trang b√°n h√†ng</Link>
                    )}
                    <Link to="/profile" onClick={() => setMenuOpen(false)}>Trang c√° nh√¢n</Link>
                    <button
                      type="button"
                      onClick={() => {
                        handleLogout();
                        setMenuOpen(false);
                      }}
                    >
                      ƒêƒÉng xu·∫•t
                    </button>
                  </div>
                )}
              </div>
            </>
          ) : (
            <>
              <button type="button" className="action-btn" onClick={openLogin}>ƒêƒÉng nh·∫≠p</button>
              <button type="button" className="action-btn" onClick={openRegister}>ƒêƒÉng k√Ω</button>
            </>
          )}

          <Link to="/cart" className="cart-btn" aria-label="Gi·ªè h√†ng">üõí</Link>
        </div>
      </header>

      <AuthModal
        open={isAuthOpen}
        initialMode={authMode}
        onClose={() => setIsAuthOpen(false)}
        onLoginSuccess={handleAuthSuccess}
        onRegisterSuccess={handleAuthSuccess}
      />
    </>
  );
}
