import { useState, useMemo } from 'react'
import { useParams, Link } from 'react-router-dom'
import Layout from '../components/Layout.jsx'
import ProductGrid from '../components/ProductGrid.jsx'
import FiltersSidebar from '../components/FiltersSidebar.jsx'
import PageHeader from '../components/PageHeader.jsx'
import EmptyState from '../components/EmptyState.jsx'
import Pagination from '../components/Pagination.jsx'
import { PRODUCTS, CATEGORIES } from '../data/products.js'
import '../assets/CategoryPage.css'
import '../assets/Components.css'

export default function CategoryPage() {
  const { categoryName } = useParams()
  
  // Filter states
  const [query, setQuery] = useState('')
  const [priceRange, setPriceRange] = useState({ min: '', max: '' })
  const [sortBy, setSortBy] = useState('relevance')
  const [currentPage, setCurrentPage] = useState(1)

  // Decode category name from URL
  const decodedCategory = decodeURIComponent(categoryName || '')
  const currentCategory = CATEGORIES.find(cat => cat === decodedCategory) || 'T·∫•t c·∫£'

  const filteredProducts = useMemo(() => {
    let filtered = PRODUCTS.filter(product => {
      // Category filter
      const matchesCategory = currentCategory === 'T·∫•t c·∫£' || 
        product.category === currentCategory
      
      // Search query filter
      const matchesQuery = !query || 
        product.name.toLowerCase().includes(query.toLowerCase()) ||
        product.description.toLowerCase().includes(query.toLowerCase())
      
      // Price range filter
      const matchesPrice = (!priceRange.min || product.price >= parseInt(priceRange.min)) &&
        (!priceRange.max || product.price <= parseInt(priceRange.max))
      
      return matchesCategory && matchesQuery && matchesPrice
    })

    // Sort products
    switch (sortBy) {
      case 'price-low':
        filtered.sort((a, b) => a.price - b.price)
        break
      case 'price-high':
        filtered.sort((a, b) => b.price - a.price)
        break
      case 'name':
        filtered.sort((a, b) => a.name.localeCompare(b.name))
        break
      case 'newest':
        filtered.sort((a, b) => b.id - a.id)
        break
      default: // relevance
        break
    }

    return filtered
  }, [currentCategory, query, priceRange, sortBy])


  const clearFilters = () => {
    setQuery('')
    setPriceRange({ min: '', max: '' })
    setSortBy('relevance')
  }

  const getCategoryDescription = (category) => {
    const descriptions = {
      'ƒêi·ªán tho·∫°i': 'Kh√°m ph√° c√°c m·∫´u ƒëi·ªán tho·∫°i th√¥ng minh m·ªõi nh·∫•t v·ªõi c√¥ng ngh·ªá ti√™n ti·∫øn',
      'Laptop': 'Laptop hi·ªáu nƒÉng cao, ph√π h·ª£p cho c√¥ng vi·ªác v√† gi·∫£i tr√≠',
      'Th·ªùi trang': 'Th·ªùi trang hi·ªán ƒë·∫°i, phong c√°ch v√† ch·∫•t l∆∞·ª£ng cao',
      'M·ªπ ph·∫©m': 'M·ªπ ph·∫©m ch√≠nh h√£ng, an to√†n cho l√†n da',
      'Gia d·ª•ng': 'ƒê·ªì gia d·ª•ng th√¥ng minh, ti·ªán √≠ch cho cu·ªôc s·ªëng',
      'Th·ªÉ thao': 'ƒê·ªì th·ªÉ thao ch·∫•t l∆∞·ª£ng, h·ªó tr·ª£ t·ªëi ƒëa cho ho·∫°t ƒë·ªông',
      'ƒê·ªì ch∆°i': 'ƒê·ªì ch∆°i an to√†n, k√≠ch th√≠ch s√°ng t·∫°o cho tr·∫ª em'
    }
    return descriptions[category] || 'Kh√°m ph√° c√°c s·∫£n ph·∫©m ch·∫•t l∆∞·ª£ng cao'
  }

  const totalPages = Math.ceil(filteredProducts.length / 12) // 12 items per page

  return (
    <Layout className="category-page">
      <div className="category-container">
        <PageHeader
          title={currentCategory}
          subtitle={getCategoryDescription(currentCategory)}
          resultsCount={filteredProducts.length}
          breadcrumb={[
            { text: 'Trang ch·ªß', link: '/' },
            { text: currentCategory }
          ]}
          className="category-header"
        />

        <div className="category-content">
          <FiltersSidebar
            categories={CATEGORIES}
            selectedCategory={currentCategory}
            onCategoryChange={() => {}}
            priceRange={priceRange}
            onPriceRangeChange={setPriceRange}
            sortBy={sortBy}
            onSortChange={setSortBy}
            onClearFilters={clearFilters}
            showCategoryLinks={true}
          />

          <div className="products-section">
            {filteredProducts.length === 0 ? (
              <EmptyState
                icon="üì¶"
                title="Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o"
                description="Hi·ªán t·∫°i ch∆∞a c√≥ s·∫£n ph·∫©m n√†o trong danh m·ª•c n√†y"
                actionText="Xem t·∫•t c·∫£ s·∫£n ph·∫©m"
                actionLink="/"
                className="no-results"
              />
            ) : (
              <>
                <ProductGrid products={filteredProducts} />
                <Pagination
                  currentPage={currentPage}
                  totalPages={totalPages}
                  onPageChange={setCurrentPage}
                />
              </>
            )}
          </div>
        </div>
      </div>
    </Layout>
  )
}
