import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../utils/CartContext';
import { useAuth } from '../utils/AuthContext';
import AuthModal from '../components/AuthModal';  // â¬…ï¸ Import
import { formatPrice } from '../utils/formatPrice';
import { cities, getDistrictsByCity, getWardsByDistrict } from '../utils/locationsData';
import '../assets/Checkout.css';

export default function Checkout() {
  const navigate = useNavigate();
  const { cartItems, clearCart, getCartTotal } = useCart();
  const { user } = useAuth();
  
  // State cho auth modal
  const [showAuthModal, setShowAuthModal] = useState(false);

  // Form states
  const [formData, setFormData] = useState({
    fullName: '',
    phone: '',
    email: '',
    address: '',
    city: '',
    district: '',
    ward: '',
    paymentMethod: 'cod',
    notes: ''
  });

  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [cities, setCities] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [wards, setWards] = useState([]);

  // Kiá»ƒm tra user Ä‘Ã£ Ä‘Äƒng nháº­p chÆ°a
  useEffect(() => {
    if (cartItems.length === 0) {
      navigate('/cart');
      return;
    }

    // Náº¿u chÆ°a Ä‘Äƒng nháº­p, hiá»‡n modal
    if (!user) {
      setShowAuthModal(true);
    } else {
      // Load thÃ´ng tin user
      setFormData(prev => ({
        ...prev,
        fullName: user.full_name || '',
        phone: user.phone || '',
        email: user.email || '',
      }));
    }
  }, [user, cartItems, navigate]);

  // Xá»­ lÃ½ sau khi Ä‘Äƒng nháº­p thÃ nh cÃ´ng
  const handleLoginSuccess = (userData) => {
    console.log('âœ… Login successful:', userData);
    setShowAuthModal(false);
    
    // Load thÃ´ng tin user vÃ o form
    setFormData(prev => ({
      ...prev,
      fullName: userData.full_name || '',
      phone: userData.phone || '',
      email: userData.email || '',
    }));
  };

  // Náº¿u chÆ°a Ä‘Äƒng nháº­p, hiá»ƒn thá»‹ modal
  if (!user) {
    return (
      <>
        <div className="checkout-page">
          <div style={{ textAlign: 'center', padding: '60px 20px' }}>
            <h2>Vui lÃ²ng Ä‘Äƒng nháº­p Ä‘á»ƒ tiáº¿p tá»¥c Ä‘áº·t hÃ ng</h2>
          </div>
        </div>
        
        <AuthModal
          open={showAuthModal}
          initialMode="login"
          onClose={() => navigate('/cart')}
          onLoginSuccess={handleLoginSuccess}
          onRegisterSuccess={handleLoginSuccess}
        />
      </>
    );
  }

  // Load user info and address data
  useEffect(() => {
    if (cartItems.length === 0) {
      navigate('/cart');
      return;
    }

    // Load user info if logged in
    if (user) {
      setFormData(prev => ({
        ...prev,
        fullName: user.full_name || '',
        phone: user.phone || '',
        email: user.email || ''
      }));

      // Load user's saved address if exists
      fetch('/api/user/address/', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('access_token')}`
        }
      })
        .then(res => res.json())
        .then(data => {
          if (data && data.address) {
            setFormData(prev => ({
              ...prev,
              address: data.address || '',
              city: data.city || '',
              district: data.district || '',
              ward: data.ward || ''
            }));
          }
        })
        .catch(err => console.error('Error loading address:', err));
    }

    // Load cities from mock data instead of API
    setCities(getCities());
  }, [cartItems, user, navigate]);

  // Load districts when city changes
  useEffect(() => {
    if (formData.city) {
      setDistricts(getDistrictsByCity(formData.city));
    } else {
      setDistricts([]);
      setWards([]);
    }
  }, [formData.city]);

  // Load wards when district changes
  useEffect(() => {
    if (formData.district) {
      setWards(getWardsByDistrict(formData.district));
    } else {
      setWards([]);
    }
  }, [formData.district]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.fullName.trim()) newErrors.fullName = 'Vui lÃ²ng nháº­p há» tÃªn';
    if (!formData.phone.trim()) newErrors.phone = 'Vui lÃ²ng nháº­p sá»‘ Ä‘iá»‡n thoáº¡i';
    else if (!/^[0-9]{10,11}$/.test(formData.phone.trim())) {
      newErrors.phone = 'Sá»‘ Ä‘iá»‡n thoáº¡i khÃ´ng há»£p lá»‡';
    }
    if (!formData.email.trim()) newErrors.email = 'Vui lÃ²ng nháº­p email';
    else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Email khÃ´ng há»£p lá»‡';
    }
    if (!formData.address.trim()) newErrors.address = 'Vui lÃ²ng nháº­p Ä‘á»‹a chá»‰';
    if (!formData.city) newErrors.city = 'Vui lÃ²ng chá»n tá»‰nh/thÃ nh phá»‘';
    if (!formData.district) newErrors.district = 'Vui lÃ²ng chá»n quáº­n/huyá»‡n';
    if (!formData.ward) newErrors.ward = 'Vui lÃ²ng chá»n phÆ°á»ng/xÃ£';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    setLoading(true);

    try {
      // Mock order submission - sá»­ dá»¥ng khi backend chÆ°a sáºµn sÃ ng
      const useMockAPI = true; // Äá»•i thÃ nh false khi backend ready
      
      if (useMockAPI) {
        // Simulate API delay
        await new Promise(resolve => setTimeout(resolve, 1500));
        
        const mockOrderId = `ORD${Date.now().toString().slice(-8)}`;
        
        console.log('ğŸ“¦ Mock Order Created:', {
          order_id: mockOrderId,
          customer_info: {
            full_name: formData.fullName,
            phone: formData.phone,
            email: formData.email
          },
          shipping_address: {
            address: formData.address,
            ward: formData.ward,
            district: formData.district,
            city: formData.city
          },
          payment_method: formData.paymentMethod,
          notes: formData.notes,
          items: cartItems,
          total_amount: getCartTotal()
        });

        // Clear cart
        clearCart();

        // Navigate to success page
        navigate('/order-success', { 
          state: { orderId: mockOrderId } 
        });
        
        return;
      }

      // Real API call
      const orderData = {
        customer_info: {
          full_name: formData.fullName,
          phone: formData.phone,
          email: formData.email
        },
        shipping_address: {
          address: formData.address,
          ward: formData.ward,
          district: formData.district,
          city: formData.city
        },
        payment_method: formData.paymentMethod,
        notes: formData.notes,
        items: cartItems.map(item => ({
          product_id: item.id,
          quantity: item.quantity,
          color: item.color || '',
          size: item.size || '',
          price: item.price
        })),
        total_amount: getCartTotal()
      };

      console.log('ğŸ“¤ Sending order data:', orderData);

      const response = await fetch('http://localhost:8000/api/orders/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...(user && { 'Authorization': `Bearer ${localStorage.getItem('access_token')}` })
        },
        body: JSON.stringify(orderData)
      });

      console.log('ğŸ“¥ Response status:', response.status);

      // Check if response is JSON
      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        const text = await response.text();
        console.error('âŒ Non-JSON response:', text.substring(0, 200));
        throw new Error('Server tráº£ vá» dá»¯ liá»‡u khÃ´ng há»£p lá»‡. Vui lÃ²ng kiá»ƒm tra backend.');
      }

      const result = await response.json();
      console.log('ğŸ“¥ Response data:', result);

      if (!response.ok) {
        throw new Error(result.message || result.detail || 'Äáº·t hÃ ng tháº¥t báº¡i');
      }

      if (!result.success) {
        throw new Error(result.message || 'Äáº·t hÃ ng tháº¥t báº¡i');
      }

      // Clear cart after successful order
      clearCart();

      // Navigate to success page with order ID
      navigate('/order-success', { 
        state: { orderId: result.order.order_id } 
      });
      
    } catch (error) {
      console.error('âŒ Error submitting order:', error);
      
      let errorMessage = 'CÃ³ lá»—i xáº£y ra khi Ä‘áº·t hÃ ng. Vui lÃ²ng thá»­ láº¡i!';
      
      if (error.message.includes('Failed to fetch')) {
        errorMessage = 'KhÃ´ng thá»ƒ káº¿t ná»‘i Ä‘áº¿n server. Vui lÃ²ng kiá»ƒm tra káº¿t ná»‘i máº¡ng hoáº·c Ä‘áº£m báº£o backend Ä‘ang cháº¡y.';
      } else if (error.message.includes('JSON')) {
        errorMessage = 'Lá»—i xá»­ lÃ½ dá»¯ liá»‡u tá»« server. Vui lÃ²ng kiá»ƒm tra backend API.';
      } else if (error.message) {
        errorMessage = error.message;
      }
      
      alert(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  if (cartItems.length === 0) {
    return null; // Will redirect in useEffect
  }

  return (
    <div className="checkout-page">
      <div className="checkout-container">
        <div className="checkout-header">
          <h1>Thanh toÃ¡n</h1>
          <div className="checkout-steps">
            <div className="step active">1. ThÃ´ng tin giao hÃ ng</div>
            <div className="step">2. XÃ¡c nháº­n Ä‘Æ¡n hÃ ng</div>
            <div className="step">3. HoÃ n táº¥t</div>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="checkout-form">
          <div className="checkout-content">
            <div className="shipping-info">
              <h2>ThÃ´ng tin giao hÃ ng</h2>
              
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="fullName">Há» vÃ  tÃªn *</label>
                  <input
                    type="text"
                    id="fullName"
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleInputChange}
                    className={errors.fullName ? 'error' : ''}
                    placeholder="Nháº­p há» vÃ  tÃªn"
                  />
                  {errors.fullName && <span className="error-message">{errors.fullName}</span>}
                </div>
                
                <div className="form-group">
                  <label htmlFor="phone">Sá»‘ Ä‘iá»‡n thoáº¡i *</label>
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    className={errors.phone ? 'error' : ''}
                    placeholder="Nháº­p sá»‘ Ä‘iá»‡n thoáº¡i"
                  />
                  {errors.phone && <span className="error-message">{errors.phone}</span>}
                </div>
              </div>

              <div className="form-group">
                <label htmlFor="email">Email *</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className={errors.email ? 'error' : ''}
                  placeholder="Nháº­p email"
                />
                {errors.email && <span className="error-message">{errors.email}</span>}
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="city">Tá»‰nh/ThÃ nh phá»‘ *</label>
                  <select
                    id="city"
                    name="city"
                    value={formData.city}
                    onChange={handleInputChange}
                    className={errors.city ? 'error' : ''}
                  >
                    <option value="">Chá»n tá»‰nh/thÃ nh phá»‘</option>
                    {cities.map(city => (
                      <option key={city.id} value={city.id}>
                        {city.name}
                      </option>
                    ))}
                  </select>
                  {errors.city && <span className="error-message">{errors.city}</span>}
                </div>
                
                <div className="form-group">
                  <label htmlFor="district">Quáº­n/Huyá»‡n *</label>
                  <select
                    id="district"
                    name="district"
                    value={formData.district}
                    onChange={handleInputChange}
                    className={errors.district ? 'error' : ''}
                    disabled={!formData.city}
                  >
                    <option value="">Chá»n quáº­n/huyá»‡n</option>
                    {districts.map(district => (
                      <option key={district.id} value={district.id}>
                        {district.name}
                      </option>
                    ))}
                  </select>
                  {errors.district && <span className="error-message">{errors.district}</span>}
                </div>
              </div>

              <div className="form-group">
                <label htmlFor="ward">PhÆ°á»ng/XÃ£ *</label>
                <select
                  id="ward"
                  name="ward"
                  value={formData.ward}
                  onChange={handleInputChange}
                  className={errors.ward ? 'error' : ''}
                  disabled={!formData.district}
                >
                  <option value="">Chá»n phÆ°á»ng/xÃ£</option>
                  {wards.map(ward => (
                    <option key={ward.id} value={ward.id}>
                      {ward.name}
                    </option>
                  ))}
                </select>
                {errors.ward && <span className="error-message">{errors.ward}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="address">Äá»‹a chá»‰ chi tiáº¿t *</label>
                <textarea
                  id="address"
                  name="address"
                  value={formData.address}
                  onChange={handleInputChange}
                  className={errors.address ? 'error' : ''}
                  placeholder="Sá»‘ nhÃ , tÃªn Ä‘Æ°á»ng..."
                  rows="3"
                />
                {errors.address && <span className="error-message">{errors.address}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="notes">Ghi chÃº</label>
                <textarea
                  id="notes"
                  name="notes"
