import React, { useEffect, useState } from 'react';
import { useAuth } from '../utils/AuthContext';
import PopupChat from './PopupChat';

export default function ChatButton() {
  const { user } = useAuth();
  const [conversations, setConversations] = useState([]);
  const [openChats, setOpenChats] = useState([]); // danh sách popup đang mở

  useEffect(() => {
    if (!user) return;

    const token = localStorage.getItem('access_token') || '';

    const fetchConversations = async () => {
      try {
        const res = await fetch(`${process.env.REACT_APP_API_BASE || 'http://localhost:8000'}/api/conversations/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        if (res.ok) {
          const data = await res.json();
          setConversations(data);
        }
      } catch (err) {
        console.error('Fetch conversations error:', err);
      }
    };

    fetchConversations();
  }, [user]);

  const toggleChat = (conv) => {
    setOpenChats((prev) => {
      if (prev.find(c => c.id === conv.id)) return prev.filter(c => c.id !== conv.id);
      return [...prev, conv];
    });
  };

  return (
    <>
      {/* Icon Messenger góc phải dưới */}
      <div style={{
        position: 'fixed',
        bottom: 20,
        right: 20,
        display: 'flex',
        flexDirection: 'column-reverse',
        gap: 10,
        zIndex: 9999
      }}>
        {conversations.map(conv => {
          const name = user.user_type === 'buyer' ? conv.shop_name : conv.buyer_name;
          return (
            <button
              key={conv.id}
              onClick={() => toggleChat(conv)}
              style={{
                width: 50,
                height: 50,
                borderRadius: '50%',
                background: '#2563eb',
                color: '#fff',
                border: 'none',
                cursor: 'pointer',
                position: 'relative'
              }}
              title={name}
            >
              {name[0].toUpperCase()}
              {conv.unread_count > 0 && (
                <span style={{
                  position: 'absolute',
                  top: -5,
                  right: -5,
                  background: 'red',
                  color: 'white',
                  fontSize: 12,
                  borderRadius: '50%',
                  padding: '2px 5px'
                }}>
                  {conv.unread_count}
                </span>
              )}
            </button>
          );
        })}
      </div>

      {/* Popup chat */}
      {openChats.map(conv => (
        <PopupChat
          key={conv.id}
          shopId={conv.shop_id}
          buyerId={conv.buyer_id}
          onClose={() => toggleChat(conv)}
        />
      ))}
    </>
  );
}
