import React, { useEffect, useRef, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../utils/AuthContext';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:8000';

export default function Chat({ shopId: shopIdProp, buyerId: buyerIdProp, isPopup = false }) {
  const { shopId: shopIdUrl } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');
  const wsRef = useRef(null);
  const bottomRef = useRef(null);

  const shopId = shopIdProp || shopIdUrl;
  const buyerId = buyerIdProp || user?.user_id;

  useEffect(() => {
    if (!user) {
      if (!isPopup) navigate('/login');
      return;
    }

    if (!shopId) return;

    const token = localStorage.getItem('access_token') || '';
    const raw = API_BASE.replace(/^https?:\/\//, '');
    const wsProtocol = API_BASE.startsWith('https') ? 'wss' : 'ws';
    const wsUrl = `${wsProtocol}://${raw}/ws/chat/${shopId}/?token=${token}&buyer=${buyerId}`;

    const ws = new WebSocket(wsUrl);
    wsRef.current = ws;

    ws.onopen = () => console.log('WS connected');
    ws.onmessage = (evt) => {
      try {
        const data = JSON.parse(evt.data);
        if (data.type === 'history') setMessages(data.messages || []);
        else if (data.type === 'message' && data.message) setMessages((prev) => [...prev, data.message]);
      } catch {}
    };
    ws.onclose = (evt) => console.error('WS closed', evt);
    ws.onerror = (e) => console.error('WS error', e);

    return () => ws.close();
  }, [shopId, buyerId, user, navigate]);

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = () => {
    const content = text.trim();
    if (!content || !wsRef.current || wsRef.current.readyState !== 1) return;
    wsRef.current.send(JSON.stringify({ type: 'message', content }));
    setText('');
  };

  if (!user) return null;

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
      {!isPopup && (
        <div style={{ padding: 12, borderBottom: '1px solid #eee', display: 'flex', justifyContent: 'space-between' }}>
          <Link to="/">&larr; Trang chủ</Link>
          <strong>Chat với shop #{shopId}</strong>
        </div>
      )}
      <div style={{ flex: 1, overflowY: 'auto', padding: 12 }}>
        {messages.map((m) => (
          <div
            key={m.id}
            style={{
              marginBottom: 8,
              display: 'flex',
              justifyContent: m.sender_id === user?.user_id ? 'flex-end' : 'flex-start',
            }}
          >
            <div
              style={{
                maxWidth: '70%',
                padding: '8px 12px',
                borderRadius: 12,
                background: m.sender_id === user?.user_id ? '#DCFCE7' : '#F3F4F6',
              }}
            >
              <div style={{ fontSize: 14 }}>{m.content}</div>
              <div style={{ fontSize: 11, color: '#6b7280', marginTop: 4 }}>
                {new Date(m.created_at).toLocaleString('vi-VN')}
              </div>
            </div>
          </div>
        ))}
        <div ref={bottomRef} />
      </div>
      <div style={{ padding: 12, display: 'flex', gap: 8, borderTop: '1px solid #eee' }}>
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
          placeholder="Nhập nội dung..."
          style={{ flex: 1, padding: '10px 12px', border: '1px solid #ddd', borderRadius: 8 }}
        />
        <button
          onClick={sendMessage}
          style={{ padding: '10px 16px', background: '#2563eb', color: '#fff', border: 'none', borderRadius: 8 }}
        >
          Gửi
        </button>
      </div>
    </div>
  );
}
