// components/PopupChat.jsx
import React, { useEffect, useRef, useState } from 'react';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:8000';

export default function PopupChat({ conversationId, shopId, buyerId, onClose }) {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');
  const wsRef = useRef(null);
  const bottomRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem('access_token');
    if (!token) return;

    // Load history via REST
    fetch(`${API_BASE}/api/chat/conversations/${conversationId}/messages/`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(r => r.ok ? r.json() : Promise.reject(r.status))
      .then(data => setMessages(Array.isArray(data) ? data : []))
      .catch(() => {});

    // WebSocket
    const raw = API_BASE.replace(/^https?:\/\//,'')
    const wsProtocol = API_BASE.startsWith('https') ? 'wss' : 'ws';
    const wsUrl = `${wsProtocol}://${raw}/ws/chat/${shopId}/?token=${encodeURIComponent(token)}`;
    const ws = new WebSocket(wsUrl);
    wsRef.current = ws;

    ws.onmessage = evt => {
      try {
        const payload = JSON.parse(evt.data);
        if (payload.type === 'history') {
          // ignore, history đã lấy qua REST
        } else if (payload.type === 'message' && payload.message) {
          setMessages(prev => [...prev, payload.message]);
        }
      } catch {}
    };

    return () => ws.close();
  }, [conversationId, shopId]);

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior:'smooth' });
  }, [messages]);

  const sendMessage = () => {
    const content = text.trim();
    if (!content) return;
    // Gửi qua REST để đảm bảo tồn tại ngay, WS sẽ broadcast sau (hoặc có thể gửi WS)
    const token = localStorage.getItem('access_token');
    fetch(`${API_BASE}/api/chat/conversations/${conversationId}/messages/`, {
      method:'POST',
      headers:{
        'Content-Type':'application/json',
        Authorization:`Bearer ${token}`
      },
      body: JSON.stringify({ content })
    })
      .then(r => r.ok ? r.json() : Promise.reject(r.status))
      .then(msg => {
        setMessages(prev => [...prev, msg]);
        setText('');
      })
      .catch(() => {});
  };

  return (
    <div style={{
      position:'fixed', bottom:80, right:20,
      width:300, height:380,
      background:'#fff', border:'1px solid #ddd', borderRadius:8,
      display:'flex', flexDirection:'column', overflow:'hidden',
      boxShadow:'0 4px 12px rgba(0,0,0,0.15)', zIndex:9999
    }}>
      <div style={{padding:'8px 12px', background:'#2563eb', color:'#fff', display:'flex', justifyContent:'space-between'}}>
        <strong>Chat</strong>
        <button onClick={onClose} style={{background:'transparent', border:'none', color:'#fff', cursor:'pointer'}}>×</button>
      </div>
      <div style={{flex:1, padding:10, overflowY:'auto'}}>
        {messages.map(m => (
          <div key={m.id} style={{marginBottom:8, display:'flex', justifyContent: m.sender_id === buyerId ? 'flex-end':'flex-start'}}>
            <div style={{
              background: m.sender_id === buyerId ? '#DCFCE7' : '#F3F4F6',
              padding:'6px 10px', borderRadius:12, maxWidth:'75%'
            }}>
              <div style={{fontSize:13}}>{m.content}</div>
              <div style={{fontSize:10, color:'#6b7280', marginTop:4}}>
                {new Date(m.created_at).toLocaleTimeString('vi-VN')}
              </div>
            </div>
          </div>
        ))}
        <div ref={bottomRef}/>
      </div>
      <div style={{padding:8, borderTop:'1px solid #eee', display:'flex', gap:6}}>
        <input
          value={text}
          onChange={e=>setText(e.target.value)}
          onKeyDown={e=> e.key==='Enter' && sendMessage()}
          placeholder="Nhập tin nhắn..."
          style={{flex:1, padding:'8px 10px', border:'1px solid #ccc', borderRadius:6}}
        />
        <button onClick={sendMessage} style={{
          background:'#2563eb', color:'#fff', border:'none',
          padding:'8px 12px', borderRadius:6, cursor:'pointer'
        }}>Gửi</button>
      </div>
    </div>
  );
}
