// components/ChatButton.jsx
import React, { useState } from 'react';
import PopupChat from './PopupChat';

export default function ChatButton({ shops, userId }) {
  const [openChats, setOpenChats] = useState([]); // danh sách popup chat đang mở

  const toggleChat = (shopId) => {
    setOpenChats(prev => {
      if (prev.includes(shopId)) return prev.filter(id => id !== shopId);
      return [...prev, shopId];
    });
  };

  return (
    <>
      {/* Icon Messenger góc phải dưới */}
      <div style={{
        position: 'fixed',
        bottom: 20,
        right: 20,
        display: 'flex',
        flexDirection: 'column-reverse',
        gap: 10,
        zIndex: 9999
      }}>
        {shops.map(shop => (
          <button
            key={shop.id}
            onClick={() => toggleChat(shop.id)}
            style={{
              width: 50, height: 50, borderRadius: '50%',
              background: '#2563eb', color: '#fff', border: 'none',
              cursor: 'pointer'
            }}
          >
            {shop.name[0].toUpperCase()}
          </button>
        ))}
      </div>

      {/* Popup chat */}
      {openChats.map(shopId => (
        <PopupChat
          key={shopId}
          shopId={shopId}
          userId={userId}
          onClose={() => toggleChat(shopId)}
        />
      ))}
    </>
  );
}
