import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../utils/CartContext';
import { useAuth } from '../utils/AuthContext';
import AuthModal from '../components/AuthModal';  // ‚¨ÖÔ∏è Import
import { formatPrice } from '../utils/formatPrice';
import { 
  getCities, 
  getDistrictsByCity, 
  getWardsByDistrict 
} from '../utils/locationsData';
import '../assets/Checkout.css';

export default function Checkout() {
  const navigate = useNavigate();
  const { cartItems, clearCart, getCartTotal } = useCart();
  const { user } = useAuth();
  
  // State cho auth modal
  const [showAuthModal, setShowAuthModal] = useState(false);

  // Form states
  const [formData, setFormData] = useState({
    fullName: '',
    phone: '',
    email: '',
    address: '',
    city: '',
    district: '',
    ward: '',
    paymentMethod: 'cod',
    notes: ''
  });

  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [cities, setCities] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [wards, setWards] = useState([]);

  // Ki·ªÉm tra user ƒë√£ ƒëƒÉng nh·∫≠p ch∆∞a
  useEffect(() => {
    if (cartItems.length === 0) {
      navigate('/cart');
      return;
    }

    // N·∫øu ch∆∞a ƒëƒÉng nh·∫≠p, hi·ªán modal
    if (!user) {
      setShowAuthModal(true);
    } else {
      // Load th√¥ng tin user
      setFormData(prev => ({
        ...prev,
        fullName: user.full_name || '',
        phone: user.phone || '',
        email: user.email || '',
      }));
    }
  }, [user, cartItems, navigate]);

  // X·ª≠ l√Ω sau khi ƒëƒÉng nh·∫≠p th√†nh c√¥ng
  const handleLoginSuccess = (userData) => {
    console.log('‚úÖ Login successful:', userData);
    setShowAuthModal(false);
    
    // Load th√¥ng tin user v√†o form
    setFormData(prev => ({
      ...prev,
      fullName: userData.full_name || '',
      phone: userData.phone || '',
      email: userData.email || '',
    }));
  };

  // N·∫øu ch∆∞a ƒëƒÉng nh·∫≠p, hi·ªÉn th·ªã modal (kh√¥ng return s·ªõm ƒë·ªÉ gi·ªØ th·ª© t·ª± Hooks)
  // Modal ƒë∆∞·ª£c ƒëi·ªÅu khi·ªÉn b·ªüi showAuthModal v√† useEffect ·ªü tr√™n s·∫Ω m·ªü modal khi user l√† falsy.
  // Khi ƒë√≥ng modal, onClose s·∫Ω ƒëi·ªÅu h∆∞·ªõng v·ªÅ /cart.

  // Load user info and address data
  useEffect(() => {
    if (cartItems.length === 0) {
      navigate('/cart');
      return;
    }

    // Load user info if logged in
    if (user) {
      setFormData(prev => ({
        ...prev,
        fullName: user.full_name || '',
        phone: user.phone || '',
        email: user.email || ''
      }));

      // Load user's saved address if exists
      fetch('/api/user/address/', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('access_token')}`
        }
      })
        .then(res => res.json())
        .then(data => {
          if (data && data.address) {
            setFormData(prev => ({
              ...prev,
              address: data.address || '',
              city: data.city || '',
              district: data.district || '',
              ward: data.ward || ''
            }));
          }
        })
        .catch(err => console.error('Error loading address:', err));
    }

    // Load cities from mock data instead of API
    setCities(getCities());
  }, [cartItems, user, navigate]);

  // Load districts when city changes
  useEffect(() => {
    if (formData.city) {
      setDistricts(getDistrictsByCity(formData.city));
    } else {
      setDistricts([]);
      setWards([]);
    }
  }, [formData.city]);

  // Load wards when district changes
  useEffect(() => {
    if (formData.district) {
      setWards(getWardsByDistrict(formData.district));
    } else {
      setWards([]);
    }
  }, [formData.district]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.fullName.trim()) newErrors.fullName = 'Vui l√≤ng nh·∫≠p h·ªç t√™n';
    if (!formData.phone.trim()) newErrors.phone = 'Vui l√≤ng nh·∫≠p s·ªë ƒëi·ªán tho·∫°i';
    else if (!/^[0-9]{10,11}$/.test(formData.phone.trim())) {
      newErrors.phone = 'S·ªë ƒëi·ªán tho·∫°i kh√¥ng h·ª£p l·ªá';
    }
    if (!formData.email.trim()) newErrors.email = 'Vui l√≤ng nh·∫≠p email';
    else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Email kh√¥ng h·ª£p l·ªá';
    }
    if (!formData.address.trim()) newErrors.address = 'Vui l√≤ng nh·∫≠p ƒë·ªãa ch·ªâ';
    if (!formData.city) newErrors.city = 'Vui l√≤ng ch·ªçn t·ªânh/th√†nh ph·ªë';
    if (!formData.district) newErrors.district = 'Vui l√≤ng ch·ªçn qu·∫≠n/huy·ªán';
    if (!formData.ward) newErrors.ward = 'Vui l√≤ng ch·ªçn ph∆∞·ªùng/x√£';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    setLoading(true);

    try {
      // Mock order submission - s·ª≠ d·ª•ng khi backend ch∆∞a s·∫µn s√†ng
      const useMockAPI = true; // ƒê·ªïi th√†nh false khi backend ready
      
      if (useMockAPI) {
        // Simulate API delay
        await new Promise(resolve => setTimeout(resolve, 1500));
        
        const mockOrderId = `ORD${Date.now().toString().slice(-8)}`;
        
        console.log('üì¶ Mock Order Created:', {
          order_id: mockOrderId,
          customer_info: {
            full_name: formData.fullName,
            phone: formData.phone,
            email: formData.email
          },
          shipping_address: {
            address: formData.address,
            ward: formData.ward,
            district: formData.district,
            city: formData.city
          },
          payment_method: formData.paymentMethod,
          notes: formData.notes,
          items: cartItems,
          total_amount: getCartTotal()
        });

        // Clear cart
        clearCart();

        // Navigate to success page
        navigate('/order-success', { 
          state: { orderId: mockOrderId } 
        });
        
        return;
      }

      // Real API call
      const orderData = {
        customer_info: {
          full_name: formData.fullName,
          phone: formData.phone,
          email: formData.email
        },
        shipping_address: {
          address: formData.address,
          ward: formData.ward,
          district: formData.district,
          city: formData.city
        },
        payment_method: formData.paymentMethod,
        notes: formData.notes,
        items: cartItems.map(item => ({
          product_id: item.id,
          quantity: item.quantity,
          color: item.color || '',
          size: item.size || '',
          price: item.price
        })),
        total_amount: getCartTotal()
      };

      console.log('üì§ Sending order data:', orderData);

      const response = await fetch('http://localhost:8000/api/orders/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...(user && { 'Authorization': `Bearer ${localStorage.getItem('access_token')}` })
        },
        body: JSON.stringify(orderData)
      });

      console.log('üì• Response status:', response.status);

      // Check if response is JSON
      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        const text = await response.text();
        console.error('‚ùå Non-JSON response:', text.substring(0, 200));
        throw new Error('Server tr·∫£ v·ªÅ d·ªØ li·ªáu kh√¥ng h·ª£p l·ªá. Vui l√≤ng ki·ªÉm tra backend.');
      }

      const result = await response.json();
      console.log('üì• Response data:', result);

      if (!response.ok) {
        throw new Error(result.message || result.detail || 'ƒê·∫∑t h√†ng th·∫•t b·∫°i');
      }

      if (!result.success) {
        throw new Error(result.message || 'ƒê·∫∑t h√†ng th·∫•t b·∫°i');
      }

      // Clear cart after successful order
      clearCart();

      // Navigate to success page with order ID
      navigate('/order-success', { 
        state: { orderId: result.order.order_id } 
      });
      
    } catch (error) {
      console.error('‚ùå Error submitting order:', error);
      
      let errorMessage = 'C√≥ l·ªói x·∫£y ra khi ƒë·∫∑t h√†ng. Vui l√≤ng th·ª≠ l·∫°i!';
      
      if (error.message.includes('Failed to fetch')) {
        errorMessage = 'Kh√¥ng th·ªÉ k·∫øt n·ªëi ƒë·∫øn server. Vui l√≤ng ki·ªÉm tra k·∫øt n·ªëi m·∫°ng ho·∫∑c ƒë·∫£m b·∫£o backend ƒëang ch·∫°y.';
      } else if (error.message.includes('JSON')) {
        errorMessage = 'L·ªói x·ª≠ l√Ω d·ªØ li·ªáu t·ª´ server. Vui l√≤ng ki·ªÉm tra backend API.';
      } else if (error.message) {
        errorMessage = error.message;
      }
      
      alert(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  if (cartItems.length === 0) {
    return null; // Will redirect in useEffect
  }

  return (
    <div className="checkout-page">
      <div className="checkout-container">
        <div className="checkout-header">
          <h1>Thanh to√°n</h1>
          <div className="checkout-steps">
            <div className="step active">1. Th√¥ng tin giao h√†ng</div>
            <div className="step">2. X√°c nh·∫≠n ƒë∆°n h√†ng</div>
            <div className="step">3. Ho√†n t·∫•t</div>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="checkout-form">
          <div className="checkout-content">
            <div className="shipping-info">
              <h2>Th√¥ng tin giao h√†ng</h2>
              
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="fullName">H·ªç v√† t√™n *</label>
                  <input
                    type="text"
                    id="fullName"
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleInputChange}
                    className={errors.fullName ? 'error' : ''}
                    placeholder="Nh·∫≠p h·ªç v√† t√™n"
                  />
                  {errors.fullName && <span className="error-message">{errors.fullName}</span>}
                </div>
                
                <div className="form-group">
                  <label htmlFor="phone">S·ªë ƒëi·ªán tho·∫°i *</label>
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    className={errors.phone ? 'error' : ''}
                    placeholder="Nh·∫≠p s·ªë ƒëi·ªán tho·∫°i"
                  />
                  {errors.phone && <span className="error-message">{errors.phone}</span>}
                </div>
              </div>

              <div className="form-group">
                <label htmlFor="email">Email *</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className={errors.email ? 'error' : ''}
                  placeholder="Nh·∫≠p email"
                />
                {errors.email && <span className="error-message">{errors.email}</span>}
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="city">T·ªânh/Th√†nh ph·ªë *</label>
                  <select
                    id="city"
                    name="city"
                    value={formData.city}
                    onChange={handleInputChange}
                    className={errors.city ? 'error' : ''}
                  >
                    <option value="">Ch·ªçn t·ªânh/th√†nh ph·ªë</option>
                    {cities.map(city => (
                      <option key={city.id} value={city.id}>
                        {city.name}
                      </option>
                    ))}
                  </select>
                  {errors.city && <span className="error-message">{errors.city}</span>}
                </div>
                
                <div className="form-group">
                  <label htmlFor="district">Qu·∫≠n/Huy·ªán *</label>
                  <select
                    id="district"
                    name="district"
                    value={formData.district}
                    onChange={handleInputChange}
                    className={errors.district ? 'error' : ''}
                    disabled={!formData.city}
                  >
                    <option value="">Ch·ªçn qu·∫≠n/huy·ªán</option>
                    {districts.map(district => (
                      <option key={district.id} value={district.id}>
                        {district.name}
                      </option>
                    ))}
                  </select>
                  {errors.district && <span className="error-message">{errors.district}</span>}
                </div>
              </div>

              <div className="form-group">
                <label htmlFor="ward">Ph∆∞·ªùng/X√£ *</label>
                <select
                  id="ward"
                  name="ward"
                  value={formData.ward}
                  onChange={handleInputChange}
                  className={errors.ward ? 'error' : ''}
                  disabled={!formData.district}
                >
                  <option value="">Ch·ªçn ph∆∞·ªùng/x√£</option>
                  {wards.map(ward => (
                    <option key={ward.id} value={ward.id}>
                      {ward.name}
                    </option>
                  ))}
                </select>
                {errors.ward && <span className="error-message">{errors.ward}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="address">ƒê·ªãa ch·ªâ chi ti·∫øt *</label>
                <textarea
                  id="address"
                  name="address"
                  value={formData.address}
                  onChange={handleInputChange}
                  className={errors.address ? 'error' : ''}
                  placeholder="S·ªë nh√†, t√™n ƒë∆∞·ªùng..."
                  rows="3"
                />
                {errors.address && <span className="error-message">{errors.address}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="notes">Ghi ch√∫</label>
                <textarea
                  id="notes"
                  name="notes"
