// components/ChatButton.jsx
import React, { useState, useEffect } from 'react';
import PopupChat from './PopupChat';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:8000';

export default function ChatButton({ user }) {
  const [conversations, setConversations] = useState([]);
  const [openChats, setOpenChats] = useState([]);

  useEffect(() => {
    const fetchChats = async () => {
      try {
        const token = localStorage.getItem('access_token');
        const res = await fetch(`${API_BASE}/api/chat/my-conversations/`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!res.ok) throw new Error(`HTTP ${res.status}`);
        const data = await res.json();
        setConversations(data);
      } catch (err) {
        console.error('Failed to fetch chats', err);
      }
    };

    if (user) fetchChats();
  }, [user]);

  const toggleChat = (id) => {
    setOpenChats((prev) =>
      prev.includes(id) ? prev.filter((x) => x !== id) : [...prev, id]
    );
  };

  if (!user) return null;

  return (
    <>
      {/* Chat buttons */}
      <div style={{
        position: 'fixed',
        bottom: 20,
        right: 20,
        display: 'flex',
        flexDirection: 'column-reverse',
        gap: 10,
        zIndex: 9999
      }}>
        {conversations.map(conv => (
          <button
            key={conv.id}
            onClick={() => toggleChat(conv.id)}
            style={{
              width: 50,
              height: 50,
              borderRadius: '50%',
              background: '#2563eb',
              color: '#fff',
              border: 'none',
              cursor: 'pointer'
            }}
          >
            {conv.name[0].toUpperCase()}
          </button>
        ))}
      </div>

      {/* Popup chats */}
      {openChats.map(id => (
        <PopupChat
          key={id}
          shopId={user.user_type === 'buyer' ? id : undefined}
          buyerId={user.user_type === 'seller' ? id : undefined}
          userId={user.user_id}
          onClose={() => toggleChat(id)}
        />
      ))}
    </>
  );
}
