import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

export default function FiltersSidebar({
  categories = [],
  selectedCategory = 'Tất cả',
  onCategoryChange = () => {},
  priceRange = { min: '', max: '' },
  onPriceRangeChange = () => {},
  sortBy = 'relevance',
  onSortChange = () => {},
  onClearFilters = () => {},
  onQueryChange = () => {},
  showCategoryLinks = false,
  className = ''
}) {
  const [localCategory, setLocalCategory] = useState(selectedCategory || 'Tất cả');
  const [localMin, setLocalMin] = useState(priceRange.min ?? '');
  const [localMax, setLocalMax] = useState(priceRange.max ?? '');
  const [localSort, setLocalSort] = useState(sortBy || 'relevance');
  const [localQuery, setLocalQuery] = useState('');

  useEffect(() => { setLocalCategory(selectedCategory || 'Tất cả'); }, [selectedCategory]);
  useEffect(() => { setLocalMin(priceRange.min ?? ''); setLocalMax(priceRange.max ?? ''); }, [priceRange]);
  useEffect(() => { setLocalSort(sortBy || 'relevance'); }, [sortBy]);

  function handleApply(e) {
    e?.preventDefault?.();
    // push changes up
    onCategoryChange(localCategory);
    onPriceRangeChange({ min: localMin, max: localMax });
    onSortChange(localSort);
    onQueryChange(localQuery);
  }

  function handleClear(e) {
    e?.preventDefault?.();
    setLocalCategory('Tất cả');
    setLocalMin('');
    setLocalMax('');
    setLocalSort('relevance');
    setLocalQuery('');
    onClearFilters();
    // also notify parent to clear explicit fields
    onPriceRangeChange({ min: '', max: '' });
    onSortChange('relevance');
    onQueryChange('');
    onCategoryChange('Tất cả');
  }

  return (
    <form className={`filters-sidebar ${className}`} onSubmit={handleApply} aria-label="Bộ lọc sản phẩm">
      <div className="filter-section">
        <label className="filter-label">Tìm kiếm</label>
        <input
          type="search"
          placeholder="Tìm tên, mô tả..."
          value={localQuery}
          onChange={(e) => setLocalQuery(e.target.value)}
          className="filter-input"
        />
      </div>

      <div className="filter-section">
        <label className="filter-label">Danh mục</label>
        {showCategoryLinks ? (
          <div className="category-links">
            {(categories || []).map((c, i) => {
              const name = typeof c === 'string' ? c : (c.name ?? String(c));
              const slug = typeof c === 'string' ? null : (c.slug ?? null);
              return (
                <div key={slug ?? name ?? i} className={`category-item ${name === localCategory ? 'active' : ''}`}>
                  {slug ? (
                    <Link to={`/category/${encodeURIComponent(slug)}`} onClick={() => onCategoryChange(name)}>{name}</Link>
                  ) : (
                    <button type="button" onClick={() => { setLocalCategory(name); }}>{name}</button>
                  )}
                </div>
              );
            })}
          </div>
        ) : (
          <select value={localCategory} onChange={(e) => setLocalCategory(e.target.value)} className="filter-select">
            <option value="Tất cả">-- Tất cả --</option>
            {(categories || []).map((c, i) => {
              const name = typeof c === 'string' ? c : (c.name ?? String(c));
              return <option key={name + i} value={name}>{name}</option>;
            })}
          </select>
        )}
      </div>

      <div className="filter-section">
        <label className="filter-label">Khoảng giá (VNĐ)</label>
        <div className="price-row">
          <input
            type="number"
            min="0"
            placeholder="Từ"
            value={localMin}
            onChange={(e) => setLocalMin(e.target.value)}
            className="filter-input"
          />
          <span className="price-sep">—</span>
          <input
            type="number"
            min="0"
            placeholder="Đến"
            value={localMax}
            onChange={(e) => setLocalMax(e.target.value)}
            className="filter-input"
          />
        </div>
      </div>

      <div className="filter-section">
        <label className="filter-label">Sắp xếp</label>
        <select value={localSort} onChange={(e) => setLocalSort(e.target.value)} className="filter-select">
          <option value="relevance">Phù hợp</option>
          <option value="newest">Mới nhất</option>
          <option value="price-low">Giá: thấp → cao</option>
          <option value="price-high">Giá: cao → thấp</option>
          <option value="name">Tên A → Z</option>
        </select>
      </div>

      <div className="filter-actions">
        <button type="button" className="btn btn-secondary" onClick={handleClear}>Xóa</button>
        <button type="submit" className="btn btn-primary">Áp dụng</button>
      </div>
    </form>
  );
}
