import React, { useEffect, useState, useMemo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Layout from '../components/Layout.jsx';
import CategoryNav from '../components/CategoryNav.jsx';
import ProductGrid from '../components/ProductGrid.jsx';
import FiltersSidebar from '../components/FiltersSidebar.jsx';
import PageHeader from '../components/PageHeader.jsx';
import EmptyState from '../components/EmptyState.jsx';
import Pagination from '../components/Pagination.jsx';
import '../assets/CategoryPage.css';
import '../assets/Components.css';

export default function CategoryPage() {
  const params = useParams();
  const rawCategoryParam = params.slug ?? params.categoryName ?? params.category ?? '';
  const slugFromUrl = rawCategoryParam ? decodeURIComponent(rawCategoryParam) : '';
  const navigate = useNavigate();

  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // UI filters
  const [query, setQuery] = useState('');
  const [priceRange, setPriceRange] = useState({ min: '', max: '' });
  const [sortBy, setSortBy] = useState('relevance');
  const [currentPage, setCurrentPage] = useState(1);

  // show/hide filters panel
  const [showFilters, setShowFilters] = useState(false);

  useEffect(() => {
    let cancelled = false;
    async function load() {
      setLoading(true);
      try {
        const [cRes, pRes] = await Promise.all([axios.get('/api/categories/'), axios.get('/api/products/')]);
        if (cancelled) return;
        const cats = Array.isArray(cRes.data) ? cRes.data : (cRes.data.results ?? []);
        const prods = Array.isArray(pRes.data) ? pRes.data : (pRes.data.results ?? []);
        setCategories(cats);
        setProducts(prods);
        console.info('CategoryPage: loaded', { slugFromUrl, categories: cats.slice(0,5), productsSample: prods.slice(0,5) });
      } catch (err) {
        if (cancelled) return;
        setError(err.response?.data ?? err.message ?? 'L·ªói t·∫£i d·ªØ li·ªáu');
      } finally {
        if (!cancelled) setLoading(false);
      }
    }
    load();
    return () => { cancelled = true; };
  }, [slugFromUrl]);

  // ensure filter toggle works even if prop wasn't passed: listen to global event
  useEffect(() => {
    function onGlobalToggle() {
      console.log('CategoryPage: received global toggle event, showFilters=', showFilters);
      setShowFilters(v => !v);
    }
    window.addEventListener('vm:toggle-filters', onGlobalToggle);
    return () => window.removeEventListener('vm:toggle-filters', onGlobalToggle);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // only attach once

  // find active category object
  const activeCategoryObj = useMemo(() => {
    if (!slugFromUrl) return null;
    return categories.find(c => {
      const s = String(c.slug ?? c.name ?? c.id ?? '').toLowerCase();
      return s === String(rawCategoryParam).toLowerCase();
    }) ?? null;
  }, [categories, rawCategoryParam, slugFromUrl]);

  // helper match & filtering (kept as before)
  function productMatchesCategory(prod, identifier) {
    if (!identifier || identifier === 'T·∫•t c·∫£') return true;
    const idToMatch = String(identifier).toLowerCase();
    const pc = prod.category;
    if (pc == null) return false;
    if (typeof pc === 'string' || typeof pc === 'number') {
      const v = String(pc).toLowerCase();
      if (v === idToMatch) return true;
      if (v.includes(idToMatch) || idToMatch.includes(v)) return true;
      return false;
    }
    const pid = pc.id ?? pc.pk ?? '';
    const pname = pc.name ?? pc.title ?? '';
    const pslug = pc.slug ?? '';
    if (String(pid).toLowerCase() === idToMatch) return true;
    if (String(pname).toLowerCase() === idToMatch) return true;
    if (String(pslug).toLowerCase() === idToMatch) return true;
    if (String(pname).toLowerCase().includes(idToMatch) || String(pslug).toLowerCase().includes(idToMatch)) return true;
    return false;
  }

  const filteredProducts = useMemo(() => {
    const activeIdentifier = activeCategoryObj ? (activeCategoryObj.slug ?? activeCategoryObj.name ?? String(activeCategoryObj.id)) : (slugFromUrl || 'T·∫•t c·∫£');
    return products.filter(product => {
      if (!productMatchesCategory(product, activeIdentifier)) return false;
      const q = (query || '').trim().toLowerCase();
      if (q) {
        const name = (product.name || '').toLowerCase();
        const desc = (product.description || '').toLowerCase();
        if (!name.includes(q) && !desc.includes(q)) return false;
      }
      const price = Number(product.price ?? 0);
      if (priceRange.min && price < Number(priceRange.min)) return false;
      if (priceRange.max && price > Number(priceRange.max)) return false;
      return true;
    }).sort((a, b) => {
      switch (sortBy) {
        case 'price-low': return (Number(a.price)||0) - (Number(b.price)||0);
        case 'price-high': return (Number(b.price)||0) - (Number(a.price)||0);
        case 'name': return (a.name||'').localeCompare(b.name||'');
        case 'newest': return (b.id||0) - (a.id||0);
        default: return 0;
      }
    });
  }, [products, activeCategoryObj, slugFromUrl, query, priceRange, sortBy]);

  // pagination
  const ITEMS_PER_PAGE = 12;
  const totalPages = Math.max(1, Math.ceil(filteredProducts.length / ITEMS_PER_PAGE));
  useEffect(() => { if (currentPage > totalPages) setCurrentPage(totalPages); }, [totalPages, currentPage]);
  const paginatedProducts = useMemo(() => {
    const start = (currentPage - 1) * ITEMS_PER_PAGE;
    return filteredProducts.slice(start, start + ITEMS_PER_PAGE);
  }, [filteredProducts, currentPage]);

  function handleCategoryChange(value) {
    if (!value || value === 'T·∫•t c·∫£') { navigate('/'); return; }
    const found = categories.find(c => (c.slug === value || String(c.id) === String(value) || c.name === value));
    const toSlug = found ? found.slug ?? found.name : value;
    navigate(`/category/${encodeURIComponent(toSlug)}`);
  }

  // toggle filters (passed to CategoryNav)
  function handleToggleFilters() {
    console.log('CategoryPage: toggling filters (before) showFilters=', showFilters);
    setShowFilters(v => {
      const next = !v;
      console.log('CategoryPage: toggling filters ->', next);
      return next;
    });
  }

  return (
    <Layout className="category-page">
      <div className="category-container">
        <PageHeader
          title={activeCategoryObj ? activeCategoryObj.name : (slugFromUrl || 'T·∫•t c·∫£')}
          subtitle={`K·∫øt qu·∫£ cho ${activeCategoryObj ? activeCategoryObj.name : (slugFromUrl || 'T·∫•t c·∫£')}`}
          resultsCount={filteredProducts.length}
          breadcrumb={[{ text: 'Trang ch·ªß', link: '/' }, { text: activeCategoryObj ? activeCategoryObj.name : (slugFromUrl || 'T·∫•t c·∫£') }]}
          className="category-header"
        />

        <CategoryNav
          categories={['T·∫•t c·∫£', ...categories]}
          active={activeCategoryObj ? (activeCategoryObj.name ?? activeCategoryObj.slug) : (slugFromUrl || 'T·∫•t c·∫£')}
          onChange={handleCategoryChange}
          onToggleFilters={handleToggleFilters}
        />

        <div className="category-content">
          {/* FiltersSidebar: render as panel when showFilters true */}
          {showFilters && (
            <aside
              className="filters-panel"
              style={{
                position: 'fixed',
                top: 80,
                right: 20,
                width: 340,
                maxWidth: '90vw',
                height: '80vh',
                overflowY: 'auto',
                background: '#fff',
                boxShadow: '0 6px 18px rgba(0,0,0,0.12)',
                borderRadius: 8,
                zIndex: 9999,
                padding: 12
              }}
            >
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 8 }}>
                <strong>B·ªô l·ªçc</strong>
                <div>
                  <button type="button" onClick={() => { setShowFilters(false); }} style={{ marginRight: 8 }}>ƒê√≥ng</button>
                  <button type="button" onClick={() => { setShowFilters(false); }}>√Åp d·ª•ng</button>
                </div>
              </div>

              <FiltersSidebar
                categories={['T·∫•t c·∫£', ...categories.map(c => c.name ?? c)]}
                selectedCategory={activeCategoryObj ? activeCategoryObj.name : (slugFromUrl || 'T·∫•t c·∫£')}
                onCategoryChange={(cat) => handleCategoryChange(cat)}
                priceRange={priceRange}
                onPriceRangeChange={(r) => { setPriceRange(r); setCurrentPage(1); }}
                sortBy={sortBy}
