import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../utils/AuthContext';
import { Link, useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../assets/SellerOrders.css';

const SellerOrders = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  // Filters
  const [statusFilter, setStatusFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('newest');
  
  // Pagination
  const [currentPage, setCurrentPage] = useState(1);
  const ordersPerPage = 10;

  // Stats
  const [stats, setStats] = useState({
    total: 0,
    pending: 0,
    paid: 0,
    shipping: 0,
    completed: 0,
    canceled: 0,
    today: 0,
    total_revenue: 0,
  });

  // Selected order for modal
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    if (user && user.user_type !== 'seller') {
      navigate('/dashboard');
    } else if (user) {
      fetchOrders();
      fetchStats();
    }
  }, [user, navigate]);

  const filterAndSortOrders = useCallback(() => {
    let filtered = [...orders];

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(order =>
        order.order_id.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.buyer_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.buyer_email.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.buyer_phone.includes(searchTerm)
      );
    }

    // Status filter
    if (statusFilter !== 'all') {
      filtered = filtered.filter(o => o.status === statusFilter);
    }

    // Sort
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'newest':
          return new Date(b.created_at) - new Date(a.created_at);
        case 'oldest':
          return new Date(a.created_at) - new Date(b.created_at);
        case 'amount-high':
          return b.seller_total - a.seller_total;
        case 'amount-low':
          return a.seller_total - b.seller_total;
        default:
          return 0;
      }
    });

    setFilteredOrders(filtered);
    setCurrentPage(1);
  }, [orders, searchTerm, statusFilter, sortBy]);

  useEffect(() => {
    filterAndSortOrders();
  }, [filterAndSortOrders]);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem('access_token');
      
      const response = await fetch('http://localhost:8000/api/orders/seller/orders/', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (!response.ok) throw new Error('Failed to fetch orders');
      
      const data = await response.json();
      setOrders(data.results || []);
      
    } catch (err) {
      console.error('Error fetching orders:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const token = localStorage.getItem('access_token');
      
      const response = await fetch('http://localhost:8000/api/orders/seller/stats/', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (!response.ok) throw new Error('Failed to fetch stats');
      
      const data = await response.json();
      setStats(data);
      
    } catch (err) {
      console.error('Error fetching stats:', err);
    }
  };

  const handleUpdateStatus = async (orderId, newStatus) => {
    try {
      const token = localStorage.getItem('access_token');
      const response = await fetch(`http://localhost:8000/api/orders/seller/orders/${orderId}/status/`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status: newStatus }),
      });

      if (!response.ok) throw new Error('Failed to update status');

      // Refresh
      await fetchOrders();
      await fetchStats();
      
      alert('Cáº­p nháº­t tráº¡ng thÃ¡i thÃ nh cÃ´ng!');
      setShowModal(false);
      
    } catch (err) {
      alert('Lá»—i khi cáº­p nháº­t: ' + err.message);
    }
  };

  const viewOrderDetail = (order) => {
    setSelectedOrder(order);
    setShowModal(true);
  };

  const getStatusBadge = (status) => {
    const badges = {
      pending: { class: 'warning', icon: 'â³', text: 'Chá» xÃ¡c nháº­n' },
      paid: { class: 'info', icon: 'ðŸ’³', text: 'ÄÃ£ thanh toÃ¡n' },
      shipping: { class: 'primary', icon: 'ðŸšš', text: 'Äang giao' },
      completed: { class: 'success', icon: 'âœ…', text: 'HoÃ n thÃ nh' },
      canceled: { class: 'danger', icon: 'âŒ', text: 'ÄÃ£ há»§y' },
    };
    return badges[status] || badges.pending;
  };

  // Pagination
  const indexOfLastOrder = currentPage * ordersPerPage;
  const indexOfFirstOrder = indexOfLastOrder - ordersPerPage;
  const currentOrders = filteredOrders.slice(indexOfFirstOrder, indexOfLastOrder);
  const totalPages = Math.ceil(filteredOrders.length / ordersPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  if (loading) {
    return (
      <>
        <Header />
        <div className="seller-orders-loading">
          <div className="spinner"></div>
          <p>Äang táº£i Ä‘Æ¡n hÃ ng...</p>
        </div>
        <Footer />
      </>
    );
  }

  return (
    <>
      
      <div className="seller-orders-page">
        <div className="seller-orders-container">
          
          {/* Page Header */}
          <div className="page-header">
            <div className="header-left">
              <Link to="/seller/dashboard" className="back-btn">
                â† Dashboard
              </Link>
              <div>
                <h1>ðŸ“‹ Quáº£n lÃ½ Ä‘Æ¡n hÃ ng</h1>
                <p>Theo dÃµi vÃ  xá»­ lÃ½ Ä‘Æ¡n hÃ ng cá»§a báº¡n</p>
              </div>
            </div>
          </div>

          {/* Stats Cards */}
          <div className="stats-row">
            <div className="stat-card total">
              <div className="stat-icon">ðŸ“Š</div>
              <div className="stat-info">
                <h3>{stats.total}</h3>
                <p>Tá»•ng Ä‘Æ¡n</p>
              </div>
            </div>
            <div className="stat-card pending">
              <div className="stat-icon">â³</div>
              <div className="stat-info">
                <h3>{stats.pending}</h3>
                <p>Chá» xÃ¡c nháº­n</p>
              </div>
            </div>
            <div className="stat-card paid">
              <div className="stat-icon">ðŸ’³</div>
              <div className="stat-info">
                <h3>{stats.paid}</h3>
                <p>ÄÃ£ thanh toÃ¡n</p>
              </div>
            </div>
            <div className="stat-card shipping">
              <div className="stat-icon">ðŸšš</div>
              <div className="stat-info">
                <h3>{stats.shipping}</h3>
                <p>Äang giao</p>
              </div>
            </div>
            <div className="stat-card completed">
              <div className="stat-icon">âœ…</div>
              <div className="stat-info">
                <h3>{stats.completed}</h3>
                <p>HoÃ n thÃ nh</p>
              </div>
            </div>
            <div className="stat-card revenue">
              <div className="stat-icon">ðŸ’°</div>
              <div className="stat-info">
                <h3>${stats.total_revenue.toFixed(2)}</h3>
                <p>Doanh thu</p>
              </div>
            </div>
          </div>

          {/* Filters */}
          <div className="filters-section">
            <div className="filters-row">
              {/* Search */}
              <div className="search-box">
                <span className="search-icon">ðŸ”</span>
                <input
                  type="text"
                  placeholder="TÃ¬m theo mÃ£ Ä‘Æ¡n, tÃªn, email, SÄT..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                {searchTerm && (
                  <button 
                    className="clear-search"
                    onClick={() => setSearchTerm('')}
                  >
                    Ã—
                  </button>
                )}
              </div>

              {/* Status Filter */}
              <select 
                className="filter-select"
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
              >
                <option value="all">Táº¥t cáº£ tráº¡ng thÃ¡i</option>
                <option value="pending">Chá» xÃ¡c nháº­n</option>
                <option value="paid">ÄÃ£ thanh toÃ¡n</option>
                <option value="shipping">Äang giao</option>
                <option value="completed">HoÃ n thÃ nh</option>
                <option value="canceled">ÄÃ£ há»§y</option>
              </select>

              {/* Sort */}
              <select 
                className="filter-select"
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
              >
                <option value="newest">Má»›i nháº¥t</option>
                <option value="oldest">CÅ© nháº¥t</option>
                <option value="amount-high">GiÃ¡ trá»‹ cao</option>
                <option value="amount-low">GiÃ¡ trá»‹ tháº¥p</option>
              </select>
            </div>

            {/* Results Info */}
            <div className="results-info">
              <p>
                Hiá»ƒn thá»‹ <strong>{indexOfFirstOrder + 1}-{Math.min(indexOfLastOrder, filteredOrders.length)}</strong> 
                {' '}trong <strong>{filteredOrders.length}</strong> Ä‘Æ¡n hÃ ng
              </p>
            </div>
          </div>

          {/* Error Message */}
          {error && (
            <div className="error-message">
              âŒ Lá»—i: {error}
              <button onClick={fetchOrders}>Thá»­ láº¡i</button>
            </div>
          )}

          {/* Orders Table */}
          {filteredOrders.length === 0 ? (
            <div className="empty-state">
              <div className="empty-icon">ðŸ“¦</div>
              <h2>KhÃ´ng tÃ¬m tháº¥y Ä‘Æ¡n hÃ ng</h2>
              <p>
                {searchTerm || statusFilter !== 'all' 
                  ? 'Thá»­ thay Ä‘á»•i bá»™ lá»c' 
                  : 'Báº¡n chÆ°a cÃ³ Ä‘Æ¡n hÃ ng nÃ o'}
              </p>
            </div>
          ) : (
            <>
              <div className="orders-table-wrapper">
                <table className="orders-table">
                  <thead>
                    <tr>
                      <th>MÃ£ Ä‘Æ¡n</th>
                      <th>KhÃ¡ch hÃ ng</th>
                      <th>Äá»‹a chá»‰</th>
                      <th>Sáº£n pháº©m</th>
                      <th>Tá»•ng tiá»n</th>
                      <th>Tráº¡ng thÃ¡i</th>
                      <th>NgÃ y Ä‘áº·t</th>
                      <th>Thao tÃ¡c</th>
                    </tr>
                  </thead>
                  <tbody>
                    {currentOrders.map((order) => {
                      const statusInfo = getStatusBadge(order.status);
                      return (
                        <tr key={order.order_id}>
                          <td>
                            <strong>{order.order_id}</strong>
                          </td>
                          <td>
                            <div className="customer-info">
                              <div className="customer-name">{order.full_name}</div>
                              <div className="customer-contact">
                                ðŸ“§ {order.buyer_email}
                              </div>
                              <div className="customer-contact">
                                ðŸ“± {order.buyer_phone}
                              </div>
                            </div>
                          </td>
                          <td>
                            <div className="address-info">
                              {order.address}, {order.ward}
                              <br />
                              {order.district}, {order.city}
                            </div>
                          </td>
                          <td>
                            <span className="items-count">
                              {order.items_count} sáº£n pháº©m
                            </span>
                          </td>
                          <td>
                            <strong className="order-total">
                              ${order.seller_total.toFixed(2)}
                            </strong>
                          </td>
                          <td>
                            <span className={`status-badge ${statusInfo.class}`}>
                              {statusInfo.icon} {statusInfo.text}
                            </span>
                          </td>
                          <td>
                            {new Date(order.created_at).toLocaleDateString('vi-VN', {
                              year: 'numeric',
                              month: '2-digit',
                              day: '2-digit',
                              hour: '2-digit',
                              minute: '2-digit'
                            })}
                          </td>
                          <td>
                            <button
                              className="btn-action btn-view"
                              onClick={() => viewOrderDetail(order)}
                              title="Xem chi tiáº¿t"
                            >
                              ðŸ‘ï¸ Chi tiáº¿t
                            </button>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>

              {/* Pagination */}
              {totalPages > 1 && (
                <div className="pagination">
                  <button 
                    className="page-btn"
                    onClick={() => paginate(currentPage - 1)}
                    disabled={currentPage === 1}
                  >
                    â† TrÆ°á»›c
                  </button>
                  
                  <div className="page-numbers">
                    {[...Array(totalPages)].map((_, index) => {
                      const pageNumber = index + 1;
                      if (
                        pageNumber === 1 ||
                        pageNumber === totalPages ||
                        (pageNumber >= currentPage - 1 && pageNumber <= currentPage + 1)
                      ) {
                        return (
                          <button
                            key={pageNumber}
                            className={`page-number ${currentPage === pageNumber ? 'active' : ''}`}
                            onClick={() => paginate(pageNumber)}
                          >
                            {pageNumber}
                          </button>
                        );
                      } else if (
                        pageNumber === currentPage - 2 ||
                        pageNumber === currentPage + 2
                      ) {
                        return <span key={pageNumber} className="page-dots">...</span>;
                      }
                      return null;
                    })}
                  </div>

                  <button 
                    className="page-btn"
                    onClick={() => paginate(currentPage + 1)}
                    disabled={currentPage === totalPages}
                  >
                    Sau â†’
                  </button>
                </div>
              )}
            </>
          )}

        </div>
      </div>

      {/* Order Detail Modal */}
      {showModal && selectedOrder && (
        <OrderDetailModal
          order={selectedOrder}
          onClose={() => setShowModal(false)}
          onUpdateStatus={handleUpdateStatus}
          getStatusBadge={getStatusBadge}
        />
      )}

      <Footer />
    </>
  );
};

// Order Detail Modal Component - Äáº¶T TRÆ¯á»šC export default
const OrderDetailModal = ({ order, onClose, onUpdateStatus, getStatusBadge }) => {
  const [newStatus, setNewStatus] = useState(order.status);
  
  const statusInfo = getStatusBadge(order.status);

  const handleSubmit = () => {
    if (newStatus !== order.status) {
      if (window.confirm('Báº¡n cÃ³ cháº¯c muá»‘n cáº­p nháº­t tráº¡ng thÃ¡i Ä‘Æ¡n hÃ ng?')) {
        onUpdateStatus(order.order_id, newStatus);
      }
    } else {
      alert('Vui lÃ²ng chá»n tráº¡ng thÃ¡i má»›i!');
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Chi tiáº¿t Ä‘Æ¡n hÃ ng {order.order_id}</h2>
          <button className="modal-close" onClick={onClose}>Ã—</button>
        </div>

        <div className="modal-body">
          {/* Status */}
          <div className="detail-section">
            <h3>Tráº¡ng thÃ¡i hiá»‡n táº¡i</h3>
            <span className={`status-badge ${statusInfo.class}`}>
              {statusInfo.icon} {statusInfo.text}
            </span>
          </div>

          {/* Customer Info */}
          <div className="detail-section">
            <h3>ThÃ´ng tin khÃ¡ch hÃ ng</h3>
            <div className="info-grid">
              <div className="info-item">
                <label>TÃªn:</label>
                <span>{order.full_name}</span>
              </div>
              <div className="info-item">
                <label>Email:</label>
                <span>{order.buyer_email}</span>
              </div>
              <div className="info-item">
                <label>SÄT:</label>
                <span>{order.buyer_phone}</span>
              </div>
              <div className="info-item full-width">
                <label>Äá»‹a chá»‰:</label>
                <span>
                  {order.address}, {order.ward}, {order.district}, {order.city}
                </span>
              </div>
              <div className="info-item">
                <label>Thanh toÃ¡n:</label>
                <span>{order.payment_method === 'cod' ? 'COD' : order.payment_method.toUpperCase()}</span>
              </div>
              {order.notes && (
                <div className="info-item full-width">
                  <label>Ghi chÃº:</label>
                  <span>{order.notes}</span>
                </div>
              )}
            </div>
          </div>

          {/* Order Items */}
          <div className="detail-section">
            <h3>Sáº£n pháº©m cá»§a báº¡n ({order.items_count})</h3>
            <div className="order-items-list">
              {order.items.map((item) => (
                <div key={item.id} className="order-item">
                  <img 
                    src={item.product_image || '/placeholder.png'} 
                    alt={item.product_name}
                    className="item-image"
                  />
                  <div className="item-info">
                    <div className="item-name">{item.product_name}</div>
                    <div className="item-meta">
                      {item.color && <span>MÃ u: {item.color}</span>}
                      {item.size && <span>Size: {item.size}</span>}
                    </div>
                    <div className="item-meta">
                      ${item.price} Ã— {item.quantity}
                    </div>
                  </div>
                  <div className="item-total">
                    ${item.total.toFixed(2)}
                  </div>
                </div>
              ))}
            </div>
            <div className="order-total-row">
              <strong>Tá»•ng cá»™ng (sáº£n pháº©m cá»§a báº¡n):</strong>
              <strong className="total-amount">${order.seller_total.toFixed(2)}</strong>
            </div>
          </div>

          {/* Update Status */}
          <div className="detail-section">
            <h3>Cáº­p nháº­t tráº¡ng thÃ¡i</h3>
            <div className="form-group">
              <label>Tráº¡ng thÃ¡i má»›i:</label>
              <select 
                value={newStatus}
                onChange={(e) => setNewStatus(e.target.value)}
                className="form-select"
              >
                <option value="pending">Chá» xÃ¡c nháº­n</option>
                <option value="paid">ÄÃ£ thanh toÃ¡n</option>
                <option value="shipping">Äang giao hÃ ng</option>
                <option value="completed">HoÃ n thÃ nh</option>
                <option value="canceled">Há»§y Ä‘Æ¡n</option>
              </select>
            </div>
          </div>
        </div>

        <div className="modal-footer">
          <button className="btn btn-secondary" onClick={onClose}>
            ÄÃ³ng
          </button>
          <button 
            className="btn btn-primary" 
            onClick={handleSubmit}
            disabled={newStatus === order.status}
          >
            ðŸ’¾ Cáº­p nháº­t
          </button>
        </div>
      </div>
    </div>
  );
};

export default SellerOrders;
