import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../utils/CartContext";
import { useAuth } from "../utils/AuthContext";
import { formatPrice } from "../utils/formatPrice";
import {
  getCities,
  getDistrictsByCity,
  getWardsByDistrict,
} from "../utils/locationsData";
import "../assets/Checkout.css";

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:8000";

async function safeFetchJSON(url, options = {}) {
  const res = await fetch(url, options);
  const ct = res.headers.get("content-type") || "";
  if (!res.ok) {
    const txt = await res.text().catch(() => "");
    throw new Error(`HTTP ${res.status} - ${txt.slice(0, 200)}`);
  }
  if (!ct.includes("application/json")) {
    const txt = await res.text().catch(() => "");
    throw new Error("Server trả về non-JSON: " + txt.slice(0, 200));
  }
  return res.json();
}

export default function Checkout() {
  const navigate = useNavigate();
  const { cartItems, clearCart, getCartTotal } = useCart();
  const { user } = useAuth();

  const [formData, setFormData] = useState({
    fullName: "",
    phone: "",
    email: "",
    address: "",
    city: "",
    district: "",
    ward: "",
    paymentMethod: "cod",
    notes: "",
  });

  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [cities, setCities] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [wards, setWards] = useState([]);

  // Load user info & địa chỉ
  useEffect(() => {
    if (cartItems.length === 0) {
      navigate("/cart");
      return;
    }

    if (user) {
      setFormData((prev) => ({
        ...prev,
        fullName: user.full_name || "",
        phone: user.phone || "",
        email: user.email || "",
      }));

      const token = localStorage.getItem("access_token");
      if (token) {
        safeFetchJSON(`${API_BASE}/api/user/address/`, {
          headers: { Authorization: `Bearer ${token}` },
        })
          .then((data) => {
            if (data && (data.address || data.city)) {
              setFormData((prev) => ({
                ...prev,
                address: data.address || "",
                city: data.city || "",
                district: data.district || "",
                ward: data.ward || "",
              }));
            }
          })
          .catch((err) => console.error("Error loading address:", err.message));
      }
    }

    setCities(getCities());
  }, [cartItems, user, navigate]);

  useEffect(() => {
    if (formData.city) setDistricts(getDistrictsByCity(formData.city));
    else {
      setDistricts([]);
      setWards([]);
    }
  }, [formData.city]);

  useEffect(() => {
    if (formData.district) setWards(getWardsByDistrict(formData.district));
    else setWards([]);
  }, [formData.district]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));

    if (errors[name]) setErrors((prev) => ({ ...prev, [name]: "" }));
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.fullName.trim()) newErrors.fullName = "Vui lòng nhập họ tên";
    if (!formData.phone.trim()) newErrors.phone = "Vui lòng nhập số điện thoại";
    else if (!/^[0-9]{10,11}$/.test(formData.phone.trim()))
      newErrors.phone = "Số điện thoại không hợp lệ";
    if (!formData.email.trim()) newErrors.email = "Vui lòng nhập email";
    else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email))
      newErrors.email = "Email không hợp lệ";
    if (!formData.address.trim()) newErrors.address = "Vui lòng nhập địa chỉ";
    if (!formData.city) newErrors.city = "Vui lòng chọn tỉnh/thành phố";
    if (!formData.district) newErrors.district = "Vui lòng chọn quận/huyện";
    if (!formData.ward) newErrors.ward = "Vui lòng chọn phường/xã";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    if (getCartTotal() <= 0 || cartItems.length === 0) return;

    setLoading(true);
    try {
      const orderData = {
        full_name: formData.fullName,
        phone: formData.phone,
        email: formData.email,
        address: formData.address,
        ward: formData.ward,
        district: formData.district,
        city: formData.city,
        payment_method: formData.paymentMethod,
        notes: formData.notes,
        items: cartItems.map((item) => ({
          product_id: item.id,
          quantity: item.quantity,
          color: item.color || "",
          size: item.size || "",
          price: item.price,
        })),
        total_amount: Math.round(getCartTotal()), // số nguyên
      };

      const token = localStorage.getItem("access_token");
      const headers = {
        "Content-Type": "application/json",
        ...(token ? { Authorization: `Bearer ${token}` } : {}),
      };

      // VNPay
      if (formData.paymentMethod === "vnpay") {
        const res = await safeFetchJSON(`${API_BASE}/api/payment/vnpay/`, {
          method: "POST",
          headers,
          body: JSON.stringify({
            order_id: `ORD${Date.now()}`, // ép string
            amount: Math.round(getCartTotal()),
          }),
        });
        if (res?.payment_url) {
          window.location.href = res.payment_url;
          return;
        } else throw new Error("Không tạo được liên kết VNPay.");
      }

      // COD hoặc Momo
      const result = await safeFetchJSON(`${API_BASE}/api/orders/`, {
        method: "POST",
        headers,
        body: JSON.stringify(orderData),
      });

      if (!result.success || !result.order?.order_id)
        throw new Error(result.message || "Đặt hàng thất bại");

      clearCart();
      navigate("/order-success", { state: { orderId: result.order.order_id } });
    } catch (error) {
      console.error("❌ Error submitting order:", error);
      let errorMessage = "Có lỗi xảy ra khi đặt hàng. Vui lòng thử lại!";
      if (/Failed to fetch/i.test(error.message))
        errorMessage = "Không thể kết nối đến server. Hãy kiểm tra backend.";
      else if (/non-JSON|Server trả về non-JSON/i.test(error.message))
        errorMessage = "Backend trả về dữ liệu không hợp lệ.";
      else if (error.message) errorMessage = error.message;

      alert(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  if (cartItems.length === 0) return null;

  return (
    <div className="checkout-page">
      <div className="checkout-container">
        <div className="checkout-header">
          <h1>Thanh toán</h1>
          <div className="checkout-steps">
            <div className="step active">1. Thông tin giao hàng</div>
            <div className="step">2. Xác nhận đơn hàng</div>
            <div className="step">3. Hoàn tất</div>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="checkout-form">
          <div className="checkout-content">
            <div className="shipping-info">
              <h2>Thông tin giao hàng</h2>
              {/* ... các input như phiên bản cũ ... */}
            </div>

            <div className="order-summary">
              <h2>Đơn hàng của bạn</h2>
              {/* ... hiển thị giỏ hàng ... */}
              {/* ... payment method ... */}
              {/* ... tổng tiền ... */}
              <button
                type="submit"
                className="place-order-btn"
                disabled={loading}
              >
                {loading ? "Đang xử lý..." : "Đặt hàng"}
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}
